INSERT INTO `txt` VALUES ('589', '2', 'MvcPager.dll.MVC.分页.2014年5月4日.jun.txt', 'MVC分页\r\n------------添加类库引用---------------------------------\r\n1.添加MvcPager.dll引用\r\n-------------Controller中转化为PagedList------------------------------\r\n2.Controller中:\r\nusing Webdiyer.WebControls.Mvc;\r\n[HttpGet]\r\npublic ActionResult Index(long userId = 0, int page = 1, int pageSize = 20)\r\n{\r\nstring ReadStatement = @\"SELECT top 500  * FROM dbo.CVInformation order by id desc\";\r\nList<CVInformation> list = Dapper.SqlMapperUtil.SqlWithParams<CVInformation>(ReadStatement, null, null);//泛型列表\r\nPagedList<CVInformation> pagedList = new PagedList<CVInformation>(list, page, pageSize);//将列表转成pagedList\r\nreturn View(pagedList);\r\n}\r\n-------------------页面显示列表-----------------------------\r\n3.Views中:\r\n@model PagedList<Career.CandidateEntity.CVInformation>\r\n@using Webdiyer.WebControls.Mvc;\r\n @{\r\n            foreach (var item in Model)\r\n            {\r\n            <tr>\r\n                <td>\r\n                    @item.ChineseName\r\n                </td>\r\n                <td>\r\n                    @item.Email\r\n                </td>\r\n            </tr>    \r\n            }\r\n        }\r\n-----------------------显示分页----------------------------------------\r\n4.显示分页控件\r\n<div class=\"pageBottom clear_box snPages\">\r\n    <div class=\"pagel\">\r\n        共 <strong class=\"col_r\">@Model.TotalItemCount </strong>条\r\n    </div>\r\n    <div class=\"pager\">\r\n        @Html.Pager(Model,//pagedList\r\n        new PagerOptions//PagerOptions\r\n        {\r\n            NumericPagerItemCount = 8,//\r\n            ShowMorePagerItems = true,\r\n            ShowFirstLast = false,\r\n            PageIndexParameterName = \"page\",\r\n            CurrentPagerItemWrapperFormatString = \"<span class=\\\"cpb\\\">{0}</span>\",\r\n            NumericPagerItemWrapperFormatString = \"<span class=\\\"item\\\">{0}</span>\",\r\n            AlwaysShowFirstLastPageNumber = true\r\n        },\r\n        \"Default\",\r\n        null)\r\n    </div>\r\n    <div class=\"clear\">\r\n    </div>\r\n</div>\r\n-----------------------分页控件样式-------------------------------------\r\n5. 分页样式(page.css)\r\n.snPages .pagel{float:left;line-height:25px;}\r\n.snPages .pager\r\n{\r\n    float: right; \r\n}\r\n.snPages a\r\n{\r\n    outline: medium none;\r\n    text-decoration: none;\r\n}\r\n\r\n.snPages a, .snPages a:visited\r\n{\r\n    background: none repeat scroll 0 0 #FFFFFF;\r\n    border: 1px solid #D7D7D7;\r\n    border-radius: 4px 4px 4px 4px;\r\n    height: 24px;\r\n    line-height: 24px;\r\n    padding: 0 8px;\r\n    text-align: center;\r\n}\r\n.snPages .cpb\r\n{\r\n    background: none repeat scroll 0 0 #39f;\r\n    border: 1px solid #39f;\r\n    border-radius: 4px 4px 4px 4px;\r\n    height: 24px;\r\n    line-height: 24px;\r\n    padding: 0 8px;\r\n    text-align: center;\r\n    color:#fff;\r\n}\r\n.snPages a, .snPages a:visited, .snPages span\r\n{\r\n    color: #333333;\r\n    display: inline;\r\n    float: left;\r\n    margin-left: 4px;\r\n}\r\n.snPages a:hover{border:1px solid #39f;background: none repeat scroll 0 0 #39f;color:#fff !important;TEXT-DECORATION: none;}\r\n', '0', '2017-09-08 09:49:09', null, 'MvcPager.dll.MVC.分页.2014年5月4日.jun.txt');
