INSERT INTO `txt` VALUES ('595', '2', 'MySql.Helper.txt', 'MySqlHelper：\r\n(from:http://xian827991006.blog.163.com/blog/static/111039827201291845925827/)\r\nusing System;\r\nusing System.Collections;\r\nusing System.Configuration;\r\nusing MySql.Data;\r\nusing MySql.Data.MySqlClient;\r\nusing System.Data;\r\n\r\n/// <summary>\r\n///MYSQLHelper 的摘要说明\r\n/// </summary>\r\npublic abstract class MySqlHelper\r\n{\r\n    //数据库连接字符串\r\n    public static string Conn = \"Database=\'wp\';Data Source=\'localhost\';User Id=\'root\';Password=\'root\';charset=\'utf8\';pooling=true\";\r\n\r\n    // 用于缓存参数的HASH表\r\n    private static Hashtable parmCache = Hashtable.Synchronized(new Hashtable());\r\n\r\n    /// <summary>\r\n    ///  给定连接的数据库用假设参数执行一个sql命令（不返回数据集）\r\n    /// </summary>\r\n    /// <param name=\"connectionString\">一个有效的连接字符串</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns>执行命令所影响的行数</returns>\r\n    public static int ExecuteNonQuery(string connectionString, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n\r\n        MySqlCommand cmd = new MySqlCommand();\r\n\r\n        using (MySqlConnection conn = new MySqlConnection(connectionString))\r\n        {\r\n            PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n    }\r\n\r\n    /// <summary>\r\n    /// 用现有的数据库连接执行一个sql命令（不返回数据集）\r\n    /// </summary>\r\n    /// <param name=\"connection\">一个现有的数据库连接</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns>执行命令所影响的行数</returns>\r\n    public static int ExecuteNonQuery(MySqlConnection connection, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n\r\n        MySqlCommand cmd = new MySqlCommand();\r\n\r\n        PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n        int val = cmd.ExecuteNonQuery();\r\n        cmd.Parameters.Clear();\r\n        return val;\r\n    }\r\n\r\n    /// <summary>\r\n    ///使用现有的SQL事务执行一个sql命令（不返回数据集）\r\n    /// </summary>\r\n    /// <remarks>\r\n    ///举例:\r\n    ///  int result = ExecuteNonQuery(connString, CommandType.StoredProcedure, \"PublishOrders\", new MySqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    /// <param name=\"trans\">一个现有的事务</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns>执行命令所影响的行数</returns>\r\n    public static int ExecuteNonQuery(MySqlTransaction trans, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n        MySqlCommand cmd = new MySqlCommand();\r\n        PrepareCommand(cmd, trans.Connection, trans, cmdType, cmdText, commandParameters);\r\n        int val = cmd.ExecuteNonQuery();\r\n        cmd.Parameters.Clear();\r\n        return val;\r\n    }\r\n\r\n    /// <summary>\r\n    /// 用执行的数据库连接执行一个返回数据集的sql命令\r\n    /// </summary>\r\n    /// <remarks>\r\n    /// 举例:\r\n    ///  MySqlDataReader r = ExecuteReader(connString, CommandType.StoredProcedure, \"PublishOrders\", new MySqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    /// <param name=\"connectionString\">一个有效的连接字符串</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns>包含结果的读取器</returns>\r\n    public static MySqlDataReader ExecuteReader(string connectionString, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n        //创建一个MySqlCommand对象\r\n        MySqlCommand cmd = new MySqlCommand();\r\n        //创建一个MySqlConnection对象\r\n        MySqlConnection conn = new MySqlConnection(connectionString);\r\n\r\n        //在这里我们用一个try/catch结构执行sql文本命令/存储过程，因为如果这个方法产生一个异常我们要关闭连接，因为没有读取器存在，\r\n        //因此commandBehaviour.CloseConnection 就不会执行\r\n        try\r\n        {\r\n            //调用 PrepareCommand 方法，对 MySqlCommand 对象设置参数\r\n            PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n            //调用 MySqlCommand  的 ExecuteReader 方法\r\n            MySqlDataReader reader = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n            //清除参数\r\n            cmd.Parameters.Clear();\r\n            return reader;\r\n        }\r\n        catch\r\n        {\r\n            //关闭连接，抛出异常\r\n            conn.Close();\r\n            throw;\r\n        }\r\n    }\r\n\r\n    /// <summary>\r\n    /// 返回DataSet\r\n    /// </summary>\r\n    /// <param name=\"connectionString\">一个有效的连接字符串</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns></returns>\r\n    public static DataSet GetDataSet(string connectionString, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n        //创建一个MySqlCommand对象\r\n        MySqlCommand cmd = new MySqlCommand();\r\n        //创建一个MySqlConnection对象\r\n        MySqlConnection conn = new MySqlConnection(connectionString);\r\n\r\n        //在这里我们用一个try/catch结构执行sql文本命令/存储过程，因为如果这个方法产生一个异常我们要关闭连接，因为没有读取器存在，\r\n\r\n        try\r\n        {\r\n            //调用 PrepareCommand 方法，对 MySqlCommand 对象设置参数\r\n            PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n            //调用 MySqlCommand  的 ExecuteReader 方法\r\n            MySqlDataAdapter adapter = new MySqlDataAdapter();\r\n            adapter.SelectCommand = cmd;\r\n            DataSet ds = new DataSet();\r\n\r\n            adapter.Fill(ds);\r\n            //清除参数\r\n            cmd.Parameters.Clear();\r\n            conn.Close();\r\n            return ds;\r\n        }\r\n        catch (Exception e)\r\n        {\r\n            throw e;\r\n        }\r\n    }\r\n\r\n \r\n\r\n    /// <summary>\r\n    /// 用指定的数据库连接字符串执行一个命令并返回一个数据集的第一列\r\n    /// </summary>\r\n    /// <remarks>\r\n    ///例如:\r\n    ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new MySqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    ///<param name=\"connectionString\">一个有效的连接字符串</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns>用 Convert.To{Type}把类型转换为想要的 </returns>\r\n    public static object ExecuteScalar(string connectionString, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n        MySqlCommand cmd = new MySqlCommand();\r\n\r\n        using (MySqlConnection connection = new MySqlConnection(connectionString))\r\n        {\r\n            PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n            object val = cmd.ExecuteScalar();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n    }\r\n\r\n    /// <summary>\r\n    /// 用指定的数据库连接执行一个命令并返回一个数据集的第一列\r\n    /// </summary>\r\n    /// <remarks>\r\n    /// 例如:\r\n    ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new MySqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    /// <param name=\"connection\">一个存在的数据库连接</param>\r\n    /// <param name=\"cmdType\">命令类型(存储过程, 文本, 等等)</param>\r\n    /// <param name=\"cmdText\">存储过程名称或者sql命令语句</param>\r\n    /// <param name=\"commandParameters\">执行命令所用参数的集合</param>\r\n    /// <returns>用 Convert.To{Type}把类型转换为想要的 </returns>\r\n    public static object ExecuteScalar(MySqlConnection connection, CommandType cmdType, string cmdText, params MySqlParameter[] commandParameters)\r\n    {\r\n\r\n        MySqlCommand cmd = new MySqlCommand();\r\n\r\n        PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n        object val = cmd.ExecuteScalar();\r\n        cmd.Parameters.Clear();\r\n        return val;\r\n    }\r\n\r\n    /// <summary>\r\n    /// 将参数集合添加到缓存\r\n    /// </summary>\r\n    /// <param name=\"cacheKey\">添加到缓存的变量</param>\r\n    /// <param name=\"commandParameters\">一个将要添加到缓存的sql参数集合</param>\r\n    public static void CacheParameters(string cacheKey, params MySqlParameter[] commandParameters)\r\n    {\r\n        parmCache[cacheKey] = commandParameters;\r\n    }\r\n\r\n    /// <summary>\r\n    /// 找回缓存参数集合\r\n    /// </summary>\r\n    /// <param name=\"cacheKey\">用于找回参数的关键字</param>\r\n    /// <returns>缓存的参数集合</returns>\r\n    public static MySqlParameter[] GetCachedParameters(string cacheKey)\r\n    {\r\n        MySqlParameter[] cachedParms = (MySqlParameter[])parmCache[cacheKey];\r\n\r\n        if (cachedParms == null)\r\n            return null;\r\n\r\n        MySqlParameter[] clonedParms = new MySqlParameter[cachedParms.Length];\r\n\r\n        for (int i = 0, j = cachedParms.Length; i < j; i++)\r\n            clonedParms[i] = (MySqlParameter)((ICloneable)cachedParms[i]).Clone();\r\n\r\n        return clonedParms;\r\n    }\r\n\r\n    /// <summary>\r\n    /// 准备执行一个命令\r\n    /// </summary>\r\n    /// <param name=\"cmd\">sql命令</param>\r\n    /// <param name=\"conn\">OleDb连接</param>\r\n    /// <param name=\"trans\">OleDb事务</param>\r\n    /// <param name=\"cmdType\">命令类型例如 存储过程或者文本</param>\r\n    /// <param name=\"cmdText\">命令文本,例如:Select * from Products</param>\r\n    /// <param name=\"cmdParms\">执行命令的参数</param>\r\n    private static void PrepareCommand(MySqlCommand cmd, MySqlConnection conn, MySqlTransaction trans, CommandType cmdType, string cmdText, MySqlParameter[] cmdParms)\r\n    {\r\n\r\n        if (conn.State != ConnectionState.Open)\r\n            conn.Open();\r\n\r\n        cmd.Connection = conn;\r\n        cmd.CommandText = cmdText;\r\n\r\n        if (trans != null)\r\n            cmd.Transaction = trans;\r\n\r\n        cmd.CommandType = cmdType;\r\n\r\n        if (cmdParms != null)\r\n        {\r\n            foreach (MySqlParameter parm in cmdParms)\r\n                cmd.Parameters.Add(parm);\r\n        }\r\n    }\r\n\r\n}\r\n\r\nMySqlHelper里面的方法和SqlHelper差不多，就不一一说明了。\r\n\r\n除此之外引用DBUTILITY类库也是一个很好的选择，DBUTILITY.DLL是一个集成SQLHELPER、MYSQLHELPER以及ORACLEHELPER等众多数据库操作类的类库，添加引用后就可以直接调用其中方法对数据库进行查询、编辑等操作，十分的方便。\r\n\r\n 3.配置你的MYSQL数据库链接字符串如下：\r\n//数据库连接字符串\r\npublic static string Conn = \"Database=\'数据库名\';Data Source=\'数据库服务器地址\';Port=\"端口号\";User Id=\'数据库用户名\';Password=\'密码\';charset=\'utf8\';pooling=true\";\r\n\r\n4.如何调用MySqlHelper类，实例如下：\r\n\r\n \r\n\r\n           MySqlParameter parameterCardId = new MySqlParameter(\"?card_num\", MySqlDbType.String, 32);\r\n           parameterCardId.Value = cardId;\r\n           DataSet resultSet = MySqlHelper.GetDataSet(MySqlHelper.Conn, CommandType.Text,\r\n                                                       \"SELECT * FROM  person WHERE card_num=?card_num\",\r\n                                                      new MySqlParameter[]\r\n                                                           {\r\n                                                               parameterCardId\r\n                                                           }\r\n               );\r\n           return resultSet;\r\n\r\n\r\n看上面的代码需要几点注意的地方：\r\n\r\n1)使用Parameter参数形式提交Command时必须要把\"@\"号换成\"?\"号，MySQL比较特殊，象MS、ODP.NET for Oracle、OleDb等方式，都是用\"@\"号的。\r\n  2)在MySQL中也有保留字，为了提供兼容，我必须要找到定界符，在MySql的论坛里找到了，使用的定界符是`，这个符号不是单引号，而是键盘上数字1前面的那个“点”。\r\n\r\n3）MySQL中的TOP功能倒是非常简单，只需要语句后面使用“limit n”就行。\r\n', '0', '2017-09-08 09:49:09', null, 'MySql.Helper.txt');
INSERT INTO `txt` VALUES ('596', '2', 'mysql.sql语句大全.txt', ' mysql sql语句大全\r\n (from:http://www.cnblogs.com/yunf/archive/2011/04/12/2013448.html)\r\n1、说明：创建数据库\r\nCREATE DATABASE database-name\r\n2、说明：删除数据库\r\ndrop database dbname\r\n3、说明：备份sql server\r\n--- 创建 备份数据的 device\r\nUSE master\r\nEXEC sp_addumpdevice \'disk\', \'testBack\', \'c:\\mssql7backup\\MyNwind_1.dat\'\r\n--- 开始 备份\r\nBACKUP DATABASE pubs TO testBack\r\n4、说明：创建新表\r\ncreate table tabname(col1 type1 [not null] [primary key],col2 type2 [not null],..)\r\n根据已有的表创建新表：\r\nA：create table tab_new like tab_old (使用旧表创建新表)\r\nB：create table tab_new as select col1,col2… from tab_old definition only\r\n5、说明：删除新表\r\ndrop table tabname\r\n6、说明：增加一个列\r\nAlter table tabname add column col type\r\n注：列增加后将不能删除。DB2中列加上后数据类型也不能改变，唯一能改变的是增加varchar类型的长度。\r\n7、说明：添加主键： Alter table tabname add primary key(col)\r\n说明：删除主键： Alter table tabname drop primary key(col)\r\n8、说明：创建索引：create [unique] index idxname on tabname(col….)\r\n删除索引：drop index idxname\r\n注：索引是不可更改的，想更改必须删除重新建。\r\n9、说明：创建视图：create view viewname as select statement\r\n删除视图：drop view viewname\r\n10、说明：几个简单的基本的sql语句\r\n选择：select * from table1 where 范围\r\n插入：insert into table1(field1,field2) values(value1,value2)\r\n删除：delete from table1 where 范围\r\n更新：update table1 set field1=value1 where 范围\r\n查找：select * from table1 where field1 like ’%value1%’ ---like的语法很精妙，查资料!\r\n排序：select * from table1 order by field1,field2 [desc]\r\n总数：select count as totalcount from table1\r\n求和：select sum(field1) as sumvalue from table1\r\n平均：select avg(field1) as avgvalue from table1\r\n最大：select max(field1) as maxvalue from table1\r\n最小：select min(field1) as minvalue from table1\r\n11、说明：几个高级查询运算词\r\nA： UNION 运算符\r\nUNION 运算符通过组合其他两个结果表（例如 TABLE1 和 TABLE2）并消去表中任何重复行而派生出一个结果表。当 ALL 随 UNION 一起使用时（即 UNION ALL），不消除重复行。两种情况下，派生表的每一行不是来自 TABLE1 就是来自 TABLE2。\r\nB： EXCEPT 运算符\r\nEXCEPT 运算符通过包括所有在 TABLE1 中但不在 TABLE2 中的行并消除所有重复行而派生出一个结果表。当 ALL 随 EXCEPT 一起使用时 (EXCEPT ALL)，不消除重复行。\r\nC： INTERSECT 运算符\r\nINTERSECT 运算符通过只包括 TABLE1 和 TABLE2 中都有的行并消除所有重复行而派生出一个结果表。当 ALL 随 INTERSECT 一起使用时 (INTERSECT ALL)，不消除重复行。\r\n注：使用运算词的几个查询结果行必须是一致的。\r\n12、说明：使用外连接\r\nA、left （outer） join：\r\n左外连接（左连接）：结果集几包括连接表的匹配行，也包括左连接表的所有行。\r\nSQL: select a.a, a.b, a.c, b.c, b.d, b.f from a LEFT OUT JOIN b ON a.a = b.c\r\nB：right （outer） join:\r\n右外连接(右连接)：结果集既包括连接表的匹配连接行，也包括右连接表的所有行。\r\nC：full/cross （outer） join：\r\n全外连接：不仅包括符号连接表的匹配行，还包括两个连接表中的所有记录。\r\n12、分组:Group by:\r\n  一张表，一旦分组完成后，查询后只能得到组相关的信息。\r\n 组相关的信息：（统计信息） count,sum,max,min,avg  分组的标准)\r\n    在SQLServer中分组时：不能以text,ntext,image类型的字段作为分组依据\r\n 在selecte统计函数中的字段，不能和普通的字段放在一起；\r\n13、对数据库进行操作：\r\n 分离数据库： sp_detach_db; 附加数据库：sp_attach_db 后接表明，附加需要完整的路径名\r\n14.如何修改数据库的名称:\r\nsp_renamedb \'old_name\', \'new_name\'\r\n \r\n二、提升\r\n1、说明：复制表(只复制结构,源表名：a 新表名：b) (Access可用)\r\n法一：select * into b from a where 1<>1（仅用于SQlServer）\r\n法二：select top 0 * into b from a\r\n2、说明：拷贝表(拷贝数据,源表名：a 目标表名：b) (Access可用)\r\ninsert into b(a, b, c) select d,e,f from b;\r\n3、说明：跨数据库之间表的拷贝(具体数据使用绝对路径) (Access可用)\r\ninsert into b(a, b, c) select d,e,f from b in ‘具体数据库’ where 条件\r\n例子：..from b in \'\"&Server.MapPath(\".\")&\"\\data.mdb\" &\"\' where..\r\n4、说明：子查询(表名1：a 表名2：b)\r\nselect a,b,c from a where a IN (select d from b ) 或者: select a,b,c from a where a IN (1,2,3)\r\n5、说明：显示文章、提交人和最后回复时间\r\nselect a.title,a.username,b.adddate from table a,(select max(adddate) adddate from table where table.title=a.title) b\r\n6、说明：外连接查询(表名1：a 表名2：b)\r\nselect a.a, a.b, a.c, b.c, b.d, b.f from a LEFT OUT JOIN b ON a.a = b.c\r\n7、说明：在线视图查询(表名1：a )\r\nselect * from (SELECT a,b,c FROM a) T where t.a > 1;\r\n8、说明：between的用法,between限制查询数据范围时包括了边界值,not between不包括\r\nselect * from table1 where time between time1 and time2\r\nselect a,b,c, from table1 where a not between 数值1 and 数值2\r\n9、说明：in 的使用方法\r\nselect * from table1 where a [not] in (‘值1’,’值2’,’值4’,’值6’)\r\n10、说明：两张关联表，删除主表中已经在副表中没有的信息\r\ndelete from table1 where not exists ( select * from table2 where table1.field1=table2.field1 )\r\n11、说明：四表联查问题：\r\nselect * from a left inner join b on a.a=b.b right inner join c on a.a=c.c inner join d on a.a=d.d where .....\r\n12、说明：日程安排提前五分钟提醒\r\nSQL: select * from 日程安排 where datediff(\'minute\',f开始时间,getdate())>5\r\n13、说明：一条sql 语句搞定数据库分页\r\nselect top 10 b.* from (select top 20 主键字段,排序字段 from 表名 order by 排序字段 desc) a,表名 b where b.主键字段 = a.主键字段 order by a.排序字段\r\n具体实现：\r\n关于数据库分页：\r\n  declare @start int,@end int\r\n  @sql  nvarchar(600)\r\n  set @sql=’select top’+str(@end-@start+1)+’+from T where rid not in(select top’+str(@str-1)+’Rid from T where Rid>-1)’\r\n  exec sp_executesql @sql\r\n \r\n注意：在top后不能直接跟一个变量，所以在实际应用中只有这样的进行特殊的处理。Rid为一个标识列，如果top后还有具体的字段，这样做是非常有好处的。因为这样可以避免 top的字段如果是逻辑索引的，查询的结果后实际表中的不一致（逻辑索引中的数据有可能和数据表中的不一致，而查询时如果处在索引则首先查询索引）\r\n14、说明：前10条记录\r\nselect top 10 * form table1 where 范围\r\n15、说明：选择在每一组b值相同的数据中对应的a最大的记录的所有信息(类似这样的用法可以用于论坛每月排行榜,每月热销产品分析,按科目成绩排名,等等.)\r\nselect a,b,c from tablename ta where a=(select max(a) from tablename tb where tb.b=ta.b)\r\n16、说明：包括所有在 TableA 中但不在 TableB和TableC 中的行并消除所有重复行而派生出一个结果表\r\n(select a from tableA ) except (select a from tableB) except (select a from tableC)\r\n17、说明：随机取出10条数据\r\nselect top 10 * from tablename order by newid()\r\n18、说明：随机选择记录\r\nselect newid()\r\n19、说明：删除重复记录\r\n1),delete from tablename where id not in (select max(id) from tablename group by col1,col2,...)\r\n2),select distinct * into temp from tablename\r\n  delete from tablename\r\n  insert into tablename select * from temp\r\n评价： 这种操作牵连大量的数据的移动，这种做法不适合大容量但数据操作\r\n3),例如：在一个外部表中导入数据，由于某些原因第一次只导入了一部分，但很难判断具体位置，这样只有在下一次全部导入，这样也就产生好多重复的字段，怎样删除重复字段\r\nalter table tablename\r\n--添加一个自增列\r\nadd  column_b int identity(1,1)\r\n delete from tablename where column_b not in(\r\nselect max(column_b)  from tablename group by column1,column2,...)\r\nalter table tablename drop column column_b\r\n20、说明：列出数据库里所有的表名\r\nselect name from sysobjects where type=\'U\' // U代表用户\r\n21、说明：列出表里的所有的列名\r\nselect name from syscolumns where id=object_id(\'TableName\')\r\n22、说明：列示type、vender、pcs字段，以type字段排列，case可以方便地实现多重选择，类似select 中的case。\r\nselect type,sum(case vender when \'A\' then pcs else 0 end),sum(case vender when \'C\' then pcs else 0 end),sum(case vender when \'B\' then pcs else 0 end) FROM tablename group by type\r\n显示结果：\r\ntype vender pcs\r\n电脑 A 1\r\n电脑 A 1\r\n光盘 B 2\r\n光盘 A 2\r\n手机 B 3\r\n手机 C 3\r\n23、说明：初始化表table1\r\nTRUNCATE TABLE table1\r\n24、说明：选择从10到15的记录\r\nselect top 5 * from (select top 15 * from table order by id asc) table_别名 order by id desc\r\n三、技巧\r\n1、1=1，1=2的使用，在SQL语句组合时用的较多\r\n“where 1=1” 是表示选择全部    “where 1=2”全部不选，\r\n如：\r\nif @strWhere !=\'\'\r\nbegin\r\nset @strSQL = \'select count(*) as Total from [\' + @tblName + \'] where \' + @strWhere\r\nend\r\nelse\r\nbegin\r\nset @strSQL = \'select count(*) as Total from [\' + @tblName + \']\'\r\nend\r\n我们可以直接写成\r\n错误！未找到目录项。\r\nset @strSQL = \'select count(*) as Total from [\' + @tblName + \'] where 1=1 安定 \'+ @strWhere 2、收缩数据库\r\n--重建索引\r\nDBCC REINDEX\r\nDBCC INDEXDEFRAG\r\n--收缩数据和日志\r\nDBCC SHRINKDB\r\nDBCC SHRINKFILE\r\n3、压缩数据库\r\ndbcc shrinkdatabase(dbname)\r\n4、转移数据库给新用户以已存在用户权限\r\nexec sp_change_users_login \'update_one\',\'newname\',\'oldname\'\r\ngo\r\n5、检查备份集\r\nRESTORE VERIFYONLY from disk=\'E:\\dvbbs.bak\'\r\n6、修复数据库\r\nALTER DATABASE [dvbbs] SET SINGLE_USER\r\nGO\r\nDBCC CHECKDB(\'dvbbs\',repair_allow_data_loss) WITH TABLOCK\r\nGO\r\nALTER DATABASE [dvbbs] SET MULTI_USER\r\nGO\r\n7、日志清除\r\nSET NOCOUNT ON\r\nDECLARE @LogicalFileName sysname,\r\n @MaxMinutes INT,\r\n @NewSize INT\r\n \r\nUSE tablename -- 要操作的数据库名\r\nSELECT  @LogicalFileName = \'tablename_log\', -- 日志文件名\r\n@MaxMinutes = 10, -- Limit on time allowed to wrap log.\r\n @NewSize = 1  -- 你想设定的日志文件的大小(M)\r\nSetup / initialize\r\nDECLARE @OriginalSize int\r\nSELECT @OriginalSize = size\r\n FROM sysfiles\r\n WHERE name = @LogicalFileName\r\nSELECT \'Original Size of \' + db_name() + \' LOG is \' +\r\n CONVERT(VARCHAR(30),@OriginalSize) + \' 8K pages or \' +\r\n CONVERT(VARCHAR(30),(@OriginalSize*8/1024)) + \'MB\'\r\n FROM sysfiles\r\n WHERE name = @LogicalFileName\r\nCREATE TABLE DummyTrans\r\n (DummyColumn char (8000) not null)\r\n \r\nDECLARE @Counter    INT,\r\n @StartTime DATETIME,\r\n @TruncLog   VARCHAR(255)\r\nSELECT @StartTime = GETDATE(),\r\n @TruncLog = \'BACKUP LOG \' + db_name() + \' WITH TRUNCATE_ONLY\'\r\nDBCC SHRINKFILE (@LogicalFileName, @NewSize)\r\nEXEC (@TruncLog)\r\n-- Wrap the log if necessary.\r\nWHILE @MaxMinutes > DATEDIFF (mi, @StartTime, GETDATE()) -- time has not expired\r\n AND @OriginalSize = (SELECT size FROM sysfiles WHERE name = @LogicalFileName)\r\n AND (@OriginalSize * 8 /1024) > @NewSize\r\n BEGIN -- Outer loop.\r\nSELECT @Counter = 0\r\n WHILE   ((@Counter < @OriginalSize / 16) AND (@Counter < 50000))\r\n BEGIN -- update\r\n INSERT DummyTrans VALUES (\'Fill Log\') DELETE DummyTrans\r\n SELECT @Counter = @Counter + 1\r\n END\r\n EXEC (@TruncLog)\r\n END\r\nSELECT \'Final Size of \' + db_name() + \' LOG is \' +\r\n CONVERT(VARCHAR(30),size) + \' 8K pages or \' +\r\n CONVERT(VARCHAR(30),(size*8/1024)) + \'MB\'\r\n FROM sysfiles\r\n WHERE name = @LogicalFileName\r\nDROP TABLE DummyTrans\r\nSET NOCOUNT OFF\r\n8、说明：更改某个表\r\nexec sp_changeobjectowner \'tablename\',\'dbo\'\r\n9、存储更改全部表\r\nCREATE PROCEDURE dbo.User_ChangeObjectOwnerBatch\r\n@OldOwner as NVARCHAR(128),\r\n@NewOwner as NVARCHAR(128)\r\nAS\r\nDECLARE @Name    as NVARCHAR(128)\r\nDECLARE @Owner   as NVARCHAR(128)\r\nDECLARE @OwnerName   as NVARCHAR(128)\r\nDECLARE curObject CURSOR FOR\r\nselect \'Name\'    = name,\r\n   \'Owner\'    = user_name(uid)\r\nfrom sysobjects\r\nwhere user_name(uid)=@OldOwner\r\norder by name\r\nOPEN   curObject\r\nFETCH NEXT FROM curObject INTO @Name, @Owner\r\nWHILE(@@FETCH_STATUS=0)\r\nBEGIN   \r\nif @Owner=@OldOwner\r\nbegin\r\n   set @OwnerName = @OldOwner + \'.\' + rtrim(@Name)\r\n   exec sp_changeobjectowner @OwnerName, @NewOwner\r\nend\r\n-- select @name,@NewOwner,@OldOwner\r\nFETCH NEXT FROM curObject INTO @Name, @Owner\r\nEND\r\nclose curObject\r\ndeallocate curObject\r\nGO\r\n \r\n10、SQL SERVER中直接循环写入数据\r\ndeclare @i int\r\nset @i=1\r\nwhile @i<30\r\nbegin\r\n    insert into test (userid) values(@i)\r\n    set @i=@i+1\r\nend\r\n案例：\r\n有如下表，要求就裱中所有沒有及格的成績，在每次增長0.1的基礎上，使他們剛好及格:\r\n Name     score\r\n Zhangshan 80\r\n Lishi       59\r\n Wangwu      50\r\n Songquan 69\r\nwhile((select min(score) from tb_table)<60)\r\nbegin\r\nupdate tb_table set score =score*1.01\r\nwhere score<60\r\nif  (select min(score) from tb_table)>60\r\n  break\r\n else\r\n    continue\r\nend\r\n \r\n数据开发-经典\r\n \r\n1.按姓氏笔画排序:\r\nSelect * From TableName Order By CustomerName Collate Chinese_PRC_Stroke_ci_as //从少到多\r\n2.数据库加密:\r\nselect encrypt(\'原始密码\')\r\nselect pwdencrypt(\'原始密码\')\r\nselect pwdcompare(\'原始密码\',\'加密后密码\') = 1--相同；否则不相同 encrypt(\'原始密码\')\r\nselect pwdencrypt(\'原始密码\')\r\nselect pwdcompare(\'原始密码\',\'加密后密码\') = 1--相同；否则不相同\r\n3.取回表中字段:\r\ndeclare @list varchar(1000),\r\n@sql nvarchar(1000)\r\nselect @list=@list+\',\'+b.name from sysobjects a,syscolumns b where a.id=b.id and a.name=\'表A\'\r\nset @sql=\'select \'+right(@list,len(@list)-1)+\' from 表A\'\r\nexec (@sql)\r\n4.查看硬盘分区:\r\nEXEC master..xp_fixeddrives\r\n5.比较A,B表是否相等:\r\nif (select checksum_agg(binary_checksum(*)) from A)\r\n     =\r\n    (select checksum_agg(binary_checksum(*)) from B)\r\nprint \'相等\'\r\nelse\r\nprint \'不相等\'\r\n6.杀掉所有的事件探察器进程:\r\nDECLARE hcforeach CURSOR GLOBAL FOR SELECT \'kill \'+RTRIM(spid) FROM master.dbo.sysprocesses\r\nWHERE program_name IN(\'SQL profiler\',N\'SQL 事件探查器\')\r\nEXEC sp_msforeach_worker \'?\'\r\n7.记录搜索:\r\n开头到N条记录\r\nSelect Top N * From 表\r\n-------------------------------\r\nN到M条记录(要有主索引ID)\r\nSelect Top M-N * From 表 Where ID in (Select Top M ID From 表) Order by ID   Desc\r\n----------------------------------\r\nN到结尾记录\r\nSelect Top N * From 表 Order by ID Desc\r\n案例\r\n例如1：一张表有一万多条记录，表的第一个字段 RecID 是自增长字段， 写一个SQL语句，找出表的第31到第40个记录。\r\n select top 10 recid from A where recid not  in(select top 30 recid from A)\r\n分析：如果这样写会产生某些问题，如果recid在表中存在逻辑索引。\r\n select top 10 recid from A where……是从索引中查找，而后面的select top 30 recid from A则在数据表中查找，这样由于索引中的顺序有可能和数据表中的不一致，这样就导致查询到的不是本来的欲得到的数据。\r\n解决方案\r\n1， 用order by select top 30 recid from A order by ricid 如果该字段不是自增长，就会出现问题\r\n2， 在那个子查询中也加条件：select top 30 recid from A where recid>-1\r\n例2：查询表中的最后以条记录，并不知道这个表共有多少数据,以及表结构。\r\nset @s = \'select top 1 * from T   where pid not in (select top \' + str(@count-1) + \' pid  from  T)\'\r\nprint @s      exec  sp_executesql  @s\r\n9：获取当前数据库中的所有用户表\r\nselect Name from sysobjects where xtype=\'u\' and status>=0\r\n10：获取某一个表的所有字段\r\nselect name from syscolumns where id=object_id(\'表名\')\r\nselect name from syscolumns where id in (select id from sysobjects where type = \'u\' and name = \'表名\')\r\n两种方式的效果相同\r\n11：查看与某一个表相关的视图、存储过程、函数\r\nselect a.* from sysobjects a, syscomments b where a.id = b.id and b.text like \'%表名%\'\r\n12：查看当前数据库中所有存储过程\r\nselect name as 存储过程名称 from sysobjects where xtype=\'P\'\r\n13：查询用户创建的所有数据库\r\nselect * from master..sysdatabases D where sid not in(select sid from master..syslogins where name=\'sa\')\r\n或者\r\nselect dbid, name AS DB_NAME from master..sysdatabases where sid <> 0x01\r\n14：查询某一个表的字段和数据类型\r\nselect column_name,data_type from information_schema.columns\r\nwhere table_name = \'表名\'\r\n15：不同服务器数据库之间的数据操作\r\n--创建链接服务器\r\nexec sp_addlinkedserver   \'ITSV \', \' \', \'SQLOLEDB \', \'远程服务器名或ip地址 \'\r\nexec sp_addlinkedsrvlogin  \'ITSV \', \'false \',null, \'用户名 \', \'密码 \'\r\n--查询示例\r\nselect * from ITSV.数据库名.dbo.表名\r\n--导入示例\r\nselect * into 表 from ITSV.数据库名.dbo.表名\r\n--以后不再使用时删除链接服务器\r\nexec sp_dropserver  \'ITSV \', \'droplogins \'\r\n \r\n--连接远程/局域网数据(openrowset/openquery/opendatasource)\r\n--1、openrowset\r\n--查询示例\r\nselect * from openrowset( \'SQLOLEDB \', \'sql服务器名 \'; \'用户名 \'; \'密码 \',数据库名.dbo.表名)\r\n--生成本地表\r\nselect * into 表 from openrowset( \'SQLOLEDB \', \'sql服务器名 \'; \'用户名 \'; \'密码 \',数据库名.dbo.表名)\r\n \r\n--把本地表导入远程表\r\ninsert openrowset( \'SQLOLEDB \', \'sql服务器名 \'; \'用户名 \'; \'密码 \',数据库名.dbo.表名)\r\nselect *from 本地表\r\n--更新本地表\r\nupdate b\r\nset b.列A=a.列A\r\n from openrowset( \'SQLOLEDB \', \'sql服务器名 \'; \'用户名 \'; \'密码 \',数据库名.dbo.表名)as a inner join 本地表 b\r\non a.column1=b.column1\r\n--openquery用法需要创建一个连接\r\n--首先创建一个连接创建链接服务器\r\nexec sp_addlinkedserver   \'ITSV \', \' \', \'SQLOLEDB \', \'远程服务器名或ip地址 \'\r\n--查询\r\nselect *\r\nFROM openquery(ITSV,  \'SELECT *  FROM 数据库.dbo.表名 \')\r\n--把本地表导入远程表\r\ninsert openquery(ITSV,  \'SELECT *  FROM 数据库.dbo.表名 \')\r\nselect * from 本地表\r\n--更新本地表\r\nupdate b\r\nset b.列B=a.列B\r\nFROM openquery(ITSV,  \'SELECT * FROM 数据库.dbo.表名 \') as a\r\ninner join 本地表 b on a.列A=b.列A\r\n \r\n--3、opendatasource/openrowset\r\nSELECT   *\r\nFROM   opendatasource( \'SQLOLEDB \',  \'Data Source=ip/ServerName;User ID=登陆名;Password=密码 \' ).test.dbo.roy_ta\r\n--把本地表导入远程表\r\ninsert opendatasource( \'SQLOLEDB \',  \'Data Source=ip/ServerName;User ID=登陆名;Password=密码 \').数据库.dbo.表名\r\nselect * from 本地表\r\nSQL Server基本函数\r\nSQL Server基本函数\r\n1.字符串函数 长度与分析用\r\n1,datalength(Char_expr) 返回字符串包含字符数,但不包含后面的空格\r\n2,substring(expression,start,length) 取子串，字符串的下标是从“1”，start为起始位置，length为字符串长度，实际应用中以len(expression)取得其长度\r\n3,right(char_expr,int_expr) 返回字符串右边第int_expr个字符，还用left于之相反\r\n4,isnull( check_expression , replacement_value )如果check_expression為空，則返回replacement_value的值，不為空，就返回check_expression字符操作类\r\n5,Sp_addtype 自定義數據類型\r\n例如：EXEC sp_addtype birthday, datetime, \'NULL\'\r\n6,set nocount {on|off}\r\n使返回的结果中不包含有关受 Transact-SQL 语句影响的行数的信息。如果存储过程中包含的一些语句并不返回许多实际的数据，则该设置由于大量减少了网络流量，因此可显著提高性能。SET NOCOUNT 设置是在执行或运行时设置，而不是在分析时设置。\r\nSET NOCOUNT 为 ON 时，不返回计数（表示受 Transact-SQL 语句影响的行数）。\r\nSET NOCOUNT 为 OFF 时，返回计数\r\n常识\r\n \r\n在SQL查询中：from后最多可以跟多少张表或视图：256\r\n在SQL语句中出现 Order by,查询时，先排序，后取\r\n在SQL中，一个字段的最大容量是8000，而对于nvarchar(4000),由于nvarchar是Unicode码。\r\n  \r\nSQLServer2000同步复制技术实现步骤\r\n一、 预备工作\r\n1.发布服务器,订阅服务器都创建一个同名的windows用户,并设置相同的密码,做为发布快照文件夹的有效访问用户\r\n--管理工具\r\n--计算机管理\r\n--用户和组\r\n--右键用户\r\n--新建用户\r\n--建立一个隶属于administrator组的登陆windows的用户（SynUser）\r\n2.在发布服务器上,新建一个共享目录,做为发布的快照文件的存放目录,操作:\r\n我的电脑--D:\\ 新建一个目录,名为: PUB\r\n--右键这个新建的目录\r\n--属性--共享\r\n--选择\"共享该文件夹\"\r\n--通过\"权限\"按纽来设置具体的用户权限,保证第一步中创建的用户(SynUser) 具有对该文件夹的所有权限\r\n \r\n--确定\r\n3.设置SQL代理(SQLSERVERAGENT)服务的启动用户(发布/订阅服务器均做此设置)\r\n开始--程序--管理工具--服务\r\n--右键SQLSERVERAGENT\r\n--属性--登陆--选择\"此账户\"\r\n--输入或者选择第一步中创建的windows登录用户名（SynUser）\r\n--\"密码\"中输入该用户的密码\r\n4.设置SQL Server身份验证模式,解决连接时的权限问题(发布/订阅服务器均做此设置)\r\n企业管理器\r\n--右键SQL实例--属性\r\n--安全性--身份验证\r\n--选择\"SQL Server 和 Windows\"\r\n--确定\r\n5.在发布服务器和订阅服务器上互相注册\r\n企业管理器\r\n--右键SQL Server组\r\n--新建SQL Server注册...\r\n--下一步--可用的服务器中,输入你要注册的远程服务器名 --添加\r\n--下一步--连接使用,选择第二个\"SQL Server身份验证\"\r\n--下一步--输入用户名和密码（SynUser）\r\n--下一步--选择SQL Server组,也可以创建一个新组\r\n--下一步--完成\r\n6.对于只能用IP,不能用计算机名的,为其注册服务器别名（此步在实施中没用到）\r\n (在连接端配置,比如,在订阅服务器上配置的话,服务器名称中输入的是发布服务器的IP)\r\n开始--程序--Microsoft SQL Server--客户端网络实用工具\r\n--别名--添加\r\n--网络库选择\"tcp/ip\"--服务器别名输入SQL服务器名\r\n--连接参数--服务器名称中输入SQL服务器ip地址\r\n--如果你修改了SQL的端口,取消选择\"动态决定端口\",并输入对应的端口号\r\n二、 正式配置\r\n1、配置发布服务器\r\n打开企业管理器，在发布服务器（B、C、D）上执行以下步骤:\r\n(1) 从[工具]下拉菜单的[复制]子菜单中选择[配置发布、订阅服务器和分发]出现配置发布和分发向导\r\n(2) [下一步] 选择分发服务器 可以选择把发布服务器自己作为分发服务器或者其他sql的服务器（选择自己）\r\n(3) [下一步] 设置快照文件夹\r\n采用默认\\\\servername\\Pub\r\n(4) [下一步] 自定义配置\r\n可以选择:是,让我设置分发数据库属性启用发布服务器或设置发布设置\r\n否,使用下列默认设置（推荐）\r\n(5) [下一步] 设置分发数据库名称和位置 采用默认值\r\n(6) [下一步] 启用发布服务器 选择作为发布的服务器\r\n(7) [下一步] 选择需要发布的数据库和发布类型\r\n(8) [下一步] 选择注册订阅服务器\r\n(9) [下一步] 完成配置\r\n2、创建出版物\r\n发布服务器B、C、D上\r\n(1)从[工具]菜单的[复制]子菜单中选择[创建和管理发布]命令\r\n(2)选择要创建出版物的数据库，然后单击[创建发布]\r\n(3)在[创建发布向导]的提示对话框中单击[下一步]系统就会弹出一个对话框。对话框上的内容是复制的三个类型。我们现在选第一个也就是默认的快照发布(其他两个大家可以去看看帮助)\r\n(4)单击[下一步]系统要求指定可以订阅该发布的数据库服务器类型,\r\nSQLSERVER允许在不同的数据库如 orACLE或ACCESS之间进行数据复制。\r\n但是在这里我们选择运行\"SQL SERVER 2000\"的数据库服务器\r\n(5)单击[下一步]系统就弹出一个定义文章的对话框也就是选择要出版的表\r\n注意: 如果前面选择了事务发布 则再这一步中只能选择带有主键的表\r\n(6)选择发布名称和描述\r\n(7)自定义发布属性 向导提供的选择:\r\n是 我将自定义数据筛选,启用匿名订阅和或其他自定义属性\r\n否 根据指定方式创建发布 （建议采用自定义的方式）\r\n(8)[下一步] 选择筛选发布的方式\r\n(9)[下一步] 可以选择是否允许匿名订阅\r\n1)如果选择署名订阅,则需要在发布服务器上添加订阅服务器\r\n方法: [工具]->[复制]->[配置发布、订阅服务器和分发的属性]->[订阅服务器] 中添加\r\n否则在订阅服务器上请求订阅时会出现的提示:改发布不允许匿名订阅\r\n如果仍然需要匿名订阅则用以下解决办法\r\n[企业管理器]->[复制]->[发布内容]->[属性]->[订阅选项] 选择允许匿名请求订阅\r\n2)如果选择匿名订阅,则配置订阅服务器时不会出现以上提示\r\n(10)[下一步] 设置快照 代理程序调度\r\n(11)[下一步] 完成配置\r\n当完成出版物的创建后创建出版物的数据库也就变成了一个共享数据库\r\n有数据\r\nsrv1.库名..author有字段:id,name,phone,\r\nsrv2.库名..author有字段:id,name,telphone,adress\r\n \r\n要求：\r\nsrv1.库名..author增加记录则srv1.库名..author记录增加\r\nsrv1.库名..author的phone字段更新，则srv1.库名..author对应字段telphone更新\r\n--*/\r\n \r\n--大致的处理步骤\r\n--1.在 srv1 上创建连接服务器,以便在 srv1 中操作 srv2,实现同步\r\nexec sp_addlinkedserver \'srv2\',\'\',\'SQLOLEDB\',\'srv2的sql实例名或ip\'\r\nexec sp_addlinkedsrvlogin \'srv2\',\'false\',null,\'用户名\',\'密码\'\r\ngo\r\n--2.在 srv1 和 srv2 这两台电脑中,启动 msdtc(分布式事务处理服务),并且设置为自动启动\r\n。我的电脑--控制面板--管理工具--服务--右键 Distributed Transaction Coordinator--属性--启动--并将启动类型设置为自动启动\r\ngo\r\n \r\n \r\n--然后创建一个作业定时调用上面的同步处理存储过程就行了\r\n \r\n企业管理器\r\n--管理\r\n--SQL Server代理\r\n--右键作业\r\n--新建作业\r\n--\"常规\"项中输入作业名称\r\n--\"步骤\"项\r\n--新建\r\n--\"步骤名\"中输入步骤名\r\n--\"类型\"中选择\"Transact-SQL 脚本(TSQL)\"\r\n--\"数据库\"选择执行命令的数据库\r\n--\"命令\"中输入要执行的语句: exec p_process\r\n--确定\r\n--\"调度\"项\r\n--新建调度\r\n--\"名称\"中输入调度名称\r\n--\"调度类型\"中选择你的作业执行安排\r\n--如果选择\"反复出现\"\r\n--点\"更改\"来设置你的时间安排\r\n \r\n \r\n然后将SQL Agent服务启动,并设置为自动启动,否则你的作业不会被执行\r\n \r\n设置方法:\r\n我的电脑--控制面板--管理工具--服务--右键 SQLSERVERAGENT--属性--启动类型--选择\"自动启动\"--确定.\r\n \r\n \r\n--3.实现同步处理的方法2,定时同步\r\n \r\n--在srv1中创建如下的同步处理存储过程\r\ncreate proc p_process\r\nas\r\n--更新修改过的数据\r\nupdate b set name=i.name,telphone=i.telphone\r\nfrom srv2.库名.dbo.author b,author i\r\nwhere b.id=i.id and\r\n(b.name <> i.name or b.telphone <> i.telphone)\r\n \r\n--插入新增的数据\r\ninsert srv2.库名.dbo.author(id,name,telphone)\r\nselect id,name,telphone from author i\r\nwhere not exists(\r\nselect * from srv2.库名.dbo.author where id=i.id)\r\n \r\n--删除已经删除的数据(如果需要的话)\r\ndelete b\r\nfrom srv2.库名.dbo.author b\r\nwhere not exists(\r\nselect * from author where id=b.id)\r\ngo\r\n', '0', '2017-09-08 09:49:09', null, 'mysql.sql语句大全.txt');
INSERT INTO `txt` VALUES ('597', '2', 'mysql中防止sql注入的模糊查询方法.MySqlParameter.Like.txt', '  -----------mysql中防止sql注入的模糊查询方法.MySqlParameter.Like-----\r\n-------------------2014年3月24日16:54:10----Career的接口测试程序中-----悬赏职位中查询候选人列表----\r\n\r\n        /// <summary>\r\n        /// 获取候选人列表\r\n        /// </summary>\r\n        /// <param name=\"PageSize\"></param>\r\n        /// <param name=\"PageIndex\"></param>\r\n        /// <param name=\"mobile\"></param>\r\n        /// <param name=\"email\"></param>\r\n        /// <param name=\"name\"></param>\r\n        /// <returns></returns>\r\n        public DataTable GetPagedList(int PageSize, int PageIndex, string mobile, string email, string name)\r\n        {\r\n            int index = PageSize * (PageIndex - 1);\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"select \");\r\n            sb.Append(\"FirstName,LastName, sid,email,PhoneNumber \");//todo\r\n            sb.Append(\" from users \");\r\n            sb.Append(\" where user_group_sid=36 \");//简历\r\n            //////sb.Append(\" AND (FirstName like \'%?name%\' Or LastName like \'%?name%\')  \");//////\r\n            //////sb.Append(\" and email like \'%?email%\' );///////这样是错误的代码：解析出来就成\'%\'email\'%\'\r\n            //////sb.Append(\" and PhoneNumber like \'%?mobile%\' );////////\r\n            sb.Append(\" AND (FirstName like ?name Or LastName like ?name)   and email like ?email   and PhoneNumber like ?mobile \");\r\n            sb.Append(\"limit ?pageindex,?pagesize\");\r\n          \r\n            MySql.Data.MySqlClient.MySqlParameter[] paramPaged = new MySql.Data.MySqlClient.MySqlParameter[] { \r\n                new MySql.Data.MySqlClient.MySqlParameter(\"?pageindex\",MySql.Data.MySqlClient.MySqlDbType.Int32),\r\n                new MySql.Data.MySqlClient.MySqlParameter(\"?pagesize\",MySql.Data.MySqlClient.MySqlDbType.Int32),\r\n                     new MySql.Data.MySqlClient.MySqlParameter(\"?name\",MySql.Data.MySqlClient.MySqlDbType.VarChar),\r\n                new MySql.Data.MySqlClient.MySqlParameter(\"?email\",MySql.Data.MySqlClient.MySqlDbType.VarChar),\r\n                  new MySql.Data.MySqlClient.MySqlParameter(\"?mobile\",MySql.Data.MySqlClient.MySqlDbType.VarChar)\r\n            };\r\n            paramPaged[0].Value = index;\r\n            paramPaged[1].Value = PageSize;\r\n            paramPaged[2].Value = \"%\" + name + \"%\";\r\n            paramPaged[3].Value = \"%\" + email + \"%\";\r\n            paramPaged[4].Value = \"%\" + mobile + \"%\";\r\n            DataTable dt = MySqlHelper.GetDataSet(MySqlHelper.Conn, CommandType.Text, sb.ToString(), paramPaged).Tables[0];\r\n            return dt;\r\n        }\r\n', '0', '2017-09-08 09:49:09', null, 'mysql中防止sql注入的模糊查询方法.MySqlParameter.Like.txt');
INSERT INTO `txt` VALUES ('598', '2', 'mysql分页.最佳方法.txt', '---我测试过（jun）-----\r\n\r\n这个方法效率高，但是加上where 条件就不快了，\r\nselect * from listings where sid > (select sid from listings order by sid limit 200, 1) limit 10\r\n\r\n\r\n\r\n\r\n普通方法（通用）\r\nselect * from listings \r\n        where listing_type_sid=6 and  active=1 and ishot=1  \r\n        limit ?pageindex,?pagesize\r\n\r\n\r\n\r\n MySql.Data.MySqlClient.MySqlParameter[] paramPaged = new MySql.Data.MySqlClient.MySqlParameter[] { \r\n                new MySql.Data.MySqlClient.MySqlParameter(\"?pageindex\",MySql.Data.MySqlClient.MySqlDbType.Int32),\r\n                new MySql.Data.MySqlClient.MySqlParameter(\"?pagesize\",MySql.Data.MySqlClient.MySqlDbType.Int32)\r\n            };\r\n            paramPaged[0].Value = index;\r\n            paramPaged[1].Value = PageSize;\r\n\r\n\r\n', '0', '2017-09-08 09:49:09', null, 'mysql分页.最佳方法.txt');
INSERT INTO `txt` VALUES ('599', '2', 'mysql分页limit 优化.txt', 'mysql分页limit 优化\r\n\r\n    博客分类：\r\n    MySQL & Sphinx\r\n\r\nMySQLSQL算法Blog.net \r\nmysql的分页比较简单，只需要limit offset,length就可以获取数据了，但是当offset和length比较大的时候，mysql明显性能下降\r\n\r\n1.子查询优化法\r\n先找出第一条数据，然后大于等于这条数据的id就是要获取的数据\r\n缺点：数据必须是连续的，可以说不能有where条件，where条件会筛选数据，导致数据失去连续性\r\n\r\n实验下\r\nSql代码  收藏代码\r\n\r\n    mysql> set profiling=1;  \r\n    Query OK, 0 rows affected (0.00 sec)  \r\n      \r\n    mysql> select count(*) from Member;  \r\n    +----------+  \r\n    | count(*) |  \r\n    +----------+  \r\n    |   169566 |   \r\n    +----------+  \r\n    1 row in set (0.00 sec)  \r\n      \r\n    mysql> pager grep !~-  \r\n    PAGER set to \'grep !~-\'  \r\n      \r\n    mysql> select * from Member limit 10, 100;  \r\n    100 rows in set (0.00 sec)  \r\n      \r\n    mysql> select * from Member where MemberID >= (select MemberID from Member limit 10,1) limit 100;  \r\n    100 rows in set (0.00 sec)  \r\n      \r\n    mysql> select * from Member limit 1000, 100;  \r\n    100 rows in set (0.01 sec)  \r\n      \r\n    mysql> select * from Member where MemberID >= (select MemberID from Member limit 1000,1) limit 100;  \r\n    100 rows in set (0.00 sec)  \r\n      \r\n    mysql> select * from Member limit 100000, 100;  \r\n    100 rows in set (0.10 sec)  \r\n      \r\n    mysql> select * from Member where MemberID >= (select MemberID from Member limit 100000,1) limit 100;  \r\n    100 rows in set (0.02 sec)  \r\n      \r\n    mysql> nopager  \r\n    PAGER set to stdout  \r\n      \r\n      \r\n    mysql> show profiles\\G  \r\n    *************************** 1. row ***************************  \r\n    Query_ID: 1  \r\n    Duration: 0.00003300  \r\n       Query: select count(*) from Member  \r\n      \r\n    *************************** 2. row ***************************  \r\n    Query_ID: 2  \r\n    Duration: 0.00167000  \r\n       Query: select * from Member limit 10, 100  \r\n    *************************** 3. row ***************************  \r\n    Query_ID: 3  \r\n    Duration: 0.00112400  \r\n       Query: select * from Member where MemberID >= (select MemberID from Member limit 10,1) limit 100  \r\n      \r\n    *************************** 4. row ***************************  \r\n    Query_ID: 4  \r\n    Duration: 0.00263200  \r\n       Query: select * from Member limit 1000, 100  \r\n    *************************** 5. row ***************************  \r\n    Query_ID: 5  \r\n    Duration: 0.00134000  \r\n       Query: select * from Member where MemberID >= (select MemberID from Member limit 1000,1) limit 100  \r\n      \r\n    *************************** 6. row ***************************  \r\n    Query_ID: 6  \r\n    Duration: 0.09956700  \r\n       Query: select * from Member limit 100000, 100  \r\n    *************************** 7. row ***************************  \r\n    Query_ID: 7  \r\n    Duration: 0.02447700  \r\n       Query: select * from Member where MemberID >= (select MemberID from Member limit 100000,1) limit 100  \r\n\r\n\r\n\r\n从结果中可以得知，当偏移1000以上使用子查询法可以有效的提高性能。\r\n\r\n\r\n2.倒排表优化法\r\n倒排表法类似建立索引，用一张表来维护页数，然后通过高效的连接得到数据\r\n\r\n缺点：只适合数据数固定的情况，数据不能删除，维护页表困难\r\n\r\n具体请看，http://blog.chinaunix.net/u/29134/showart_1333566.html\r\n\r\n3.反向查找优化法\r\n当偏移超过一半记录数的时候，先用排序，这样偏移就反转了\r\n\r\n缺点：order by优化比较麻烦，要增加索引，索引影响数据的修改效率，并且要知道总记录数\r\n，偏移大于数据的一半\r\n\r\n引用\r\nlimit偏移算法：\r\n正向查找： (当前页 - 1) * 页长度\r\n反向查找： 总记录 - 当前页 * 页长度\r\n\r\n\r\n做下实验，看看性能如何\r\n\r\n总记录数：1,628,775\r\n每页记录数： 40\r\n总页数：1,628,775 / 40 = 40720\r\n中间页数：40720 / 2 = 20360\r\n\r\n第21000页\r\n正向查找SQL:\r\nSql代码  收藏代码\r\n\r\n    SELECT * FROM `abc` WHERE `BatchID` = 123 LIMIT 839960, 40  \r\n\r\n\r\n时间：1.8696 秒\r\n\r\n反向查找sql:\r\nSql代码  收藏代码\r\n\r\n    SELECT * FROM `abc` WHERE `BatchID` = 123 ORDER BY InputDate DESC LIMIT 788775, 40  \r\n\r\n\r\n时间：1.8336 秒\r\n\r\n第30000页\r\n正向查找SQL:\r\nSql代码  收藏代码\r\n\r\n    SELECT * FROM `abc` WHERE `BatchID` = 123 LIMIT 1199960, 40  \r\n\r\n\r\n时间：2.6493 秒\r\n\r\n反向查找sql:\r\nSql代码  收藏代码\r\n\r\n    SELECT * FROM `abc` WHERE `BatchID` = 123 ORDER BY InputDate DESC LIMIT 428775, 40  \r\n\r\n\r\n时间：1.0035 秒\r\n\r\n注意，反向查找的结果是是降序desc的，并且InputDate是记录的插入时间，也可以用主键联合索引，但是不方便。\r\n\r\n\r\n4.limit限制优化法\r\n把limit偏移量限制低于某个数。。超过这个数等于没数据，我记得alibaba的dba说过他们是这样做的\r\n\r\n5.只查索引法\r\nhttp://willko.iteye.com/blog/670120\r\n\r\n总结：limit的优化限制都比较多，所以实际情况用或者不用只能具体情况具体分析了。页数那么后，基本很少人看的。。。 \r\n', '0', '2017-09-08 09:49:09', null, 'mysql分页limit 优化.txt');
INSERT INTO `txt` VALUES ('600', '2', 'Mysql和sqlServer命令比较.txt', 'Mysql和sqlServer命令比较\r\n（from：）\r\n按语句功能划分，依次讲解\r\n目录\r\n一数据定义 2\r\n\r\n1 数据库操作基本命令 2\r\n\r\n2 CREATE TABLE --创建一个数据库表 2\r\n\r\n2.1 PRIMARY KEY 约束（主键）区别解析： 2\r\n\r\n2.1.1 创建primary key 2\r\n\r\n2.1.2 撤销 PRIMARY KEY 约束 3\r\n\r\n2.1.3 创建外健约束 3\r\n\r\n2.1.4 撤销外健约束 4\r\n\r\n2.2 UNIQUE 约束（唯一的，独一无二的）区别解析 4\r\n\r\n2.2.1 创建UNIQUE约束 4\r\n\r\n2.2.2 撤销 UNIQUE 约束 5\r\n\r\n2.3 CHECK 约束 5\r\n\r\n2.3.1 创建 CHECK约束 5\r\n\r\n2.3.2 撤销 CHECK约束 6\r\n\r\n2.4 DEFAULT 约束（系统默认值） 6\r\n\r\n2.4.1 创建DEFAULT约束 6\r\n\r\n2.4 .2 撤消DEFAULT约束 7\r\n\r\n2.5 索引区别 7\r\n\r\n2.6 主键自动增加的区别 7\r\n\r\n2.7 MySQL支持enum,和set类型，SQL Server不支持 8\r\n\r\n2.7.1 枚举enum 8\r\n\r\n2.7.2 集合set 9\r\n\r\n2.8 MySQL不支持nchar,nvarchar,ntext类型 10\r\n\r\n3 DROP TABLE –删除一个数据库表 10\r\n\r\n4 显示库表 11\r\n\r\n5 alter 修改库表 11\r\n二 数据操作 12\r\n\r\n2.1 limit和top 12\r\n\r\n2.2 ISNULL()函数 12\r\n\r\n2.3 select查询 12\r\n\r\n2.4 insert 插入 12\r\n\r\n2.6 update 修改 12\r\n\r\n2.7 delete 删除 13\r\n三 语法定义 13\r\n\r\n3.1 注释符区别 13\r\n\r\n3.2 识别符的区别 13\r\n\r\n3.3存储过程的区别（未经验证，从网上找的） 13\r\n\r\n3.4字符串连接 14\r\n四 函数和数据类型的区别 14\r\n\r\n4.1 Date 函数 14\r\n五性能比较 15\r\n一数据定义\r\n1 数据库操作基本命令\r\n\r\nMysql:\r\n\r\ncreate database name; 创建数据库\r\n\r\nuse databasename; 选择数据库\r\n\r\ndrop database name 直接删除数据库，不提醒 –\r\n\r\n2 CREATE TABLE --创建一个数据库表\r\nPRIMARY KEY 约束（主键）区别解析：\r\n2.1.1 创建primary key\r\n\r\nMysql:\r\n\r\nCREATE TABLE Persons\r\n\r\n(\r\n\r\nId_P int NOT NULL,\r\n\r\nLastName varchar(255) NOT NULL,\r\n\r\nFirstName varchar(255),\r\n\r\nAddress varchar(255),\r\n\r\nCity varchar(255),\r\n\r\nPRIMARY KEY (Id_P) //声明主健写在最后\r\n\r\n)\r\n\r\nSqlServer：\r\n\r\nCREATE TABLE Persons\r\n\r\n(\r\n\r\nId_P int NOT NULL PRIMARY KEY, //声明主健 紧跟列后\r\n\r\nLastName varchar(255) NOT NULL,\r\n\r\nFirstName varchar(255),\r\n\r\nAddress varchar(255),\r\n\r\nCity varchar(255)\r\n\r\n)\r\n\r\n但是如果表存在，之后给表加主健时：\r\n\r\nMysql 和SqlServer\r\n\r\nALTER TABLE Persons ADD PRIMARY KEY (Id_P)\r\n\r\n2.1.2撤销 PRIMARY KEY 约束\r\n\r\nMySQL:\r\n\r\nALTER TABLE Persons DROP PRIMARY KEY\r\n\r\nSQL Server\r\n\r\nALTER TABLE Persons DROP CONSTRAINT pk_PersonID\r\n\r\n2.1.3 创建外健约束\r\n\r\nMySQL:\r\n\r\nCREATE TABLE Orders\r\n\r\n(\r\n\r\nO_Id int NOT NULL,\r\n\r\nOrderNo int NOT NULL,\r\n\r\nId_P int,\r\n\r\nPRIMARY KEY (O_Id),\r\n\r\nFOREIGN KEY (Id_P) REFERENCES Persons(Id_P) //写在最后\r\n\r\n)\r\n\r\nSQL Server\r\n\r\nCREATE TABLE Orders\r\n\r\n(\r\n\r\nO_Id int NOT NULL PRIMARY KEY,\r\n\r\nOrderNo int NOT NULL,\r\n\r\nId_P int FOREIGN KEY REFERENCES Persons(Id_P) //顺序不同\r\n\r\n)\r\n\r\n如果在 \"Orders\" 表已存在的情况下为 \"Id_P\" 列创建 FOREIGN KEY 约束，请使用下面的 SQL：\r\n\r\nMySQL / SQL Server\r\n\r\nALTER TABLE Orders ADD FOREIGN KEY (Id_P) REFERENCES Persons(Id_P)\r\n\r\n2.1.4 撤销外健约束\r\n\r\nMySQL:\r\n\r\nALTER TABLE Orders DROP FOREIGN KEY f k_PerOrders\r\n\r\nSQL Server\r\n\r\nALTER TABLE Orders DROP CONSTRAINT fk_PerOrders\r\n\r\nUNIQUE 约束（唯一的，独一无二的）区别解析\r\n\r\nUNIQUE 约束唯一标识数据库表中的每条记录。\r\n\r\nUNIQUE 和 PRIMARY KEY 约束均为列或列集合提供了唯一性的保证。\r\n\r\nPRIMARY KEY 拥有自动定义的 UNIQUE 约束。\r\n\r\n请注意，每个表可以有多个 UNIQUE 约束，但是每个表只能有一个 PRIMARY KEY 约束。\r\n\r\n2.2.1 创建UNIQUE约束\r\n\r\nMySQL:\r\n\r\nCREATE TABLE Persons\r\n\r\n(\r\n\r\nId_P int NOT NULL,\r\n\r\nLastName varchar(255) NOT NULL,\r\n\r\nFirstName varchar(255),\r\n\r\nAddress varchar(255),\r\n\r\nCity varchar(255),\r\n\r\nUNIQUE (Id_P) //写在最后\r\n\r\n)\r\n\r\nSQL Server\r\n\r\nCREATE TABLE Persons\r\n\r\n(\r\n\r\nId_P int NOT NULL UNIQUE, //紧跟列后\r\n\r\nLastName varchar(255) NOT NULL,\r\n\r\nFirstName varchar(255),\r\n\r\nAddres\r\n', '0', '2017-09-08 09:49:09', null, 'Mysql和sqlServer命令比较.txt');
INSERT INTO `txt` VALUES ('601', '2', 'Newtonsoft.Json.dll 使用 .txt', 'Newtonsoft.Json.dll 使用  \r\n\r\n2010-05-07 16:53:53|  分类： Json |举报 |字号 订阅\r\n安装:\r\n1.解压下载文件,得到Newtonsoft.Json.dll\r\n2.在项目中添加引用..\r\n序列化和反序列在.net项目中:\r\n\r\nProduct product = new Product();\r\n\r\n \r\n\r\nproduct.Name = \"Apple\";\r\n\r\nproduct.Expiry = new DateTime(2008, 12, 28);\r\n\r\nproduct.Price = 3.99M;\r\n\r\nproduct.Sizes = new string[] { \"Small\", \"Medium\", \"Large\" };\r\n\r\n \r\n\r\nstring output = JavaScriptConvert.SerializeObject(product);\r\n\r\n//{\r\n\r\n//  \"Name\": \"Apple\",\r\n\r\n//  \"Expiry\": new Date(1230422400000),\r\n\r\n//  \"Price\": 3.99,\r\n\r\n//  \"Sizes\": [\r\n\r\n//     \"Small\",\r\n\r\n//     \"Medium\",\r\n\r\n//     \"Large\"\r\n\r\n//  ]\r\n\r\n//}\r\n\r\n \r\n\r\nProduct deserializedProduct = (Product)JavaScriptConvert.DeserializeObject(output, typeof(Product));\r\n\r\n\r\n读取JSON\r\n\r\nstring jsonText = \"[\'JSON!\',1,true,{property:\'value\'}]\";\r\n\r\n \r\n\r\nJsonReader reader = new JsonReader(new StringReader(jsonText));\r\n\r\n \r\n\r\nConsole.WriteLine(\"TokenType\\t\\tValueType\\t\\tValue\");\r\n\r\n \r\n\r\nwhile (reader.Read())\r\n\r\n{\r\n\r\n     Console.WriteLine(reader.TokenType + \"\\t\\t\" + WriteValue(reader.ValueType) + \"\\t\\t\" + WriteValue(reader.Value))\r\n\r\n}\r\n\r\n\r\n结果显示:\r\nTokenType 	ValueType 	Value\r\nStartArray 	null 	null\r\nString 	System.String 	JSON!\r\nInteger 	System.Int32 	1\r\nBoolean 	System.Boolean 	True\r\nStartObject 	null 	null\r\nPropertyName 	System.String 	property\r\nString 	System.String 	value\r\nEndObject 	null 	null\r\nEndArray 	null 	null\r\nJSON写入\r\n\r\nStringWriter sw = new StringWriter();\r\n\r\nJsonWriter writer = new JsonWriter(sw);\r\n\r\n \r\n\r\nwriter.WriteStartArray();\r\n\r\nwriter.WriteValue(\"JSON!\");\r\n\r\nwriter.WriteValue(1);\r\n\r\nwriter.WriteValue(true);\r\n\r\nwriter.WriteStartObject();\r\n\r\nwriter.WritePropertyName(\"property\");\r\n\r\nwriter.WriteValue(\"value\");\r\n\r\nwriter.WriteEndObject();\r\n\r\nwriter.WriteEndArray();\r\n\r\n \r\n\r\nwriter.Flush();\r\n\r\n \r\n\r\nstring jsonText = sw.GetStringBuilder().ToString();\r\n\r\n \r\n\r\nConsole.WriteLine(jsonText);\r\n\r\n// [\'JSON!\',1,true,{property:\'value\'}]\r\n\r\n\r\n这里会打印出: [\'JSON!\',1,true,{property:\'value\'}]. \r\n', '0', '2017-09-08 09:49:09', null, 'Newtonsoft.Json.dll 使用 .txt');
INSERT INTO `txt` VALUES ('602', '2', 'Newtonsoft.Json.反序列化.JsonConvert.DeserializeObject.txt', 'using Newtonsoft.Json;\r\nusing Newtonsoft.Json.Linq;\r\n\r\n\r\n/////////////简单的反序列化....Json串到对象     \r\n	 //定义一个JSON字符串   \r\n                string jsonText = \"[{\'a\':\'aaa\',\'b\':\'bbb\',\'c\':\'ccc\'},{\'a\':\'aaa2\',\'b\':\'bbb2\',\'c\':\'ccc2\'}]\";\r\n                //反序列化JSON字符串  \r\n                JArray ja = (JArray)JsonConvert.DeserializeObject(jsonText);\r\n                //将反序列化的JSON字符串转换成对象  \r\n                JObject o = (JObject)ja[1];\r\n                //读取对象中的各项值  \r\n                var oa = o[\"a\"];//取到的是aaa2\r\n                var ob = ja[1][\"a\"];//取到的是aaa2\r\n', '0', '2017-09-08 09:49:09', null, 'Newtonsoft.Json.反序列化.JsonConvert.DeserializeObject.txt');
INSERT INTO `txt` VALUES ('603', '2', 'Nginx.高性能的 HTTP 和 反向代理 服务器.txt', '\r\nnginx\r\n\r\n\r\n编辑\r\n \r\n\r\n\r\n\r\n\r\nNginx (\"engine x\") 是一个高性能的 HTTP 和 反向代理 服务器，也是一个 IMAP/POP3/SMTP 代理服务器。 Nginx 是由 Igor Sysoev 为俄罗斯访问量第二的 Rambler.ru 站点开发的，第一个公开版本0.1.0发布于2004年10月4日。其将源代码以类BSD许可证的形式发布，因它的稳定性、丰富的功能集、示例配置文件和低系统资源的消耗而闻名。2011年6月1日，nginx 1.0.4发布。\r\n\r\n\r\n\r\n目录\r\n\r\n\r\n1简介\r\n\r\n2优点\r\n▪ 服务器 ▪ 代码 ▪ 邮件代理服务器 \r\n3功能\r\n▪ 支持的操作系统 \r\n▪ 结构与扩展 ▪ 实验特性 ▪ HTTP基础功能 ▪ 其他HTTP功能 \r\n4安装\r\n▪ 模块依赖性 ▪ 预先编译好的安装包 ▪ 官方源代码下载 \r\n\r\n5使用技巧\r\n\r\n6Nginx反向代理实践\r\n\r\n7Nginx Rewrite\r\n\r\n8版本发布\r\n\r\n\r\n\r\n\r\n\r\n1\r\n\r\n\r\n\r\n简介\r\n\r\n\r\n编辑\r\n\r\nNginx（发音同 engine x）是一款轻量级的Web 服务器/反向代理服务器及电子邮件（IMAP/POP3）代理服务器，并在一个BSD-like 协议下发行。由俄罗斯的程序设计师Igor Sysoev所开发，供俄国大型的入口网站及搜索引擎Rambler（俄文：Рамблер）使用。其特点是占有内存少，并发能力强，事实上nginx的并发能力确实在同类型的网页服务器中表现较好，中国大陆使用nginx网站用户有：新浪、网易、 腾讯等。\r\n\r\n\r\n2\r\n\r\n\r\n\r\n优点\r\n\r\n\r\n编辑\r\n\r\n\r\n\r\n\r\nnginx map\r\nnginx map\r\nNginx 可以在大多数 Unix like OS 上编译运行，并有 Windows 移植版。 Nginx 的1.4.0稳定版已经于2013年4月24日发布，一般情况下，对于新建站点，建议使用最新稳定版作为生产版本，已有站点的升级急迫性不高。Nginx 的源代码使用 2-clause BSD-like license。\r\nNginx 是一个很强大的高性能Web和反向代理服务器，它具有很多非常优越的特性：\r\n\r\n在高连接并发的情况下，Nginx是Apache服务器不错的替代品：Nginx在美国是做虚拟主机生意的老板们经常选择的软件平台之一。能够支持高达 50,000 个并发连接数的响应，感谢Nginx为我们选择了 epoll and kqueue作为开发模型。\r\n\r\n\r\n\r\n服务器\r\nNginx作为负载均衡服务器：Nginx 既可以在内部直接支持 Rails 和 PHP 程序对外进行服务，也可以支持作为 HTTP代理服务器对外进行服务。Nginx采用C进行编写，不论是系统资源开销还是CPU使用效率都比 Perlbal 要好很多。\r\n\r\n\r\n\r\n代码\r\nNginx代码完全用C语言从头写成，已经移植到许多体系结构和操作系统，包括：Linux、FreeBSD、Solaris、Mac OS X、AIX以及Microsoft Windows。Nginx有自己的函数库，并且除了zlib、PCRE和OpenSSL之外，标准模块只使用系统C库函数。而且，如果不需要或者考虑到潜在的授权冲突，可以不使用这些第三方库。\r\n\r\n\r\n\r\n邮件代理服务器\r\n作为邮件代理服务器：Nginx 同时也是一个非常优秀的邮件代理服务器（最早开发这个产品的目的之一也是作为邮件代理服务器），Last. fm 描述了成功并且美妙的使用经验。\r\n\r\nNginx 是一个安装非常的简单，配置文件非常简洁（还能够支持perl语法），Bugs非常少的服务器：Nginx 启动特别容易，并且几乎可以做到7*24不间断运行，即使运行数个月也不需要重新启动。你还能够不间断服务的情况下进行软件版本的升级。\r\n\r\n\r\n3\r\n\r\n\r\n\r\n功能\r\n\r\n\r\n编辑\r\n\r\n\r\n\r\n支持的操作系统\r\nFreeBSD 3.x,4.x,5.x,6.x i386; FreeBSD 5.x,6.x amd64;\r\n\r\nLinux 2.2,2.4,2.6 i386; Linux 2.6 amd64;\r\n\r\nSolaris 8 i386; Solaris 9 i386 and sun4u; Solaris 10 i386;\r\n\r\nMacOS X （10.4） PPC;\r\n\r\nWindows XP，Windows Server 2003和Windows 7中。\r\n\r\n\r\n\r\n结构与扩展\r\n一个主进程和多个工作进程。工作进程是单线程的，且不需要特殊授权即可运行；\r\n\r\nkqueue (FreeBSD 4.1+),epoll (Linux 2.6+),rt signals (Linux 2.2.19+),/dev/poll (Solaris 7 11/99+),select，以及 poll 支持；\r\n\r\nkqueue支持的不同功能包括 EV_CLEAR,EV_DISABLE （临时禁止事件）， NOTE_LOWAT,EV_EOF，有效数据的数目，错误代码；\r\n\r\nsendfile (FreeBSD 3.1+),sendfile (Linux 2.2+),sendfile64 (Linux 2.4.21+），和 sendfilev (Solaris 8 7/01+) 支持；\r\n\r\n输入过滤 (FreeBSD 4.1+) 以及 TCP_DEFER_ACCEPT (Linux 2.4+) 支持；\r\n\r\n10,000 非活动的 HTTP keep-alive 连接仅需要 2.5M内存。\r\n\r\n最小化的数据拷贝操作；\r\n\r\n其他HTTP功能：\r\n\r\n基于IP 和名称的虚拟主机服务；\r\n\r\nMemcached 的 GET 接口；\r\n\r\n支持 keep-alive 和管道连接；\r\n\r\n灵活简单的配置；\r\n\r\n重新配置和在线升级而无须中断客户的工作进程；\r\n\r\n可定制的访问日志，日志写入缓存，以及快捷的日志回卷；\r\n\r\n4xx-5xx错误代码重定向；\r\n\r\n基于 PCRE 的 rewrite 重写模块；\r\n\r\n基于客户端IP 地址和 HTTP 基本认证的访问控制；\r\n\r\nPUT,DELETE，和 MKCOL 方法；\r\n\r\n支持 FLV （Flash 视频）；\r\n\r\n带宽限制。\r\n\r\n\r\n\r\n实验特性\r\n内嵌的 perl；\r\n\r\n通过 aio_read()/aio_write() 的套接字工作的实验模块，仅在 FreeBSD 下；\r\n\r\n对线程的实验化支持，FreeBSD 4.x 的实现基于 rfork()；\r\n\r\nNginx 主要的英语站点是 http://sysoev. ru/en/；\r\n\r\n英语文档草稿由 Aleksandar Lazic 完成 点击。\r\n\r\n\r\n\r\nHTTP基础功能\r\n处理静态文件，索引文件以及自动索引；\r\n\r\n反向代理加速（无缓存），简单的负载均衡和容错；\r\n\r\nFastCGI，简单的负载均衡和容错；\r\n\r\n模块化的结构。过滤器包括gzipping,byte ranges,chunked responses，以及 SSI-filter。在SSI过滤器中，到同一个 proxy 或者 FastCGI 的多个子请求并发处理；\r\n\r\nSSL 和 TLS SNI 支持；\r\n\r\nIMAP/POP3代理服务功能：\r\n\r\n使用外部 HTTP 认证服务器重定向用户到 IMAP/POP3 后端；\r\n\r\n使用外部 HTTP 认证服务器认证用户后连接重定向到内部的 SMTP 后端；\r\n\r\n\r\n\r\n其他HTTP功能\r\n基于名称和基于IP的虚拟服务器；\r\n\r\nKeep-alive and pipelined connections support；保持活动和支持管线连接；\r\n\r\nFlexible configuration；灵活的配置；\r\n\r\nReconfiguration and online upgrade without interruption of the client processing；重载配置，无间断程序升级；\r\n\r\nAccess log formats,bufferred log writing,and quick log rotation；访问日志格式，bufferred日志写，快速登录旋转；\r\n\r\n3xx-5xx error codes redirection; 3xx的- 5xx错误代码重定向；\r\n\r\nThe rewrite module；重写模块;\r\n\r\nAccess control based on client IP address and HTTP Basic authentication；基于客户端IP地址访问控制和HTTP基本认证；\r\n\r\nThe PUT,DELETE,MKCOL,COPY and MOVE methods; 提交，删除，MKCOL，复制和移动方法；\r\n\r\nFLV streaming;FLV视频流;\r\n\r\nSpeed limitation；速度限制；\r\n\r\nLimitation of simultaneous connections or requests from one address.限制同个IP地址请求数量。\r\n\r\nEmbedded perl.嵌入式的Perl。\r\n\r\n邮件代理服务器功能\r\n\r\n用户重定向到IMAP/POP3后端使用外部HTTP认证服务器；\r\n\r\nUser authentication using an external HTTP authentication server and connection redirection to internal SMTP backend；用户身份验证使用外部HTTP认证服务器和连接重定向到内部的SMTP后端；\r\n\r\nAuthentication methods：验证方法：\r\n\r\nPOP3: USER/PASS,APOP,AUTH LOGIN/PLAIN/CRAM-MD5；的POP3：用户名/密码，的APOP，AUTH的LOGIN/PLAIN/CRAM-MD5;\r\n\r\nIMAP: LOGIN,AUTH LOGIN/PLAIN/CRAM-MD5; IMAP的：登录，AUTH的LOGIN/PLAIN/CRAM-MD5;\r\n\r\nSMTP: AUTH LOGIN/PLAIN/CRAM-MD5；的SMTP：AUTH的LOGIN/PLAIN/CRAM-MD5;\r\n\r\nSSL support; SSL支持；\r\n\r\nSTARTTLS and STLS support. STARTTLS的和补充的支持。\r\n\r\n认证方法\r\n\r\nPOP3: POP3 USER/PASS,APOP,AUTH LOGIN PLAIN CRAM-MD5;\r\n\r\nIMAP: IMAP LOGIN;\r\n\r\nSMTP: AUTH LOGIN PLAIN CRAM-MD5;\r\n\r\nSSL 支持；\r\n\r\n在 IMAP 和 POP3 模式下的 STARTTLS 和 STLS 支持。\r\n\r\n\r\n4\r\n\r\n\r\n\r\n安装\r\n\r\n\r\n编辑\r\n\r\n\r\n\r\n模块依赖性\r\ngzip模块需要 zlib 库\r\n\r\nrewrite模块需要 pcre 库\r\n\r\nssl 功能需要openssl库\r\n\r\n\r\n\r\n预先编译好的安装包\r\nNginx在一些Linux发行版和BSD的各个变种版本的安装包仓库中都会有，通过各个系统自带的软件包管理方法即可安装。需要注意的是，很多预先编译好的安装包都比较陈旧，大多数情况下还是推荐直接从源码编译。\r\n\r\n\r\n\r\n官方源代码下载\r\n特定平台的安装和记录\r\n\r\nNginx 在 Slackware 上的编译安装脚本\r\n\r\nNginx 在ubuntu和debian上的安装及脚本,debian针对nginx包的官方下载站点\r\n\r\n使用源代码进行构建\r\n\r\nNginx 使用 Unix 下常用的 \'./configure && make && make install\' 过程来编译安装。\r\n\r\nconfigure脚本确定系统所具有一些特性，特别是 nginx 用来处理连接的方法。然后，它创建 Makefile 文件。\r\n\r\nconfigure 支持下面的选项：\r\n\r\n--prefix=<path> - Nginx安装路径。如果没有指定，默认为 /usr/local/nginx。\r\n\r\n--sbin-path=<path> - Nginx可执行文件安装路径。只能安装时指定，如果没有指定，默认为<prefix>/sbin/nginx。\r\n\r\n--conf-path=<path> - 在没有给定-c选项下默认的nginx.conf的路径。如果没有指定，默认为<prefix>/conf/nginx.conf。\r\n\r\n--pid-path=<path> - 在nginx.conf中没有指定pid指令的情况下，默认的nginx.pid的路径。如果没有指定，默认为 <prefix>/logs/nginx.pid。\r\n\r\n--lock-path=<path> - nginx.lock文件的路径。\r\n\r\n--error-log-path=<path> - 在nginx.conf中没有指定error_log指令的情况下，默认的错误日志的路径。如果没有指定，默认为 <prefix>/logs/error.log。\r\n\r\n--http-log-path=<path> - 在nginx.conf中没有指定access_log指令的情况下，默认的访问日志的路径。如果没有指定，默认为 <prefix>/logs/access.log。\r\n\r\n--user=<user> - 在nginx.conf中没有指定user指令的情况下，默认的nginx使用的用户。如果没有指定，默认为 nobody。\r\n\r\n--group=<group> - 在nginx.conf中没有指定user指令的情况下，默认的nginx使用的组。如果没有指定，默认为 nobody。\r\n\r\n--builddir=DIR - 指定编译的目录\r\n\r\n--with-rtsig_module - 启用 rtsig模块\r\n\r\n--with-select_module --without-select_module - Whether or not to enable the select module. This module is enabled by default if a more suitable method such as kqueue,epoll,rtsig or /dev/poll is not discovered by configure.\r\n\r\n//允许或不允许开启SELECT模式，如果 configure 没有找到更合适的模式，比如：kqueue(sun os),epoll (linux kenel 2.6+),rtsig（实时信号）或者/dev/poll（一种类似select的模式，底层实现与SELECT基本相 同，都是采用轮训方法） SELECT模式将是默认安装模式\r\n\r\n--with-poll_module --without-poll_module - Whether or not to enable the poll module. This module is enabled by default if a more suitable method such as kqueue,epoll,rtsig or /dev/poll is not discovered by configure.\r\n\r\n--with-http_ssl_module - Enable ngx_http_ssl_module. Enables SSL support and the ability to handle HTTPS requests. Requires OpenSSL. On Debian,this is libssl-dev.\r\n\r\n//开启HTTP SSL模块，使NGINX可以支持HTTPS请求。这个模块需要已经安装了OPENSSL，在DEBIAN上是libssl\r\n\r\n--with-http_realip_module - 启用 ngx_http_realip_module\r\n\r\n--with-http_addition_module - 启用 ngx_http_addition_module\r\n\r\n--with-http_sub_module - 启用 ngx_http_sub_module\r\n\r\n--with-http_dav_module - 启用 ngx_http_dav_module\r\n\r\n--with-http_flv_module - 启用 ngx_http_flv_module\r\n\r\n--with-http_stub_status_module - 启用 \"server status\" 页\r\n\r\n--without-http_charset_module - 禁用 ngx_http_charset_module\r\n\r\n--without-http_gzip_module - 禁用 ngx_http_gzip_module. 如果启用，需要 zlib。\r\n\r\n--without-http_ssi_module - 禁用 ngx_http_ssi_module\r\n\r\n--without-http_userid_module - 禁用 ngx_http_userid_module\r\n\r\n--without-http_access_module - 禁用 ngx_http_access_module\r\n\r\n--without-http_auth_basic_module - 禁用 ngx_http_auth_basic_module\r\n\r\n--without-http_autoindex_module - 禁用 ngx_http_autoindex_module\r\n\r\n--without-http_geo_module - 禁用 ngx_http_geo_module\r\n\r\n--without-http_map_module - 禁用 ngx_http_map_module\r\n\r\n--without-http_referer_module - 禁用 ngx_http_referer_module\r\n\r\n--without-http_rewrite_module - 禁用 ngx_http_rewrite_module. 如果启用需要 PCRE。\r\n\r\n--without-http_proxy_module - 禁用 ngx_http_proxy_module\r\n\r\n--without-http_fastcgi_module - 禁用 ngx_http_fastcgi_module\r\n\r\n--without-http_memcached_module - 禁用 ngx_http_memcached_module\r\n\r\n--without-http_limit_zone_module - 禁用 ngx_http_limit_zone_module\r\n\r\n--without-http_empty_gif_module - 禁用 ngx_http_empty_gif_module\r\n\r\n--without-http_browser_module - 禁用 ngx_http_browser_module\r\n\r\n--without-http_upstream_ip_hash_module - 禁用 ngx_http_upstream_ip_hash_module\r\n\r\n--with-http_perl_module - 启用 ngx_http_perl_module\r\n\r\n--with-perl_modules_path=PATH - 指定 perl模块的路径\r\n\r\n--with-perl=PATH - 指定 perl 执行文件的路径\r\n\r\n--http-log-path=PATH - Set path to the http access log\r\n\r\n--http-client-body-temp-path=PATH - Set path to the http client request body temporary files\r\n\r\n--http-proxy-temp-path=PATH - Set path to the http proxy temporary files\r\n\r\n--http-fastcgi-temp-path=PATH - Set path to the http fastcgi temporary files\r\n\r\n--without-http - 禁用 HTTP server\r\n\r\n--with-mail - 启用 IMAP4/POP3/SMTP 代理模块\r\n\r\n--with-mail_ssl_module - 启用 ngx_mail_ssl_module\r\n\r\n--with-cc=PATH - 指定 C编译器的路径\r\n\r\n--with-cpp=PATH - 指定 C预处理器的路径\r\n\r\n--with-cc-opt=OPTIONS - Additional parameters which will be added to the variable CFLAGS. With the use of the system library PCRE in FreeBSD,it is necessary to indicate --with-cc-opt=\"-I /usr/local/include\". If we are using select() and it is necessary to increase the number of file descriptors,then this also can be assigned here: --with-cc-opt=\"-D FD_SETSIZE=2048\".\r\n\r\n--with-ld-opt=OPTIONS - Additional parameters passed to the linker. With the use of the system library PCRE in FreeBSD,it is necessary to indicate --with-ld-opt=\"-L /usr/local/lib\".\r\n\r\n--with-cpu-opt=CPU - 为特定的 CPU 编译，有效的值包括：pentium,pentiumpro,pentium3,pentium4,athlon,opteron,amd64,sparc32,sparc64,ppc64\r\n\r\n--without-pcre - 禁止 PCRE 库的使用。同时也会禁止 HTTP rewrite 模块。在 \"location\" 配置指令中的正则表达式也需要 PCRE。\r\n\r\n--with-pcre=DIR - 指定 PCRE 库的源代码的路径。\r\n\r\n--with-pcre-opt=OPTIONS - Set additional options for PCRE building.\r\n\r\n--with-md5=DIR - Set path to md5 library sources.\r\n\r\n--with-md5-opt=OPTIONS - Set additional options for md5 building.\r\n\r\n--with-md5-asm - Use md5 assembler sources.\r\n\r\n--with-sha1=DIR - Set path to sha1 library sources.\r\n\r\n--with-sha1-opt=OPTIONS - Set additional options for sha1 building.\r\n\r\n--with-sha1-asm - Use sha1 assembler sources.\r\n\r\n--with-zlib=DIR - Set path to zlib library sources.\r\n\r\n--with-zlib-opt=OPTIONS - Set additional options for zlib building.\r\n\r\n--with-zlib-asm=CPU - Use zlib assembler sources optimized for specified CPU,valid values are: pentium,pentiumpro\r\n\r\n--with-openssl=DIR - Set path to OpenSSL library sources\r\n\r\n--with-openssl-opt=OPTIONS - Set additional options for OpenSSL building\r\n\r\n--with-debug - 启用调试日志\r\n\r\n--add-module=PATH - Add in a third-party module found in directory PATH\r\n\r\n在不同版本间，选项可能会有些许变化，请总是使用 ./configure --help 命令来检查一下当前的选项列表。\r\n\r\n\r\n5\r\n\r\n\r\n\r\n使用技巧\r\n\r\n\r\n编辑\r\n\r\n对于chroot的支持是否在计划之中\r\n\r\n在什么情况下使用Nginx比使用squid要好? 反之亦然。\r\n\r\n大体上来说nginx主要用于反向加速代理而不是像squid那样做为常规代理服务器。Nginx的最大优势在于高负载情况下内存和CPU的低消耗。我不认为squid能给你带来比nginx更好的性能。\r\n\r\n有没有人能给出一个完整的.conf配置文件来详细的解读一下怎么配置和测试 IMAP模块， 而不只是关于 IMAP 的只言片语啊?\r\n\r\n依照 [NginxImapProxyExample] 开始你的配置. 关于不同配置参数的具体信息，请查看 [NginxMailCoreModule] 页。\r\n\r\n示例1: 用运行于apache上的php脚本做后端验证\r\n\r\n示例2: 使用运行于同一个服务器的 nginx-embedded-perl模块作为 imap/pop代理和认证后端\r\n\r\n某些东东不工作\r\n\r\n（URL重写，代理，路径，...)\r\n\r\n例如：如URL重写（rewrite）不工作了或者是unix的路径（/$PATH）的问题云云...\r\n\r\n请仔细阅读 [NginxDebugging] 并且 逐行 查看错误日志。\r\n\r\n如果你没找到错误 打起精神 试着到IRC或邮件列表里说明一下你碰到的问题。\r\n\r\n有没有其它类似的Web服务器\r\n\r\nCherokee\r\n\r\nLighttpd (Lighty)\r\n\r\nthttpd\r\n\r\n关于各自的优缺点请使用自己喜欢的搜索引擎查找\r\n\r\n让Nginx成为以postfix做为后端的SMTP代理\r\n\r\nNginx使用什么算法来实现负载均衡它能实现基于连接数的负载均衡吗?\r\n\r\nNginx使用简单的轮巡算法，所以无法做基本链接计数的负载均衡。这个可能会在将来的版本中有所改变。\r\n\r\n我能关闭从代理服务器到后端服务器的缓存吗或者使用上传进度特性?\r\n\r\n\r\n6\r\n\r\n\r\n\r\nNginx反向代理实践\r\n\r\n\r\n编辑\r\n\r\n. nginx + substitutions 安装\r\n　　nginx 自带一个Substitution模块，但该模块只能写一行，所以我们改用 substitutions\r\n　　下面是安装一些预备软件\r\n\r\nyum -y --noplugins install wget zip\r\n\r\n\r\nyum -y --noplugins install unzip\r\n\r\n\r\nyum -y --noplugins install gcc\r\n\r\n\r\nyum -y --noplugins install make\r\n\r\n\r\nyum -y --noplugins install pcre-devel\r\n\r\n\r\nyum -y --noplugins install openssl-devel\r\n\r\n\r\n编译软件\r\n\r\ntar zxf nginx-1.0.8.tar.gz\r\n\r\n\r\ncd nginx-1.0.8.tar.gz\r\n\r\n\r\n./configure ./configure --add-module=path/substitutions4nginx-read-only //注意这里的path是相对应的真实路径\r\n\r\n\r\nmake\r\n\r\n\r\nmake install\r\n\r\n\r\n配置 nginx.conf\r\n此时，nginx应该安装在于 /usr/local/nginx 下面\r\n\r\n\r\nserver_name servername;\r\n\r\n\r\nlocation / {\r\n\r\n\r\nsubs_filter ca-pub-********** ca-pub-**********; //把google ad 的用户号 ca-pub-9805743306566114 改成你自己的，比如 ca-pub-**********\r\n\r\n\r\nsubs_filter ********** **********; //把google ad 的广告号 **********改成你自己的，比如 **********，你懂的！：D\r\n\r\n\r\nproxy_pass 这里是填写需要代理的网址; //反向代理站。\r\n\r\n\r\nindex index.html index.htm;\r\n\r\n\r\n}\r\n\r\n\r\n记住subs_filter 命令的格式即可随意发挥想象力替换你想替换的广告、超链接，等等。\r\n\r\n\r\n　　\r\n\r\n基于太多人询问下面的问题：\r\n\r\n我能为了得到上传进度而关闭代理的缓存吗\r\n\r\n使用nginx我怎么才能给用户显示上传进度\r\n\r\n到目前为止 （2007-Apr-26） 还没有办法关闭到后端服务器的缓存.\r\n\r\n\r\n7\r\n\r\n\r\n\r\nNginx Rewrite\r\n\r\n\r\n编辑\r\n\r\n[1]1.Nginx Rewrite 基本标记(flags)复制内容到剪贴板代码:last – 基本上都用这个Flag。\r\n\r\nbreak – 中止Rewirte，不在继续匹配\r\n\r\nredirect – 返回临时重定向的HTTP状态302\r\n\r\npermanent – 返回永久重定向的HTTP状态301\r\n\r\n2. 正则表达式匹配，其中：代码:\r\n\r\nCODE:\r\n\r\n* ~ 为区分大小写匹配\r\n\r\n* ~* 为不区分大小写匹配\r\n\r\n* !~和!~*分别为区分大小写不匹配及不区分大小写不匹配\r\n\r\n3. 文件及目录匹配，其中：代码:\r\n\r\nCODE:\r\n\r\n* -f和!-f用来判断是否存在文件\r\n\r\n* -d和!-d用来判断是否存在目录\r\n\r\n* -e和!-e用来判断是否存在文件或目录\r\n\r\n* -x和!-x用来判断文件是否可执行\r\n\r\n4.Nginx 的一些可用的全局变量，可用做条件判断：\r\n\r\n代码:\r\n\r\nCODE:\r\n\r\n$args\r\n\r\n$content_length\r\n\r\n$content_type\r\n\r\n$document_root\r\n\r\n$document_uri\r\n\r\n$host\r\n\r\n$http_user_agent\r\n\r\n$http_cookie\r\n\r\n$limit_rate\r\n\r\n$request_body_file\r\n\r\n$request_method\r\n\r\n$remote_addr\r\n\r\n$remote_port\r\n\r\n$remote_user\r\n\r\n$request_filename\r\n\r\n$request_uri\r\n\r\n$query_string\r\n\r\n$scheme\r\n\r\n$server_protocol\r\n\r\n$server_addr\r\n\r\n$server_name\r\n\r\n$server_port\r\n\r\n$uri\r\n\r\n\r\n8\r\n\r\n\r\n\r\n版本发布\r\n\r\n\r\n编辑\r\n\r\n2012年08月22日，Nginx 1.3.5 开发版发布。\r\n\r\n2012年09月12日，Nginx 1.3.6 开发版发布。\r\n\r\n2012年12月11日，Nginx 1.2.6 稳定版发布\r\n\r\n2013年02月12日，Nginx 1.2.7 稳定版发布\r\n\r\n2013年02月19日，Nginx 1.3.13 开发版发布\r\n\r\n2013年04月03日，Nginx 1.2.8 稳定版发布。\r\n\r\n2013年04月16日，Nginx 1.3.16 开发者版本发布。\r\n\r\n2013年04月24日，Nginx 1.4.0 稳定版发布。\r\n\r\n2013年05月07日，Nginx 1.4.1 稳定版发布。\r\n\r\n2013年05月07日，Nginx 1.5.0 开发版本发布。\r\n\r\n2013年06月04日，Nginx 1.5.1 主版本发布。\r\n\r\n2013年07月02日，Nginx 1.5.2 开发版发布。\r\n\r\n2013年07月17日，Nginx 1.4.2 稳定版发布。\r\n\r\n2013年07月30日，Nginx 1.5.3 开发版本发布。\r\n\r\n2013年08月27日，Nginx 1.5.4 开发版本发布。\r\n\r\n2013年09月17日，Nginx 1.5.5 开发版本发布。[1]\r\n\r\n\r\n\r\n参考资料\r\n1．  Nginx 常见应用技术指南(Nginx Tips)  ．冯馨怡的博客 [引用日期2013-07-3] ．\r\n\r\n', '0', '2017-09-08 09:49:09', null, 'Nginx.高性能的 HTTP 和 反向代理 服务器.txt');
