           object obj = GetSingle(strSql, cmdParms);\r\n            int cmdresult;\r\n            if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n            {\r\n                cmdresult = 0;\r\n            }\r\n            else\r\n            {\r\n                cmdresult = int.Parse(obj.ToString());\r\n            }\r\n            if (cmdresult == 0)\r\n            {\r\n                return false;\r\n            }\r\n            else\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region  ִ�м�SQL���\r\n\r\n        /// <summary>\r\n        /// ִ��SQL��䣬����Ӱ��ļ�¼��\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL���</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSql(string SQLString)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                using (OleDbCommand cmd = new OleDbCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.OleDb.OleDbException E)\r\n                    {\r\n                        connection.Close();\r\n                        throw new Exception(E.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�ж���SQL���ACCESSS��ʹ��\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">����SQL���</param>		\r\n        public static void ExecuteMulti_Sql(ArrayList SQLStringList)\r\n        {\r\n            using (OleDbConnection conn = new OleDbConnection(connectionString))\r\n            {\r\n                conn.Open();\r\n                OleDbCommand cmd = new OleDbCommand();\r\n                cmd.Connection = conn;\r\n\r\n                try\r\n                {\r\n                    for (int n = 0; n < SQLStringList.Count; n++)\r\n                    {\r\n                        string strsql = SQLStringList[n].ToString();\r\n                        if (strsql.Trim().Length > 1)\r\n                        {\r\n                            cmd.CommandText = strsql;\r\n                            cmd.ExecuteNonQuery();\r\n                        }\r\n                    }\r\n                }\r\n                catch (System.Data.OleDb.OleDbException E)\r\n                {\r\n                    conn.Close();\r\n                    throw new Exception(E.Message);\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�ж���SQL��䣬ʵ�����ݿ�����\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">����SQL���</param>		\r\n        public static void ExecuteSqlTran(ArrayList SQLStringList)\r\n        {\r\n            using (OleDbConnection conn = new OleDbConnection(connectionString))\r\n            {\r\n                conn.Open();\r\n                OleDbCommand cmd = new OleDbCommand();\r\n                cmd.Connection = conn;\r\n                OleDbTransaction tx = conn.BeginTransaction();\r\n                cmd.Transaction = tx;\r\n                try\r\n                {\r\n                    for (int n = 0; n < SQLStringList.Count; n++)\r\n                    {\r\n                        string strsql = SQLStringList[n].ToString();\r\n                        if (strsql.Trim().Length > 1)\r\n                        {\r\n                            cmd.CommandText = strsql;\r\n                            cmd.ExecuteNonQuery();\r\n                        }\r\n                    }\r\n                    tx.Commit();\r\n                }\r\n                catch (System.Data.OleDb.OleDbException E)\r\n                {\r\n                    tx.Rollback();\r\n                    throw new Exception(E.Message);\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�д�һ���洢���̲����ĵ�SQL��䡣\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL���</param>\r\n        /// <param name=\"content\">��������,����һ���ֶ��Ǹ�ʽ���ӵ����£���������ţ�����ͨ�������ʽ���</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSql(string SQLString, string content)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                OleDbCommand cmd = new OleDbCommand(SQLString, connection);\r\n                System.Data.OleDb.OleDbParameter myParameter = new System.Data.OleDb.OleDbParameter(\"@content\", OleDbType.VarChar);\r\n                myParameter.Value = content;\r\n                cmd.Parameters.Add(myParameter);\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    int rows = cmd.ExecuteNonQuery();\r\n                    return rows;\r\n                }\r\n                catch (System.Data.OleDb.OleDbException E)\r\n                {\r\n                    throw new Exception(E.Message);\r\n                }\r\n                finally\r\n                {\r\n                    cmd.Dispose();\r\n                    connection.Close();\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// �����ݿ������ͼ���ʽ���ֶ�(������������Ƶ���һ��ʵ��)\r\n        /// </summary>\r\n        /// <param name=\"strSQL\">SQL���</param>\r\n        /// <param name=\"fs\">ͼ���ֽ�,���ݿ���ֶ�����Ϊimage�����</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSqlInsertImg(string strSQL, byte[] fs)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                OleDbCommand cmd = new OleDbCommand(strSQL, connection);\r\n                System.Data.OleDb.OleDbParameter myParameter = new System.Data.OleDb.OleDbParameter(\"@fs\", OleDbType.Binary);\r\n                myParameter.Value = fs;\r\n                cmd.Parameters.Add(myParameter);\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    int rows = cmd.ExecuteNonQuery();\r\n                    return rows;\r\n                }\r\n                catch (System.Data.OleDb.OleDbException E)\r\n                {\r\n                    throw new Exception(E.Message);\r\n                }\r\n                finally\r\n                {\r\n                    cmd.Dispose();\r\n                    connection.Close();\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ��һ�������ѯ�����䣬���ز�ѯ�����object����\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">�����ѯ������</param>\r\n        /// <returns>��ѯ�����object��</returns>\r\n        public static object GetSingle(string SQLString)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                using (OleDbCommand cmd = new OleDbCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        object obj = cmd.ExecuteScalar();\r\n                        if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                        {\r\n                            return null;\r\n                        }\r\n                        else\r\n                        {\r\n                            return obj;\r\n                        }\r\n                    }\r\n                    catch (System.Data.OleDb.OleDbException e)\r\n                    {\r\n                        connection.Close();\r\n                        throw new Exception(e.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�в�ѯ��䣬����OleDbDataReader\r\n        /// </summary>\r\n        /// <param name=\"strSQL\">��ѯ���</param>\r\n        /// <returns>OleDbDataReader</returns>\r\n        public static OleDbDataReader ExecuteReader(string strSQL)\r\n        {\r\n            OleDbConnection connection = new OleDbConnection(connectionString);\r\n            OleDbCommand cmd = new OleDbCommand(strSQL, connection);\r\n            try\r\n            {\r\n                connection.Open();\r\n                OleDbDataReader myReader = cmd.ExecuteReader();\r\n                return myReader;\r\n            }\r\n            catch (System.Data.OleDb.OleDbException e)\r\n            {\r\n                throw new Exception(e.Message);\r\n            }\r\n\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�в�ѯ��䣬����DataSet\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">��ѯ���</param>\r\n        /// <returns>DataSet</returns>\r\n        public static DataSet Query(string SQLString)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                DataSet ds = new DataSet();\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    OleDbDataAdapter command = new OleDbDataAdapter(SQLString, connection);\r\n                    command.Fill(ds, \"ds\");\r\n                }\r\n                catch (System.Data.OleDb.OleDbException ex)\r\n                {\r\n                    throw new Exception(ex.Message);\r\n                }\r\n                return ds;\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region ִ�д�������SQL���\r\n\r\n        /// <summary>\r\n        /// ִ��SQL��䣬����Ӱ��ļ�¼��\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL���</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSql(string SQLString, params OleDbParameter[] cmdParms)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                using (OleDbCommand cmd = new OleDbCommand())\r\n                {\r\n                    try\r\n                    {\r\n                        PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        cmd.Parameters.Clear();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.OleDb.OleDbException E)\r\n                    {\r\n                        throw new Exception(E.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�ж���SQL��䣬ʵ�����ݿ�����\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">SQL���Ĺ�ϣ���keyΪsql��䣬value�Ǹ�����OleDbParameter[]��</param>\r\n        public static void ExecuteSqlTran(Hashtable SQLStringList)\r\n        {\r\n            using (OleDbConnection conn = new OleDbConnection(connectionString))\r\n            {\r\n                conn.Open();\r\n                using (OleDbTransaction trans = conn.BeginTransaction())\r\n                {\r\n                    OleDbCommand cmd = new OleDbCommand();\r\n                    try\r\n                    {\r\n                        //ѭ��\r\n                        foreach (DictionaryEntry myDE in SQLStringList)\r\n                        {\r\n                            string cmdText = myDE.Key.ToString();\r\n                            OleDbParameter[] cmdParms = (OleDbParameter[])myDE.Value;\r\n                            PrepareCommand(cmd, conn, trans, cmdText, cmdParms);\r\n                            int val = cmd.ExecuteNonQuery();\r\n                            cmd.Parameters.Clear();\r\n\r\n                            trans.Commit();\r\n                        }\r\n                    }\r\n                    catch\r\n                    {\r\n                        trans.Rollback();\r\n                        throw;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ��һ�������ѯ�����䣬���ز�ѯ�����object����\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">�����ѯ������</param>\r\n        /// <returns>��ѯ�����object��</returns>\r\n        public static object GetSingle(string SQLString, params OleDbParameter[] cmdParms)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                using (OleDbCommand cmd = new OleDbCommand())\r\n                {\r\n                    try\r\n                    {\r\n                        PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                        object obj = cmd.ExecuteScalar();\r\n                        cmd.Parameters.Clear();\r\n                        if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                        {\r\n                            return null;\r\n                        }\r\n                        else\r\n                        {\r\n                            return obj;\r\n                        }\r\n                    }\r\n                    catch (System.Data.OleDb.OleDbException e)\r\n                    {\r\n                        throw new Exception(e.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�в�ѯ��䣬����OleDbDataReader\r\n        /// </summary>\r\n        /// <param name=\"strSQL\">��ѯ���</param>\r\n        /// <returns>OleDbDataReader</returns>\r\n        public static OleDbDataReader ExecuteReader(string SQLString, params OleDbParameter[] cmdParms)\r\n        {\r\n            OleDbConnection connection = new OleDbConnection(connectionString);\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            try\r\n            {\r\n                PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                OleDbDataReader myReader = cmd.ExecuteReader();\r\n                cmd.Parameters.Clear();\r\n                return myReader;\r\n            }\r\n            catch (System.Data.OleDb.OleDbException e)\r\n            {\r\n                throw new Exception(e.Message);\r\n            }\r\n\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�в�ѯ��䣬����DataSet\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">��ѯ���</param>\r\n        /// <returns>DataSet</returns>\r\n        public static DataSet Query(string SQLString, params OleDbParameter[] cmdParms)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                OleDbCommand cmd = new OleDbCommand();\r\n                PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                using (OleDbDataAdapter da = new OleDbDataAdapter(cmd))\r\n                {\r\n                    DataSet ds = new DataSet();\r\n                    try\r\n                    {\r\n                        da.Fill(ds, \"ds\");\r\n                        cmd.Parameters.Clear();\r\n                    }\r\n                    catch (System.Data.OleDb.OleDbException ex)\r\n                    {\r\n                        throw new Exception(ex.Message);\r\n                    }\r\n                    return ds;\r\n                }\r\n            }\r\n        }\r\n\r\n        private static void PrepareCommand(OleDbCommand cmd, OleDbConnection conn, OleDbTransaction trans, string cmdText, OleDbParameter[] cmdParms)\r\n        {\r\n            if (conn.State != ConnectionState.Open)\r\n                conn.Open();\r\n            cmd.Connection = conn;\r\n            cmd.CommandText = cmdText;\r\n            if (trans != null)\r\n                cmd.Transaction = trans;\r\n            cmd.CommandType = CommandType.Text;//cmdType;\r\n            if (cmdParms != null)\r\n            {\r\n                foreach (OleDbParameter parm in cmdParms)\r\n                    cmd.Parameters.Add(parm);\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region �洢���̲���\r\n\r\n        /// <summary>\r\n        /// ִ�д洢����\r\n        /// </summary>\r\n        /// <param name=\"storedProcName\">�洢������</param>\r\n        /// <param name=\"parameters\">�洢���̲���</param>\r\n        /// <returns>OleDbDataReader</returns>\r\n        public static OleDbDataReader RunProcedure(string storedProcName, IDataParameter[] parameters)\r\n        {\r\n            OleDbConnection connection = new OleDbConnection(connectionString);\r\n            OleDbDataReader returnReader;\r\n            connection.Open();\r\n            OleDbCommand command = BuildQueryCommand(connection, storedProcName, parameters);\r\n            command.CommandType = CommandType.StoredProcedure;\r\n            returnReader = command.ExecuteReader();\r\n            return returnReader;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�д洢����\r\n        /// </summary>\r\n        /// <param name=\"storedProcName\">�洢������</param>\r\n        /// <param name=\"parameters\">�洢���̲���</param>\r\n        /// <param name=\"tableName\">DataSet����еı���</param>\r\n        /// <returns>DataSet</returns>\r\n        public static DataSet RunProcedure(string storedProcName, IDataParameter[] parameters, string tableName)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                DataSet dataSet = new DataSet();\r\n                connection.Open();\r\n                OleDbDataAdapter sqlDA = new OleDbDataAdapter();\r\n                sqlDA.SelectCommand = BuildQueryCommand(connection, storedProcName, parameters);\r\n                sqlDA.Fill(dataSet, tableName);\r\n                connection.Close();\r\n                return dataSet;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ���� OleDbCommand ����(��������һ���������������һ������ֵ)\r\n        /// </summary>\r\n        /// <param name=\"connection\">���ݿ�����</param>\r\n        /// <param name=\"storedProcName\">�洢������</param>\r\n        /// <param name=\"parameters\">�洢���̲���</param>\r\n        /// <returns>OleDbCommand</returns>\r\n        private static OleDbCommand BuildQueryCommand(OleDbConnection connection, string storedProcName, IDataParameter[] parameters)\r\n        {\r\n            OleDbCommand command = new OleDbCommand(storedProcName, connection);\r\n            command.CommandType = CommandType.StoredProcedure;\r\n            foreach (OleDbParameter parameter in parameters)\r\n            {\r\n                command.Parameters.Add(parameter);\r\n            }\r\n            return command;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�д洢���̣�����Ӱ�������		\r\n        /// </summary>\r\n        /// <param name=\"storedProcName\">�洢������</param>\r\n        /// <param name=\"parameters\">�洢���̲���</param>\r\n        /// <param name=\"rowsAffected\">Ӱ�������</param>\r\n        /// <returns></returns>\r\n        public static int RunProcedure(string storedProcName, IDataParameter[] parameters, out int rowsAffected)\r\n        {\r\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\r\n            {\r\n                int result;\r\n                connection.Open();\r\n                OleDbCommand command = BuildIntCommand(connection, storedProcName, parameters);\r\n                rowsAffected = command.ExecuteNonQuery();\r\n                result = (int)command.Parameters[\"ReturnValue\"].Value;\r\n                //Connection.Close();\r\n                return result;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ���� OleDbCommand ����ʵ��(��������һ������ֵ)	\r\n        /// </summary>\r\n        /// <param name=\"storedProcName\">�洢������</param>\r\n        /// <param name=\"parameters\">�洢���̲���</param>\r\n        /// <returns>OleDbCommand ����ʵ��</returns>\r\n        private static OleDbCommand BuildIntCommand(OleDbConnection connection, string storedProcName, IDataParameter[] parameters)\r\n        {\r\n            OleDbCommand command = BuildQueryCommand(connection, storedProcName, parameters);\r\n            command.Parameters.Add(new OleDbParameter(\"ReturnValue\",\r\n                OleDbType.Integer, 4, ParameterDirection.ReturnValue,\r\n                false, 0, 0, string.Empty, DataRowVersion.Default, null));\r\n            return command;\r\n        }\r\n        #endregion\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'OleDBHelper.cs.DBUtility.txt');
INSERT INTO `txt` VALUES ('609', '2', 'oracle.helper.dbhelper.txt', 'using System;\r\nusing System.Configuration;\r\nusing System.Data;\r\nusing System.Data.OracleClient;\r\nusing System.Collections;\r\n\r\nnamespace Kufolin.Framework.DBUtility\r\n{\r\n\r\n    /// <summary>\r\n    /// A helper class used to execute queries against an Oracle database\r\n    /// </summary>\r\n    public abstract class OracleHelper {\r\n\r\n        // Read the connection strings from the configuration file\r\n        public static readonly string ConnectionStringLocalTransaction = ConfigurationManager.ConnectionStrings[\"OraConnString1\"].ConnectionString;\r\n        public static readonly string ConnectionStringInventoryDistributedTransaction = ConfigurationManager.ConnectionStrings[\"OraConnString2\"].ConnectionString;\r\n        public static readonly string ConnectionStringOrderDistributedTransaction = ConfigurationManager.ConnectionStrings[\"OraConnString3\"].ConnectionString;\r\n        public static readonly string ConnectionStringProfile = ConfigurationManager.ConnectionStrings[\"OraProfileConnString\"].ConnectionString;\r\n        public static readonly string ConnectionStringMembership = ConfigurationManager.ConnectionStrings[\"OraMembershipConnString\"].ConnectionString;\r\n\r\n        //Create a hashtable for the parameter cached\r\n        private static Hashtable parmCache = Hashtable.Synchronized(new Hashtable());\r\n\r\n        /// <summary>\r\n        /// Execute a database query which does not include a select\r\n        /// </summary>\r\n        /// <param name=\"connString\">Connection string to database</param>\r\n        /// <param name=\"cmdType\">Command type either stored procedure or SQL</param>\r\n        /// <param name=\"cmdText\">Acutall SQL Command</param>\r\n        /// <param name=\"commandParameters\">Parameters to bind to the command</param>\r\n        /// <returns></returns>\r\n        public static int ExecuteNonQuery(string connectionString, CommandType cmdType, string cmdText, params OracleParameter[] commandParameters) {\r\n            // Create a new Oracle command\r\n            OracleCommand cmd = new OracleCommand();\r\n\r\n            //Create a connection\r\n            using (OracleConnection connection = new OracleConnection(connectionString)) {\r\n\r\n                //Prepare the command\r\n                PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n\r\n                //Execute the command\r\n                int val = cmd.ExecuteNonQuery();\r\n                cmd.Parameters.Clear();\r\n                return val;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute an OracleCommand (that returns no resultset) against an existing database transaction \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  int result = ExecuteNonQuery(trans, CommandType.StoredProcedure, \"PublishOrders\", new OracleParameter(\":prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"trans\">an existing database transaction</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or PL/SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of OracleParamters used to execute the command</param>\r\n        /// <returns>an int representing the number of rows affected by the command</returns>\r\n        public static int ExecuteNonQuery(OracleTransaction trans, CommandType cmdType, string cmdText, params OracleParameter[] commandParameters) {\r\n            OracleCommand cmd = new OracleCommand();\r\n            PrepareCommand(cmd, trans.Connection, trans, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute an OracleCommand (that returns no resultset) against an existing database connection \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  int result = ExecuteNonQuery(connString, CommandType.StoredProcedure, \"PublishOrders\", new OracleParameter(\":prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"conn\">an existing database connection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or PL/SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of OracleParamters used to execute the command</param>\r\n        /// <returns>an int representing the number of rows affected by the command</returns>\r\n        public static int ExecuteNonQuery(OracleConnection connection, CommandType cmdType, string cmdText, params OracleParameter[] commandParameters) {\r\n\r\n            OracleCommand cmd = new OracleCommand();\r\n\r\n            PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a select query that will return a result set\r\n        /// </summary>\r\n        /// <param name=\"connString\">Connection string</param>\r\n        //// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or PL/SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of OracleParamters used to execute the command</param>\r\n        /// <returns></returns>\r\n        public static OracleDataReader ExecuteReader(string connectionString, CommandType cmdType, string cmdText, params OracleParameter[] commandParameters) {\r\n\r\n            //Create the command and connection\r\n            OracleCommand cmd = new OracleCommand();\r\n            OracleConnection conn = new OracleConnection(connectionString);\r\n\r\n            try {\r\n                //Prepare the command to execute\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n\r\n                //Execute the query, stating that the connection should close when the resulting datareader has been read\r\n                OracleDataReader rdr = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n                cmd.Parameters.Clear();\r\n                return rdr;\r\n\r\n            }\r\n            catch {\r\n\r\n                //If an error occurs close the connection as the reader will not be used and we expect it to close the connection\r\n                conn.Close();\r\n                throw;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute an OracleCommand that returns the first column of the first record against the database specified in the connection string \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new OracleParameter(\":prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">a valid connection string for a SqlConnection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or PL/SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of OracleParamters used to execute the command</param>\r\n        /// <returns>An object that should be converted to the expected type using Convert.To{Type}</returns>\r\n        public static object ExecuteScalar(string connectionString, CommandType cmdType, string cmdText, params OracleParameter[] commandParameters) {\r\n            OracleCommand cmd = new OracleCommand();\r\n\r\n            using (OracleConnection conn = new OracleConnection(connectionString)) {\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n                object val = cmd.ExecuteScalar();\r\n                cmd.Parameters.Clear();\r\n                return val;\r\n            }\r\n        }\r\n\r\n        ///    <summary>\r\n        ///    Execute    a OracleCommand (that returns a 1x1 resultset)    against    the    specified SqlTransaction\r\n        ///    using the provided parameters.\r\n        ///    </summary>\r\n        ///    <param name=\"transaction\">A    valid SqlTransaction</param>\r\n        ///    <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        ///    <param name=\"commandText\">The stored procedure name    or PL/SQL command</param>\r\n        ///    <param name=\"commandParameters\">An array of    OracleParamters used to execute the command</param>\r\n        ///    <returns>An    object containing the value    in the 1x1 resultset generated by the command</returns>\r\n        public static object ExecuteScalar(OracleTransaction transaction, CommandType commandType, string commandText, params OracleParameter[] commandParameters) {\r\n            if(transaction == null)\r\n                throw new ArgumentNullException(\"transaction\");\r\n            if(transaction != null && transaction.Connection == null)\r\n                throw new ArgumentException(\"The transaction was rollbacked    or commited, please    provide    an open    transaction.\", \"transaction\");\r\n\r\n            // Create a    command    and    prepare    it for execution\r\n            OracleCommand cmd = new OracleCommand();\r\n\r\n            PrepareCommand(cmd, transaction.Connection, transaction, commandType, commandText, commandParameters);\r\n\r\n            // Execute the command & return    the    results\r\n            object retval = cmd.ExecuteScalar();\r\n\r\n            // Detach the SqlParameters    from the command object, so    they can be    used again\r\n            cmd.Parameters.Clear();\r\n            return retval;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute an OracleCommand that returns the first column of the first record against an existing database connection \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  Object obj = ExecuteScalar(conn, CommandType.StoredProcedure, \"PublishOrders\", new OracleParameter(\":prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"conn\">an existing database connection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or PL/SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of OracleParamters used to execute the command</param>\r\n        /// <returns>An object that should be converted to the expected type using Convert.To{Type}</returns>\r\n        public static object ExecuteScalar(OracleConnection connectionString, CommandType cmdType, string cmdText, params OracleParameter[] commandParameters) {\r\n            OracleCommand cmd = new OracleCommand();\r\n\r\n            PrepareCommand(cmd, connectionString, null, cmdType, cmdText, commandParameters);\r\n            object val = cmd.ExecuteScalar();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Add a set of parameters to the cached\r\n        /// </summary>\r\n        /// <param name=\"cacheKey\">Key value to look up the parameters</param>\r\n        /// <param name=\"commandParameters\">Actual parameters to cached</param>\r\n        public static void CacheParameters(string cacheKey, params OracleParameter[] commandParameters) {\r\n            parmCache[cacheKey] = commandParameters;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Fetch parameters from the cache\r\n        /// </summary>\r\n        /// <param name=\"cacheKey\">Key to look up the parameters</param>\r\n        /// <returns></returns>\r\n        public static OracleParameter[] GetCachedParameters(string cacheKey) {\r\n            OracleParameter[] cachedParms = (OracleParameter[])parmCache[cacheKey];\r\n\r\n            if (cachedParms == null)\r\n                return null;\r\n\r\n            // If the parameters are in the cache\r\n            OracleParameter[] clonedParms = new OracleParameter[cachedParms.Length];\r\n\r\n            // return a copy of the parameters\r\n            for (int i = 0, j = cachedParms.Length; i < j; i++)\r\n                clonedParms[i] = (OracleParameter)((ICloneable)cachedParms[i]).Clone();\r\n\r\n            return clonedParms;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Internal function to prepare a command for execution by the database\r\n        /// </summary>\r\n        /// <param name=\"cmd\">Existing command object</param>\r\n        /// <param name=\"conn\">Database connection object</param>\r\n        /// <param name=\"trans\">Optional transaction object</param>\r\n        /// <param name=\"cmdType\">Command type, e.g. stored procedure</param>\r\n        /// <param name=\"cmdText\">Command test</param>\r\n        /// <param name=\"commandParameters\">Parameters for the command</param>\r\n        private static void PrepareCommand(OracleCommand cmd, OracleConnection conn, OracleTransaction trans, CommandType cmdType, string cmdText, OracleParameter[] commandParameters) {\r\n\r\n            //Open the connection if required\r\n            if (conn.State != ConnectionState.Open)\r\n                conn.Open();\r\n\r\n            //Set up the command\r\n            cmd.Connection = conn;\r\n            cmd.CommandText = cmdText;\r\n            cmd.CommandType = cmdType;\r\n\r\n            //Bind it to the transaction if it exists\r\n            if (trans != null)\r\n                cmd.Transaction = trans;\r\n\r\n            // Bind the parameters passed in\r\n            if (commandParameters != null) {\r\n                foreach (OracleParameter parm in commandParameters)\r\n                    cmd.Parameters.Add(parm);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Converter to use boolean data type with Oracle\r\n        /// </summary>\r\n        /// <param name=\"value\">Value to convert</param>\r\n        /// <returns></returns>\r\n        public static string OraBit(bool value) {\r\n            if(value)\r\n                return \"Y\";\r\n            else\r\n                return \"N\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// Converter to use boolean data type with Oracle\r\n        /// </summary>\r\n        /// <param name=\"value\">Value to convert</param>\r\n        /// <returns></returns>\r\n        public static bool OraBool(string value) {\r\n            if(value.Equals(\"Y\"))\r\n                return true;\r\n            else\r\n                return false;\r\n        } \r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'oracle.helper.dbhelper.txt');
INSERT INTO `txt` VALUES ('610', '2', 'OracleHelper.cs.DBUtility.txt', '/**************************************\r\n* ���ã�Oracle����ʵ��\r\n**************************************/\r\n\r\nusing System;\r\nusing System.Collections;\r\nusing System.Collections.Specialized;\r\nusing System.Data;\r\nusing System.Data.OracleClient;\r\nusing System.Configuration;\r\n\r\nnamespace Permission.DBUtility\r\n{\r\n    public abstract class OracleHelper\r\n    {\r\n        //���ݿ������ַ���(web.config������)�����Զ�̬����SQLString֧�ֶ����ݿ�.		\r\n        public static string connectionString = ConfigurationManager.AppSettings[\"SQLString\"];\r\n\r\n        public OracleHelper() { }\r\n\r\n        #region ���÷���\r\n\r\n        public static int GetMaxID(string FieldName, string TableName)\r\n        {\r\n            string strsql = \"select max(\" + FieldName + \")+1 from \" + TableName;\r\n            object obj = GetSingle(strsql);\r\n            if (obj == null)\r\n            {\r\n                return 1;\r\n            }\r\n            else\r\n            {\r\n                return int.Parse(obj.ToString());\r\n            }\r\n        }\r\n\r\n        public static bool Exists(string strSql)\r\n        {\r\n            object obj = GetSingle(strSql);\r\n            int cmdresult;\r\n            if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n            {\r\n                cmdresult = 0;\r\n            }\r\n            else\r\n            {\r\n                cmdresult = int.Parse(obj.ToString());\r\n            }\r\n            if (cmdresult == 0)\r\n            {\r\n                return false;\r\n            }\r\n            else\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n\r\n        public static bool Exists(string strSql, params OracleParameter[] cmdParms)\r\n        {\r\n            object obj = GetSingle(strSql, cmdParms);\r\n            int cmdresult;\r\n            if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n            {\r\n                cmdresult = 0;\r\n            }\r\n            else\r\n            {\r\n                cmdresult = int.Parse(obj.ToString());\r\n            }\r\n            if (cmdresult == 0)\r\n            {\r\n                return false;\r\n            }\r\n            else\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region  ִ�м�SQL���\r\n\r\n        /// <summary>\r\n        /// ִ��SQL��䣬����Ӱ��ļ�¼��\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL���</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSql(string SQLString)\r\n        {\r\n            using (OracleConnection connection = new OracleConnection(connectionString))\r\n            {\r\n                using (OracleCommand cmd = new OracleCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.OracleClient.OracleException E)\r\n                    {\r\n                        connection.Close();\r\n                        throw new Exception(E.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�ж���SQL��䣬ʵ�����ݿ�����\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">����SQL���</param>		\r\n        public static void ExecuteSqlTran(ArrayList SQLStringList)\r\n        {\r\n            using (OracleConnection conn = new OracleConnection(connectionString))\r\n            {\r\n                conn.Open();\r\n                OracleCommand cmd = new OracleCommand();\r\n                cmd.Connection = conn;\r\n                OracleTransaction tx = conn.BeginTransaction();\r\n                cmd.Transaction = tx;\r\n                try\r\n                {\r\n                    for (int n = 0; n < SQLStringList.Count; n++)\r\n                    {\r\n                        string strsql = SQLStringList[n].ToString();\r\n                        if (strsql.Trim().Length > 1)\r\n                        {\r\n                            cmd.CommandText = strsql;\r\n                            cmd.ExecuteNonQuery();\r\n                        }\r\n                    }\r\n                    tx.Commit();\r\n                }\r\n                catch (System.Data.OracleClient.OracleException E)\r\n                {\r\n                    tx.Rollback();\r\n                    throw new Exception(E.Message);\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�д�һ���洢���̲����ĵ�SQL��䡣\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL���</param>\r\n        /// <param name=\"content\">��������,����һ���ֶ��Ǹ�ʽ���ӵ����£���������ţ�����ͨ�������ʽ���</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSql(string SQLString, string content)\r\n        {\r\n            using (OracleConnection connection = new OracleConnection(connectionString))\r\n            {\r\n                OracleCommand cmd = new OracleCommand(SQLString, connection);\r\n                System.Data.OracleClient.OracleParameter myParameter = new System.Data.OracleClient.OracleParameter(\"@content\", OracleType.NVarChar);\r\n                myParameter.Value = content;\r\n                cmd.Parameters.Add(myParameter);\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    int rows = cmd.ExecuteNonQuery();\r\n                    return rows;\r\n                }\r\n                catch (System.Data.OracleClient.OracleException E)\r\n                {\r\n                    throw new Exception(E.Message);\r\n                }\r\n                finally\r\n                {\r\n                    cmd.Dispose();\r\n                    connection.Close();\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// �����ݿ������ͼ���ʽ���ֶ�(������������Ƶ���һ��ʵ��)\r\n        /// </summary>\r\n        /// <param name=\"strSQL\">SQL���</param>\r\n        /// <param name=\"fs\">ͼ���ֽ�,���ݿ���ֶ�����Ϊimage�����</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSqlInsertImg(string strSQL, byte[] fs)\r\n        {\r\n            using (OracleConnection connection = new OracleConnection(connectionString))\r\n            {\r\n                OracleCommand cmd = new OracleCommand(strSQL, connection);\r\n                System.Data.OracleClient.OracleParameter myParameter = new System.Data.OracleClient.OracleParameter(\"@fs\", OracleType.LongRaw);\r\n                myParameter.Value = fs;\r\n                cmd.Parameters.Add(myParameter);\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    int rows = cmd.ExecuteNonQuery();\r\n                    return rows;\r\n                }\r\n                catch (System.Data.OracleClient.OracleException E)\r\n                {\r\n                    throw new Exception(E.Message);\r\n                }\r\n                finally\r\n                {\r\n                    cmd.Dispose();\r\n                    connection.Close();\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ��һ�������ѯ�����䣬���ز�ѯ�����object����\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">�����ѯ������</param>\r\n        /// <returns>��ѯ�����object��</returns>\r\n        public static object GetSingle(string SQLString)\r\n        {\r\n            using (OracleConnection connection = new OracleConnection(connectionString))\r\n            {\r\n                using (OracleCommand cmd = new OracleCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        object obj = cmd.ExecuteScalar();\r\n                        if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                        {\r\n                            return null;\r\n                        }\r\n                        else\r\n                        {\r\n                            return obj;\r\n                        }\r\n                    }\r\n                    catch (System.Data.OracleClient.OracleException e)\r\n                    {\r\n                        connection.Close();\r\n                        throw new Exception(e.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�в�ѯ��䣬����OracleDataReader ( ע�⣺���ø÷�����һ��Ҫ��SqlDataReader����Close )\r\n        /// </summary>\r\n        /// <param name=\"strSQL\">��ѯ���</param>\r\n        /// <returns>OracleDataReader</returns>\r\n        public static OracleDataReader ExecuteReader(string strSQL)\r\n        {\r\n            OracleConnection connection = new OracleConnection(connectionString);\r\n            OracleCommand cmd = new OracleCommand(strSQL, connection);\r\n            try\r\n            {\r\n                connection.Open();\r\n                OracleDataReader myReader = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n                return myReader;\r\n            }\r\n            catch (System.Data.OracleClient.OracleException e)\r\n            {\r\n                throw new Exception(e.Message);\r\n            }\r\n\r\n        }\r\n\r\n        /// <summary>\r\n        /// ִ�в�ѯ��䣬����DataSet\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">��ѯ���</param>\r\n        /// <returns>DataSet</returns>\r\n        public static DataSet Query(string SQLString)\r\n        {\r\n            using (OracleConnection connection = new OracleConnection(connectionString))\r\n            {\r\n                DataSet ds = new DataSet();\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    OracleDataAdapter command = new OracleDataAdapter(SQLString, connection);\r\n                    command.Fill(ds, \"ds\");\r\n                }\r\n                catch (System.Data.OracleClient.OracleException ex)\r\n                {\r\n                    throw new Exception(ex.Message);\r\n                }\r\n                return ds;\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region ִ�д�������SQL���\r\n\r\n        /// <summary>\r\n        /// ִ��SQL��䣬����Ӱ��ļ�¼��\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL���</param>\r\n        /// <returns>Ӱ��ļ�¼��</returns>\r\n        public static int ExecuteSql(string SQLString, params OracleParameter[] cmdParms)\r\n        {\r\n            using (OracleConnection connection = new OracleConnection(connectionString))\r\n            {\r\n                using (OracleCommand cmd = new OracleCommand())\r\n                {\r\n                    try\r\n                    {\r\n                        PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        cmd.Parameters.Clear();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.OracleClient.OracleException E)\r\n                    {\r\n                        throw new Exception(E.Message);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n        \r\n        /// <summary>\r\n        /// ִ�ж���SQL��䣬ʵ�����ݿ�����\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">SQL���Ĺ�ϣ���keyΪsql��䣬value�Ǹ�����OracleParameter[]��</param>\r\n        public static void E