   /// </summary>\r\n        public string ToJosn()\r\n        {\r\n            StringBuilder str = new StringBuilder();\r\n            Name = Name.Replace(\"\\\"\", \"＂\");\r\n            Value = Value.Replace(\"\\\"\", \"＂\"); \r\n            str.Append(\"\\\"\" + Name + \"\\\"\");\r\n            str.Append(\":\");\r\n            str.Append(\"\\\"\" + Value + \"\\\"\"); \r\n            return str.ToString();\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'ParamsKey.cs.txt');
INSERT INTO `txt` VALUES ('615', '2', 'ParamsKeyList.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Web;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    /// <summary>\r\n    /// 获取列表数组\r\n    /// </summary>\r\n    public class ParamsKeyList : List<ParamsKey>\r\n    {\r\n        /// <summary>\r\n        /// 添加一个参数表\r\n        /// </summary>\r\n        /// <param name=\"param\"></param>\r\n        public void Add(ParamsKey param)\r\n        {\r\n            ParamsKey paramsKey1 = null;\r\n            foreach (ParamsKey paramsKey in this)\r\n            {\r\n                if (paramsKey.Name == param.Name)\r\n                {\r\n                    paramsKey1 = paramsKey;\r\n                    break;\r\n                }\r\n            }\r\n            if (paramsKey1 != null)\r\n            {\r\n                this.Remove(paramsKey1);\r\n            }\r\n            base.Add(param);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 添加一个参数表\r\n        /// </summary>\r\n        /// <param name=\"sName\">参数名</param>\r\n        public void Remove(string sName)\r\n        { \r\n            foreach (ParamsKey paramsKey in this)\r\n            {\r\n                if (paramsKey.Name == sName)\r\n                {\r\n                    this.Remove(paramsKey); \r\n                    break;\r\n                }\r\n            }  \r\n        }\r\n\r\n        /// <summary>\r\n        /// 添加一个参数表\r\n        /// </summary>\r\n        /// <param name=\"sName\">名字</param>\r\n        /// <param name=\"sValue\">值</param>\r\n        public void Add(string sName, string sValue)\r\n        {\r\n            ParamsKey paramsKey1 = null;\r\n            foreach (ParamsKey paramsKey in this)\r\n            {\r\n                if (paramsKey.Name == sName)\r\n                {\r\n                    paramsKey1 = paramsKey;\r\n                    break;\r\n                }\r\n            }\r\n            if (paramsKey1 != null)\r\n            {\r\n                this.Remove(paramsKey1);\r\n            }\r\n            base.Add(new ParamsKey(sName, sValue));\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 生成值数组列表\r\n        /// </summary> \r\n        public object[] ToObjectList()\r\n        {\r\n            object[] oList = new object[this.Count];\r\n            for (int i = 0; i < this.Count; i++)\r\n            {\r\n                oList[i] = this[i].Value;\r\n            }\r\n            return oList;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 生成值数组列表\r\n        /// </summary> \r\n        public string[] ToStringList()\r\n        {\r\n            string[] oList = new string[this.Count];\r\n            for (int i = 0; i < this.Count; i++)\r\n            {\r\n                oList[i] = this[i].Value;\r\n            }\r\n            return oList;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 生成键值列表\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public override string ToString()\r\n        {\r\n            string sTemp = \"\";\r\n            StringBuilder str = new StringBuilder();\r\n            for (int i = 0; i < this.Count; i++)\r\n            {\r\n                ParamsKey key = this[i];\r\n                if (i != 0)\r\n                {\r\n                    sTemp = \"&\";\r\n                }\r\n                str.Append(sTemp + key.ToOperator());\r\n            }\r\n            return str.ToString();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 根据值查询名字\r\n        /// </summary>\r\n        /// <param name=\"sValue\">值</param>\r\n        /// <returns>名字(没有则为空字符)</returns>\r\n        public string FetchNameByValue(string sValue)\r\n        {\r\n            foreach (ParamsKey sParams in this)\r\n            {\r\n                if (sParams.Value == sValue)\r\n                {\r\n                    return sParams.Name;\r\n                }\r\n            }\r\n            return \"\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 根据名字查询值\r\n        /// </summary>\r\n        /// <param name=\"sName\">名字</param>\r\n        /// <returns>值(没有则为空字符)</returns>\r\n        public string FetchValueByName(string sName)\r\n        {\r\n            foreach (ParamsKey sParams in this)\r\n            {\r\n                if (sParams.Name == sName)\r\n                {\r\n                    return sParams.Value;\r\n                }\r\n            }\r\n            return \"\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 参数 解码 输出\r\n        /// </summary> \r\n        /// <returns>解码是否成功</returns>\r\n        public bool ToDecode(string sDecode)\r\n        {\r\n            if (string.IsNullOrEmpty(sDecode))\r\n            {\r\n                return false;\r\n            }\r\n            string sDecodeString = HttpUtility.UrlDecode(sDecode);\r\n            this.Clear();\r\n            return Resolve(sDecodeString);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 分解一个参数列表\r\n        /// </summary>\r\n        private bool Resolve(string sParam)\r\n        {\r\n            if (string.IsNullOrEmpty(sParam))\r\n            {\r\n                return false;\r\n            }\r\n            string[] sParamList = StringUtil.Split(sParam, \"&\");\r\n            foreach (string sPar in sParamList)\r\n            {\r\n                if (sPar.Trim() == \"\")\r\n                {\r\n                    continue;\r\n                }\r\n                string[] sParaList = sPar.Split(\'=\');\r\n                Add(sParaList[0], sParaList[1]);\r\n            }\r\n            return true;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 将参数加密 输出\r\n        /// </summary> \r\n        /// <returns></returns>\r\n        public string ToEncode()\r\n        {\r\n            return HttpUtility.UrlEncode(this.ToString());\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将参数生成一个JOSN数组 输出(字符中的\"已替换成“)\r\n        /// </summary> \r\n        /// <returns>JOSN数组</returns>\r\n        public string ToJosnList()\r\n        {\r\n            StringBuilder str = new StringBuilder();\r\n            str.Append(\"[\");\r\n            for (int i = 0; i < this.Count; i++)\r\n            {\r\n                ParamsKey sParams = this[i];\r\n                if (i != 0)\r\n                {\r\n                    str.Append(\",\");\r\n                }\r\n                str.Append(sParams.ToJosnList());\r\n            }\r\n            str.Append(\"]\");\r\n            return str.ToString();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将参数生成一个JOSN数组 输出(字符中的\"已替换成“)\r\n        /// </summary> \r\n        /// <returns>JOSN数组</returns>\r\n        public string ToJosn()\r\n        {\r\n            StringBuilder str = new StringBuilder();\r\n            //str.Append(\"[\");\r\n            str.Append(\"{\");\r\n            for (int i = 0; i < this.Count; i++)\r\n            {\r\n                ParamsKey sParams = this[i];\r\n                if (i != 0)\r\n                {\r\n                    str.Append(\",\");\r\n                }\r\n                str.Append(sParams.ToJosn());\r\n            }\r\n            str.Append(\"}\");\r\n            //str.Append(\"]\");\r\n            return str.ToString();\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 将参数生成一个JOSN数组 输出(字符中的\"已替换成“)\r\n        /// </summary> \r\n        /// <returns>JOSN数组</returns>\r\n        public string ToJosnArray()\r\n        {\r\n            StringBuilder str = new StringBuilder();\r\n            str.Append(\"[\");\r\n            str.Append(\"{\");\r\n            for (int i = 0; i < this.Count; i++)\r\n            {\r\n                ParamsKey sParams = this[i];\r\n                if (i != 0)\r\n                {\r\n                    str.Append(\",\");\r\n                }\r\n                str.Append(sParams.ToJosn());\r\n            }\r\n            str.Append(\"}\");\r\n            str.Append(\"]\");\r\n            return str.ToString();\r\n        }\r\n\r\n     \r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'ParamsKeyList.cs.txt');
INSERT INTO `txt` VALUES ('616', '2', 'php 调用c# .NET 写的webservice(亲测通过).txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Web;\r\nusing System.Web.Services;\r\nusing System.Web.Services.Protocols;\r\n/// <summary>\r\n///ibmfashion 的摘要说明\r\n/// </summary>\r\n[WebService(Namespace = \"http://tempuri.org/\")]\r\n[WebServiceBinding(ConformsTo = WsiProfiles.BasicProfile1_1)]\r\n[SoapDocumentService(RoutingStyle = SoapServiceRoutingStyle.RequestElement)]\r\n\r\n//若要允许使用 ASP.NET AJAX 从脚本中调用此 Web 服务，请取消对下行的注释。\r\n// [System.Web.Script.Services.ScriptService]\r\npublic class ibmfashion : System.Web.Services.WebService {\r\n    public ibmfashion () {\r\n        //如果使用设计的组件，请取消注释以下行\r\n        //InitializeComponent();\r\n    }\r\n    [WebMethod]\r\n    public string HelloWorld() {\r\n        return \"Hello World\";\r\n    }\r\n    [WebMethod]\r\n    public int multiplication(int a, int b)\r\n    {\r\n        return a*b;\r\n    }\r\n}\r\n \r\nphp调用c# webservice代码：\r\n<?php\r\n// Pull in the NuSOAP code\r\nob_start();\r\nrequire_once(\'lib/nusoap.php\');\r\n\r\n$url =\"http://localhost:8787/wcf/ibmfashion.asmx?wsdl\";\r\n$client = new nusoap_client($url, \'wsdl\',\'\',\'\',\'\',\'\');\r\n$client->soap_defencoding=\'utf-8\';\r\n$client->decode_utf8=false;\r\n$client->xml_encoding=\'utf-8\';\r\n//参数转换为数组传递\r\n$ary = array(\'a\' => 11, \'b\' => 22);\r\n$result = $client->call(\'multiplication\',$ary);\r\necho \"<pre>\".print_r($result,true).\"</pre>\";\r\n\r\n//错误及debug信息\r\nif ($client->fault) {\r\necho \'<h2>Fault</h2><pre>\';\r\nprint_r($result);\r\necho \'</pre>\';\r\n} else {\r\n// Check for errors\r\n$err = $client->getError();\r\nif ($err) {\r\n// Display the error\r\necho \'<h2>Error</h2><pre>\' . $err . \'</pre>\';\r\n} else {\r\n// Display the result\r\necho \'<h2>Result</h2><pre>\';\r\nprint_r($result);\r\necho \'</pre>\';\r\n}\r\n}\r\n// Display the debug messages\r\necho \'<h2>Debug</h2>\';\r\necho \'<pre>\' . htmlspecialchars($client->debug_str, ENT_QUOTES) . \'</pre>\';\r\n \r\n \r\n?>\r\n \r\n总结php调用c# .NET  webservice常用的几种方法：\r\n法1：\r\n检查System32目录是否有php_soap.dll，如果没有网上下载放到这个目录下。\r\n\r\n找到配置文件php.ini 文件， 打开以下扩展\r\nextension = php_soap.dll\r\nextension = php_curl.dll\r\nextension = php_openssl.dll\r\nPHP调用代码如下：\r\n方法1：\r\n$url =\"http://www.ws.cn/1/Healthgrow/Service.asmx?wsdl\";\r\n$client = new SoapClient($url);\r\n\r\n$client->soap_defencoding=\'utf-8\';\r\n$client->decode_utf8=false;\r\n$client->xml_encoding=\'utf-8\'\r\n\r\n$result = $client->__soapCall(\"UserLogin\",array(\"UserLogin\"=>array(\r\n\'str\' => \'{\"userName\":\"3\",\"password\":\"222\"}\')));\r\nif (is_soap_fault($result)) {\r\n    trigger_error(\"SOAP Fault: (faultcode: {$result->faultcode}, faultstring: {$result->faultstring})\", E_USER_ERROR);\r\n}\r\nelse\r\n{\r\n    echo print_r(\"return:\".$result->UserLoginResult,true);\r\n}\r\n \r\n方法2：\r\nphp 调用c# .NET 写的webservice(亲测通过)\r\n(from:http://www.2cto.com/kf/201211/170754.html)\r\nC#  代码：\r\n同样用php_soap.dll，只是代码略有不同：\r\n \r\n$url =\"http://www.ws.cn/1/Healthgrow/Service.asmx?wsdl\";\r\n$client = new SoapClient($url);\r\n\r\n$client->soap_defencoding=\'utf-8\';\r\n$client->decode_utf8=false;\r\n$client->xml_encoding=\'utf-8\';\r\n\r\n$result = $client->UserLogin(array(\'str\' => \'{\"userName\":\"3\",\"password\":\"222\"}\'));\r\n\r\nif (is_soap_fault($result)) {\r\n    trigger_error(\"SOAP Fault: (faultcode: {$result->faultcode}, faultstring: {$result->faultstring})\", E_USER_ERROR);\r\n}\r\nelse\r\n{\r\n    echo print_r(\"return:\".$result->UserLoginResult,true);\r\n}\r\n 方法3：\r\n使用NuSoap是PHP环境下的WebService编程工具，用于创建或调用WebService。它是一个开源软件，是完全采用PHP语言编写的、通过HTTP收发SOAP消息的一系列PHP类，由NuSphere Corporation(http://dietrich.ganx4.com/nusoap/ )开发。NuSOAP的一个优势是不需要扩展库的支持，这种特性使得NuSoap可以用于所有的PHP环境，不受服务器安全设置的影响。\r\n \r\n \r\n\r\n在处理过程中一定要注意WebService提供的参数是否匹配及正确。\r\n', '0', '2017-09-08 09:49:10', null, 'php 调用c# .NET 写的webservice(亲测通过).txt');
