INSERT INTO `txt` VALUES ('656', '2', 'SecurityHelper.cs.加密解密.txt', '/*----------------------------------------------------------------\r\n * \r\n        // 文件名：         SecurityHelper.cs\r\n * \r\n        // 文件功能描述：   加密解密算法\r\n * \r\n        // 创建人：         张晓坤\r\n * \r\n        // 创建时间：       2012-12-27\r\n * \r\n//----------------------------------------------------------------*/\r\n\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\nusing System.Data;\r\nusing System.Configuration;\r\nusing System.Security;\r\nusing System.Security.Cryptography;\r\nusing System.Web;\r\nusing System.Web.Security;\r\nusing System.IO;\r\n\r\nnamespace Bjqng.BjqngUtility\r\n{\r\n    /// <summary>\r\n    /// 加密解密\r\n    /// </summary>\r\n    public class SecurityHelper\r\n    {\r\n        public SecurityHelper() { }\r\n\r\n        #region public static string Encrypt( string pToEncrypt, string sKey )\r\n\r\n        /// <summary>\r\n        /// DES对称加密\r\n        /// </summary>\r\n        /// <param name=\"pToEncrypt\">需要加密的字符串</param>\r\n        /// <param name=\"sKey\">密钥</param>\r\n        /// <returns>加密后的字符串</returns>\r\n        public static string Encrypt(string pToEncrypt, string sKey)\r\n        {\r\n            return encrypt(pToEncrypt, sKey);\r\n        }\r\n\r\n        private static string encrypt(string pToEncrypt, string sKey)\r\n        {\r\n            DESCryptoServiceProvider des = new DESCryptoServiceProvider();\r\n            //把字符串放到byte数组中\r\n            byte[] inputByteArray = Encoding.Default.GetBytes(pToEncrypt);\r\n            //建立加密对象的密钥和偏移量\r\n            //原文使用ASCIIEncoding.ASCII方法的GetBytes方法\r\n            //使得输入密码必须输入英文文本  \r\n            KeySizes[] sizes = des.LegalKeySizes;\r\n            des.KeySize = 64;\r\n\r\n            des.Key = ASCIIEncoding.ASCII.GetBytes(sKey);\r\n            des.IV = ASCIIEncoding.ASCII.GetBytes(sKey);\r\n\r\n            MemoryStream ms = new MemoryStream();\r\n            CryptoStream cs = new CryptoStream(ms, des.CreateEncryptor(), CryptoStreamMode.Write);\r\n            //Write  the  byte  array  into  the  crypto  stream\r\n            //(It  will  end  up  in  the  memory  stream)\r\n            cs.Write(inputByteArray, 0, inputByteArray.Length);\r\n            cs.FlushFinalBlock();\r\n            //Get  the  data  back  from  the  memory  stream,  and  into  a  string\r\n            StringBuilder ret = new StringBuilder();\r\n            foreach (byte b in ms.ToArray())\r\n            {\r\n                //Format  as  hex  \r\n                ret.AppendFormat(\"{0:X2}\", b);\r\n            }\r\n            ret.ToString();\r\n            return ret.ToString();\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region public static string Decrypt( string pToDecrypt, string sKey )\r\n        /// <summary>\r\n        /// DES对称解密\r\n        /// </summary>\r\n        /// <param name=\"pToDecrypt\">需要解密的字符串</param>\r\n        /// <param name=\"sKey\">密钥</param>\r\n        /// <returns>解密后的字符串</returns>\r\n        public static string Decrypt(string pToDecrypt, string sKey)\r\n        {\r\n            return decrypt(pToDecrypt, sKey);\r\n        }\r\n\r\n        private static string decrypt(string pToDecrypt, string sKey)\r\n        {\r\n            DESCryptoServiceProvider des = new DESCryptoServiceProvider();\r\n            //Put  the  input  string  into  the  byte  array  \r\n            byte[] inputByteArray = new byte[pToDecrypt.Length / 2];\r\n            for (int x = 0; x < pToDecrypt.Length / 2; x++)\r\n            {\r\n                int i = (Convert.ToInt32(pToDecrypt.Substring(x * 2, 2), 16));\r\n                inputByteArray[x] = (byte)i;\r\n            }\r\n            //建立加密对象的密钥和偏移量，此值重要，不能修改  \r\n            des.Key = ASCIIEncoding.ASCII.GetBytes(sKey);\r\n            des.IV = ASCIIEncoding.ASCII.GetBytes(sKey);\r\n            MemoryStream ms = new MemoryStream();\r\n            CryptoStream cs = new CryptoStream(ms, des.CreateDecryptor(), CryptoStreamMode.Write);\r\n            //Flush  the  data  through  the  crypto  stream  into  the  memory  stream  \r\n            cs.Write(inputByteArray, 0, inputByteArray.Length);\r\n            cs.FlushFinalBlock();\r\n            //Get  the  decrypted  data  back  from  the  memory  stream  \r\n            //建立StringBuild对象，CreateDecrypt使用的是流对象，必须把解密后的文本变成流对象  \r\n            StringBuilder ret = new StringBuilder();\r\n            return System.Text.Encoding.Default.GetString(ms.ToArray());\r\n        }\r\n        #endregion\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:11', null, 'SecurityHelper.cs.加密解密.txt');
INSERT INTO `txt` VALUES ('657', '2', 'select count(1) from emp.txt', 'Ganesh -- Thanks for the question regarding \"difference between count(1) and count(*) \", version 3.0\r\nSubmitted on 12-Aug-2001 23:01 Central time zoneTom\'s latest followup | Bookmark | BottomLast updated 19-Oct-2011 18:04\r\nYou Asked \r\nWhat is the difference between count(1) and count(*) in a sql query\r\neg.\r\nselect count(1) from emp;\r\n   and\r\nselect count(*) from emp;\r\n\r\n \r\n\r\n\r\nand we said...\r\nnothing, they are the same, incur the same amount of work -- do the same thing, take the \r\nsame amount of resources.\r\n\r\n\r\nYou can see this via:\r\n\r\nops$tkyte@ORA817.US.ORACLE.COM> alter session set sql_trace=true;\r\n\r\nSession altered.\r\n\r\nops$tkyte@ORA817.US.ORACLE.COM> select count(*) from all_objects;\r\n\r\n  COUNT(*)\r\n----------\r\n     27044\r\n\r\nops$tkyte@ORA817.US.ORACLE.COM> select count(1) from all_objects\r\n  2  /\r\n\r\n  COUNT(1)\r\n----------\r\n     27044\r\n\r\n\r\nand the tkprof will show:\r\n\r\n\r\n\r\nselect count(*)\r\nfrom\r\n all_objects\r\n\r\n\r\ncall     count       cpu    elapsed       disk      query    current        rows\r\n------- ------  -------- ---------- ---------- ---------- ----------  ----------\r\nParse        1      0.02       0.02          0          0          0           0\r\nExecute      1      0.00       0.00          0          0          0           0\r\nFetch        2      5.56       5.56          0     234998          4           1\r\n------- ------  -------- ---------- ---------- ---------- ----------  ----------\r\ntotal        4      5.58       5.58          0     234998          4           1\r\n\r\nselect count(1)\r\nfrom\r\n all_objects\r\n\r\n\r\ncall     count       cpu    elapsed       disk      query    current        rows\r\n------- ------  -------- ---------- ---------- ---------- ----------  ----------\r\nParse        1      0.02       0.02          0          0          0           0\r\nExecute      1      0.00       0.00          0          0          0           0\r\nFetch        2      5.46       5.47          0     234998          4           1\r\n------- ------  -------- ---------- ---------- ---------- ----------  ----------\r\ntotal        4      5.48       5.49          0     234998          4           1\r\n\r\nSame number of blocks read/written/processed, same cpu times (basically) same elapsed \r\ntimes (basically).\r\n\r\nthey are identical.\r\n\r\n\r\nAnyone who thinks different (and I know you are out there) will have to post a test case \r\nlike the above or some scientific proof otherwise to be taken seriously....\r\n\r\n', '0', '2017-09-08 09:49:11', null, 'select count(1) from emp.txt');
