INSERT INTO `txt` VALUES ('680', '2', 'sql.ado.net.trans.事务.txt', '//��������Ŀ���ִ��sql������õ����� \r\n\r\nint newCount = 0;\r\n            string SQL_UPDATE = \"UPDATE [Information] SET [ColumnNewID] = @ColumnNewID,[Title] = @Title,[TitleEnglish]=@TitleEnglish,[Content] = @Content,ContentEnglish=@ContentEnglish,[Remarks] = @Remarks,[D_k1] = @D_k1,[DateCreate] = @DateCreate,Author=@Author,Source=@Source,ReleaseDateTime=@ReleaseDateTime,D_k2=@D_k2,D_k3=@D_k3,Mark=@Mark WHERE InformationID=@InformationID\";\r\n            //SqlParameter[] parms = GetUpdateParms(information);!\r\n            SqlParameter[] param = new SqlParameter[]{\r\n							new SqlParameter(\"@InformationID\",information.InformationID),\r\n							new SqlParameter(\"@ColumnNewID\",information.ColumnNewID),\r\n							new SqlParameter(\"@Title\",information.Title),\r\n                            new SqlParameter(\"@TitleEnglish\",information.TitleEnglish),\r\n							new SqlParameter(\"@Content\",information.Content),\r\n                            new SqlParameter(\"@ContentEnglish\",information.ContentEnglish),\r\n							new SqlParameter(\"@remarks\",information.Remarks),\r\n							new SqlParameter(\"@D_k1\",information.D_k1),\r\n							new SqlParameter(\"@DateCreate\",information.DateCreate),\r\n							new SqlParameter(\"@Author\",information.Author),\r\n							new SqlParameter(\"@Source\",information.Source),\r\n							new SqlParameter(\"@ReleaseDateTime\",information.ReleaseDateTime),\r\n							new SqlParameter(\"@D_k2\",information.D_k2),\r\n							new SqlParameter(\"@D_k3\",information.D_k3),\r\n							new SqlParameter(\"@Mark\",information.Mark)\r\n							};\r\n\r\n            using (SqlConnection conn = new SqlConnection(SqlDBHelper.conString))\r\n            {\r\n                conn.Open();\r\n                using (SqlTransaction trans = conn.BeginTransaction())\r\n                {\r\n                    try\r\n                    {\r\n                        newCount = SqlDBHelper.ExecuteNonQuery(trans, CommandType.Text, SQL_UPDATE, param);\r\n                        trans.Commit();\r\n                    }\r\n                    catch\r\n                    {\r\n                        trans.Rollback();\r\n                        throw;\r\n                    }\r\n                }\r\n            }\r\n            return newCount;\r\n', '0', '2017-09-08 09:49:11', null, 'sql.ado.net.trans.事务.txt');
