rns>\r\n        public int ExecuteNonQuery(string sql, SqlParameter[] para)\r\n        {\r\n            int flag = 0;\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql, con);\r\n                cmd.CommandType = CommandType.Text;\r\n                cmd.CommandText = sql;\r\n                cmd.Connection = GetConnection();\r\n            }\r\n            if (para != null && para.Length > 0)\r\n            {\r\n                //foreach (SqlParameter item in para)\r\n                //{\r\n                //    cmd.Parameters.Add(item);\r\n                //}\r\n                cmd.Parameters.AddRange(para);//������ı���ѭ��Ч��һ��\r\n            }\r\n            try\r\n            {\r\n                flag = cmd.ExecuteNonQuery();\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n            finally\r\n            {\r\n                CloseConnection();\r\n            }\r\n            return flag;\r\n        }\r\n        /// <summary>\r\n        /// ִ�зǲ�ѯ����\r\n        /// </summary>\r\n        /// <param name=\"spName\">sql����洢������(�����������ҪΪ��ִ�д洢���̣���Ϊִ��sql�����������ʵķ���)</param>\r\n        /// <param name=\"para\">��������</param>\r\n        /// <param name=\"type\">���ͣ�Text����StoredProcedure��</param>\r\n        /// <returns>int</returns>\r\n        public int ExecuteNonQuery(string spName, SqlParameter[] para, CommandType type)\r\n        {\r\n            int flag = 0;\r\n            if (!string.IsNullOrEmpty(spName))\r\n            {\r\n                cmd = new SqlCommand(spName, con);\r\n                cmd.CommandType = type;\r\n                cmd.CommandText = spName;\r\n                cmd.Connection = GetConnection();\r\n            }\r\n            if (para != null && para.Length > 0)\r\n            {\r\n                //foreach (SqlParameter item in para)\r\n                //{\r\n                //    cmd.Parameters.Add(item);\r\n                //}\r\n                cmd.Parameters.AddRange(para);\r\n            }\r\n            try\r\n            {\r\n                flag = cmd.ExecuteNonQuery();\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n            finally\r\n            {\r\n                if (con.State == ConnectionState.Open)\r\n                {\r\n                    con.Close();\r\n                    con = null;\r\n                }\r\n            }\r\n            return flag;\r\n        }\r\n        /// <summary>\r\n        /// ������\r\n        /// </summary>\r\n        public void OpenTransaction()\r\n        {\r\n            this.GetConnection();\r\n            trans = con.BeginTransaction();\r\n        }\r\n        /// <summary>\r\n        /// �ύ����\r\n        /// </summary>\r\n        public void CommitTransaction()\r\n        {\r\n            trans.Commit();\r\n            this.CloseConnection();\r\n        }\r\n        /// <summary>\r\n        /// �ع�����\r\n        /// </summary>\r\n        public void RollbackTransaction()\r\n        {\r\n            trans.Rollback();\r\n            this.CloseConnection();\r\n        }\r\n    }\r\n}\r\n        /*App.config�ǵ�����\r\n                        <?xml version=\"1.0\" encoding=\"utf-8\" ?>\r\n        <configuration>\r\n         <connectionStrings>\r\n           <add name=\"conStr\" connectionString=\"server=.;uid=sa;pwd=sa;database=northwind;\"/>\r\n         </connectionStrings>\r\n        </configuration>\r\n                */\r\n        /*Ӧ��ʵ��\r\n        /// <summary>\r\n        /// ���ݲɹ����ź���Ʒ��Ż�ȡ�ɹ������͵���\r\n        /// </summary>\r\n        /// <param name=\"PurchaseID\"></param>\r\n        /// <returns></returns>\r\n        public DataTable GetQuantityPrice(string PurchaseID, string ProductID)\r\n        {\r\n         //��1������һ��DataTable���͵�dt,�������ղ����ķ���ֵ\r\n            DataTable dt; \r\n         //��2������һ��sql���\r\n            string sql = \"select PurchaseQuantity,PurchaseUnitPrice from PurchaseDetail where PurchaseID=@PurchaseID and ProductID=@ProductID\";\r\n         //��3��������ʵ�������������飩\r\n            SqlParameter[] para = new SqlParameter[]{\r\n                    new SqlParameter(\"@PurchaseID\",PurchaseID),\r\n                    new SqlParameter(\"@ProductID\",ProductID)\r\n                 };\r\n            try\r\n            {\r\n          //��4�����÷���������dt���շ���ֵ\r\n                dt = helper.ExecuteQuery(sql, para);\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n            return dt;\r\n        }\r\n         */\r\n', '0', '2017-09-08 09:49:12', null, 'SqlHelper(10.2).txt');
INSERT INTO `txt` VALUES ('734', '2', 'SqlHelper(diy).txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n//\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing System.Configuration;//（1）先添加引用System.Configuration程序集，（2）再引入namaspace\r\n\r\nnamespace DAL\r\n{\r\n    public class SqlHelper\r\n    {\r\n        #region 声明变量\r\n\r\n        private SqlConnection con;\r\n        private SqlCommand cmd;\r\n        private SqlDataReader dr;\r\n        private DataTable dt;\r\n        private SqlTransaction tran;\r\n\r\n        #endregion\r\n\r\n        #region 打开数据库 OpenConnection()\r\n\r\n        /// <summary>\r\n        /// 打开数据库\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public SqlConnection OpenConnection()\r\n        {\r\n            if (con == null)\r\n            {\r\n                //con = new SqlConnection(ConfigurationSettings.AppSettings[\"SQL\"].ToString());//这种已经过时，不建议使用\r\n                con=new SqlConnection(ConfigurationManager.ConnectionStrings[\"conStr\"].ToString());\r\n                /*App.config是的内容\r\n                 <?xml version=\"1.0\" encoding=\"utf-8\" ?>\r\n<configuration>\r\n  <connectionStrings>\r\n    <add name=\"conStr\" connectionString=\"server=.;uid=sa;pwd=sa;database=northwind;\"/>\r\n  </connectionStrings>\r\n</configuration>\r\n                 */\r\n                con.Open();\r\n            }\r\n            return con;\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 关闭数据库 CloseConnection()\r\n\r\n        /// <summary>\r\n        /// 关闭数据库\r\n        /// </summary> \r\n        public void CloseConnection()\r\n        {\r\n            if (con != null && con.State != ConnectionState.Closed)\r\n            {\r\n                con.Close();\r\n                con = null;\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 打开事务 OpenTransaction()\r\n\r\n        /// <summary>\r\n        /// 打开事务\r\n        /// </summary>\r\n        public void OpenTransaction()\r\n        {\r\n            this.OpenConnection();\r\n            tran = con.BeginTransaction();\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 提交事务 CommitTransaction()\r\n\r\n        /// <summary>\r\n        /// 提交事务\r\n        /// </summary>\r\n        public void CommitTransaction()\r\n        {\r\n            tran.Commit();\r\n            this.CloseConnection();\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 回滚事务 RollbackTransaction()\r\n\r\n        /// <summary>\r\n        /// 回滚事务\r\n        /// </summary>\r\n        public void RollbackTransaction()\r\n        {\r\n            tran.Rollback();\r\n            this.CloseConnection();\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 查询数据库 GetDataTableBySql(string sql, SqlParameter[] para)\r\n\r\n        /// <summary>\r\n        /// 查询数据库，获得一个DataTable\r\n        /// </summary>\r\n        /// <param name=\"sql\">sql语句</param>\r\n        /// <param name=\"para\">参数（数组）</param>\r\n        /// <returns>DataTable</returns>\r\n        public DataTable GetDataTableBySql(string sql, SqlParameter[] para)\r\n        {\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql,con);\r\n                cmd.Connection = OpenConnection();\r\n            }\r\n            if (para != null && para.Length > 0)\r\n            {\r\n                //foreach (SqlParameter item in para)\r\n                //{\r\n                //    cmd.Parameters.Add(item);\r\n                //}\r\n                cmd.Parameters.AddRange(para);//与上面的遍历循环的效果一样\r\n            }\r\n            try\r\n            {\r\n                dr = cmd.ExecuteReader();\r\n                dt = new DataTable();\r\n                dt.Load(dr);\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n            finally\r\n            {\r\n		dr.Close();\r\n		dr=null;\r\n                CloseConnection();\r\n            }\r\n            return dt;\r\n        }\r\n        /*应用实例\r\n          /// <summary>\r\n        /// 根据采购单号和商品编号获取采购数量和单价\r\n        /// </summary>\r\n        /// <param name=\"PurchaseID\"></param>\r\n        /// <returns></returns>\r\n        public DataTable GetQuantityPrice(string PurchaseID, string ProductID)\r\n        {\r\n         //（1）声明一个DataTable类型的dt,用来接收操作的返回值\r\n            DataTable dt; \r\n         //（2）声明一个sql语句\r\n            string sql = \"select PurchaseQuantity,PurchaseUnitPrice from PurchaseDetail where PurchaseID=@PurchaseID and ProductID=@ProductID\";\r\n         //（3）声明并实例化参数（数组）\r\n            SqlParameter[] para = new SqlParameter[]{\r\n                    new SqlParameter(\"@PurchaseID\",PurchaseID),\r\n                    new SqlParameter(\"@ProductID\",ProductID)\r\n                 };\r\n            try\r\n            {\r\n          //（4）调用方法，并用dt接收返回值\r\n                dt = helper.GetDataTableBySql(sql, para);\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n            return dt;\r\n        }\r\n         */\r\n        #endregion\r\n\r\n        #region 更新数据库 UpdateDataBySql(string sql, SqlParameter[] para)\r\n\r\n        /// <summary>\r\n        /// 更新数据库\r\n        /// </summary>\r\n        /// <param name=\"sql\">sql语句</param>\r\n        /// <param name=\"para\">参数（数组）</param>\r\n        /// <returns></returns>\r\n        public int UpdateDataBySql(string sql, SqlParameter[] para)\r\n        {\r\n            int flag = 0;\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql,con);\r\n                cmd.Connection = OpenConnection();\r\n            }\r\n            if (para != null && para.Length > 0)\r\n            {\r\n                //foreach (SqlParameter mete in para)\r\n                //{\r\n                //    cmd.Parameters.Add(mete);\r\n                //}\r\n		cmd.Parameters.AddRange(para);//与上面的遍历循环的效果一样\r\n            }\r\n            try\r\n            {\r\n                flag = cmd.ExecuteNonQuery();\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n            finally\r\n            {\r\n                CloseConnection();\r\n            }\r\n            return flag;\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 利用事务对多个表进行插入 InsertDataBySql(string sql, SqlParameter[] para)\r\n\r\n        /// <summary>\r\n        /// 利用事务对多个表进行插入\r\n        /// </summary>\r\n        /// <param name=\"sql\">sql语句</param>\r\n        /// <param name=\"para\">参数（数组）</param>\r\n        /// <returns>int类型</returns>\r\n        public int InsertDataBySql(string sql, SqlParameter[] para)\r\n        {\r\n            int flag = 0;\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql,con);\r\n                cmd.Connection = this.con;\r\n            }\r\n            if (para != null && para.Length > 0)\r\n            {\r\n                foreach (SqlParameter p in para)\r\n                {\r\n                    cmd.Parameters.Add(p);\r\n                }\r\n 		//cmd.Parameters.AddRange(para);//与上面的遍历循环的效果一样\r\n            }\r\n            try\r\n            {\r\n                if (tran != null)\r\n                {\r\n                    cmd.Transaction = tran;\r\n                    flag = cmd.ExecuteNonQuery();\r\n                }\r\n            }\r\n            catch\r\n            {\r\n                throw;\r\n            }\r\n\r\n            return flag;\r\n        }\r\n\r\n        #endregion\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:12', null, 'SqlHelper(diy).txt');
INSERT INTO `txt` VALUES ('735', '2', 'SqlHelper(English).txt', 'using System;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing System.Configuration;\r\nusing System.Web;\r\nusing System.Web.Security;\r\nusing System.Web.UI;\r\nusing System.Web.UI.WebControls;\r\nusing System.Web.UI.WebControls.WebParts;\r\nusing System.Web.UI.HtmlControls;\r\n\r\n/// <summary>\r\n/// The SqlHelper class is intended to encapsulate high performance, \r\n/// scalable best practices for common uses of SqlClient.\r\n/// </summary>\r\npublic sealed class SqlHelper\r\n{\r\n	private SqlHelper(){}\r\n\r\n    //Database connection strings\r\n    public static readonly string CONN_STRING = ConfigurationManager.ConnectionStrings[\"Sql2005Express\"].ConnectionString;\r\n\r\n    /// <summary>\r\n    /// Execute a SqlCommand that returns a resultset against the database specified in the connection string \r\n    /// using the provided parameters.\r\n    /// </summary>\r\n    /// <remarks>\r\n    /// e.g.:  \r\n    ///  SqlDataReader r = ExecuteReader(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    /// <param name=\"cmdType\">the CommandType (stored procedure, text, etc.)</param>\r\n    /// <param name=\"cmdText\">the stored procedure name or T-SQL command</param>\r\n    /// <param name=\"cmdParms\">an array of SqlParamters used to execute the command</param>\r\n    /// <returns>A SqlDataReader containing the results</returns>\r\n    public static SqlDataReader ExecuteReader(CommandType cmdType, string cmdText, params SqlParameter[] cmdParms)\r\n    {\r\n        SqlCommand cmd = new SqlCommand();\r\n        SqlConnection conn = new SqlConnection(CONN_STRING);\r\n\r\n        // we use a try/catch here because if the method throws an exception we want to \r\n        // close the connection throw code, because no datareader will exist, hence the \r\n        // commandBehaviour.CloseConnection will not work\r\n        try\r\n        {\r\n            PrepareCommand(cmd, conn, null, cmdType, cmdText, cmdParms);\r\n            SqlDataReader rdr = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n            cmd.Parameters.Clear();\r\n            return rdr;\r\n        }\r\n        catch\r\n        {\r\n            conn.Close();\r\n            throw;\r\n        }\r\n    }\r\n\r\n    /// <summary>\r\n    /// Execute a SqlCommand that returns the first column of the first record against the database specified in the connection string \r\n    /// using the provided parameters.\r\n    /// </summary>\r\n    /// <remarks>\r\n    /// e.g.:  \r\n    ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    /// <param name=\"cmdType\">the CommandType (stored procedure, text, etc.)</param>\r\n    /// <param name=\"cmdText\">the stored procedure name or T-SQL command</param>\r\n    /// <param name=\"cmdParms\">an array of SqlParamters used to execute the command</param>\r\n    /// <returns>An object that should be converted to the expected type using Convert.To{Type}</returns>\r\n    public static object ExecuteScalar(CommandType cmdType, string cmdText, params SqlParameter[] cmdParms)\r\n    {\r\n        SqlCommand cmd = new SqlCommand();\r\n\r\n        using (SqlConnection conn = new SqlConnection(CONN_STRING))\r\n        {\r\n            PrepareCommand(cmd, conn, null, cmdType, cmdText, cmdParms);\r\n            object val = cmd.ExecuteScalar();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n    }\r\n\r\n    /// <summary>\r\n    /// Execute a SqlCommand that returns the first column of the first record against an existing database connection \r\n    /// using the provided parameters.\r\n    /// </summary>\r\n    /// <remarks>\r\n    /// e.g.:  \r\n    ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n    /// </remarks>\r\n    /// <param name=\"conn\">an existing database connection</param>\r\n    /// <param name=\"cmdType\">the CommandType (stored procedure, text, etc.)</param>\r\n    /// <param name=\"cmdText\">the stored procedure name or T-SQL command</param>\r\n    /// <param name=\"cmdParms\">an array of SqlParamters used to execute the command</param>\r\n    /// <returns>An object that should be converted to the expected type using Convert.To{Type}</returns>\r\n    public static object ExecuteScalar(SqlConnection conn, CommandType cmdType, string cmdText, params SqlParameter[] cmdParms)\r\n    {\r\n\r\n        SqlCommand cmd = new SqlCommand();\r\n\r\n        PrepareCommand(cmd, conn, null, cmdType, cmdText, cmdParms);\r\n        object val = cmd.ExecuteScalar();\r\n        cmd.Parameters.Clear();\r\n        return val;\r\n    }\r\n\r\n\r\n    /// <summary>\r\n    /// Prepare a command for execution\r\n    /// </summary>\r\n    /// <param name=\"cmd\">SqlCommand object</param>\r\n    /// <param name=\"conn\">SqlConnection object</param>\r\n    /// <param name=\"trans\">SqlTransaction object</param>\r\n    /// <param name=\"cmdType\">Cmd type e.g. stored procedure or text</param>\r\n    /// <param name=\"cmdText\">Command text, e.g. Select * from Products</param>\r\n    /// <param name=\"cmdParms\">SqlParameters to use in the command</param>\r\n    private static void PrepareCommand(SqlCommand cmd, SqlConnection conn, SqlTransaction trans, CommandType cmdType, string cmdText, SqlParameter[] cmdParms)\r\n    {\r\n\r\n        if (conn.State != ConnectionState.Open)\r\n            conn.Open();\r\n\r\n        cmd.Connection = conn;\r\n        cmd.CommandText = cmdText;\r\n\r\n        if (trans != null)\r\n            cmd.Transaction = trans;\r\n\r\n        cmd.CommandType = cmdType;\r\n\r\n        if (cmdParms != null)\r\n        {\r\n            foreach (SqlParameter parm in cmdParms)\r\n                cmd.Parameters.Add(parm);\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:12', null, 'SqlHelper(English).txt');
INSERT INTO `txt` VALUES ('736', '2', 'SqlHelper(PetShop).txt', 'using System;\r\nusing System.Configuration;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing System.Collections;\r\n\r\nnamespace PetShop.DBUtility {\r\n\r\n    /// <summary>\r\n    /// SqlHelper����ר���ṩ������û����ڸ����ܡ��������������ϰ��sql���ݲ���\r\n    /// </summary>\r\n    public abstract class SqlHelper {\r\n\r\n        //���ݿ������ַ���\r\n        public static readonly string ConnectionStringLocalTransaction= ConfigurationManager.ConnectionStrings[\"SQLConnString1\"].ConnectionString;\r\n        public static readonly string ConnectionStringInventoryDistributedTransaction = ConfigurationManager.ConnectionStrings[\"SQLConnString2\"].ConnectionString;\r\n        public static readonly string ConnectionStringOrderDistributedTransaction = ConfigurationManager.ConnectionStrings[\"SQLConnString3\"].ConnectionString;\r\n		public static readonly string ConnectionStringProfile = ConfigurationManager.ConnectionStrings[\"SQLProfileConnString\"].ConnectionString;		\r\n		\r\n        // ���ڻ��������HASH��\r\n        private static Hashtable parmCache = Hashtable.Synchronized(new Hashtable());\r\n\r\n        /// <summary>\r\n        ///  �������ӵ����ݿ��ü������ִ��һ��sql������������ݼ���\r\n        /// </summary>\r\n        /// <param name=\"connectionString\">һ����Ч�������ַ���</param>\r\n        /// <param name=\"commandType\">��������(�洢����, �ı�, �ȵ�)</param>\r\n        /// <param name=\"commandText\">�洢�������ƻ���sql�������</param>\r\n        /// <param name=\"commandParameters\">ִ���������ò����ļ���</param>\r\n        /// <returns>ִ��������Ӱ�������</returns>\r\n        public static int ExecuteNonQuery(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters) {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            using (SqlConnection conn = new SqlConnection(connectionString)) {\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n                int val = cmd.ExecuteNonQuery();\r\n                cmd.Parameters.Clear();\r\n                return val;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// �����е����ݿ�����ִ��һ��sql������������ݼ���\r\n        /// </summary>\r\n        /// <param name=\"conn\">һ�����е����ݿ�����</param>\r\n        /// <param name=\"commandType\">��������(�洢����, �ı�, �ȵ�)</param>\r\n        /// <param name=\"commandText\">�洢�������ƻ���sql�������</param>\r\n        /// <param name=\"commandParameters\">ִ���������ò����ļ���</param>\r\n        /// <returns>ִ��������Ӱ�������</returns>\r\n        public static int ExecuteNonQuery(SqlConnection connection, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters) {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        ///ʹ�����е�SQL����ִ��һ��sql������������ݼ���\r\n        /// </summary>\r\n        /// <remarks>\r\n        ///����:  \r\n        ///  int result = ExecuteNonQuery(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"trans\">һ�����е�����</param>\r\n        /// <param name=\"commandType\">��������(�洢����, �ı�, �ȵ�)</param>\r\n        /// <param name=\"commandText\">�洢�������ƻ���sql�������</param>\r\n        /// <param name=\"commandParameters\">ִ���������ò����ļ���</param>\r\n        /// <returns>ִ��������Ӱ�������</returns>\r\n        public static int ExecuteNonQuery(SqlTransaction trans, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters) {\r\n            SqlCommand cmd = new SqlCommand();\r\n            PrepareCommand(cmd, trans.Connection, trans, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ִ�е����ݿ�����ִ��һ���������ݼ���sql����\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// ����:  \r\n        ///  SqlDataReader r = ExecuteReader(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">һ����Ч�������ַ���</param>\r\n        /// <param name=\"commandType\">��������(�洢����, �ı�, �ȵ�)</param>\r\n        /// <param name=\"commandText\">�洢�������ƻ���sql�������</param>\r\n        /// <param name=\"commandParameters\">ִ���������ò����ļ���</param>\r\n        /// <returns>��������Ķ�ȡ��</returns>\r\n        public static SqlDataReader ExecuteReader(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters) \r\n        {\r\n            //����һ��SqlCommand����\r\n            SqlCommand cmd = new SqlCommand();\r\n            //����һ��SqlConnection����\r\n            SqlConnection conn = new SqlConnection(connectionString);\r\n\r\n            //������������һ��try/catch�ṹִ��sql�ı�����/�洢���̣���Ϊ��������������һ���쳣����Ҫ�ر����ӣ���Ϊû�ж�ȡ�����ڣ�\r\n            //���commandBehaviour.CloseConnection �Ͳ���ִ��\r\n            try {\r\n                //���� PrepareCommand �������� SqlCommand �������ò���\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n                //���� SqlCommand  �� ExecuteReader ����\r\n                SqlDataReader reader = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n                //�������\r\n                cmd.Parameters.Clear();\r\n                return reader;\r\n            }\r\n            catch {\r\n                //�ر����ӣ��׳��쳣\r\n                conn.Close();\r\n                throw;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ָ�������ݿ������ַ���ִ��һ���������һ�����ݼ��ĵ�һ��\r\n        /// </summary>\r\n        /// <remarks>\r\n        ///����:  \r\n        ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        ///<param name=\"connectionString\">һ����Ч�������ַ���</param>\r\n        /// <param name=\"commandType\">��������(�洢����, �ı�, �ȵ�)</param>\r\n        /// <param name=\"commandText\">�洢�������ƻ���sql�������</param>\r\n        /// <param name=\"commandParameters\">ִ���������ò����ļ���</param>\r\n        /// <returns>�� Convert.To{Type}������ת��Ϊ��Ҫ�� </returns>\r\n        public static object ExecuteScalar(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters) {\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            using (SqlConnection connection = new SqlConnection(connectionString)) {\r\n                PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n                object val = cmd.ExecuteScalar();\r\n                cmd.Parameters.Clear();\r\n                return val;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ָ�������ݿ�����ִ��һ���������һ�����ݼ��ĵ�һ��\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// ����:  \r\n        ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"conn\">һ�����ڵ����ݿ�����</param>\r\n        /// <param name=\"commandType\">��������(�洢����, �ı�, �ȵ�)</param>\r\n        /// <param name=\"commandText\">�洢�������ƻ���sql�������</param>\r\n        /// <param name=\"commandParameters\">ִ���������ò����ļ���</param>\r\n        /// <returns>�� Convert.To{Type}������ת��Ϊ��Ҫ�� </returns>\r\n        public static object ExecuteScalar(SqlConnection connection, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters) {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n            object val = cmd.ExecuteScalar();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ������������ӵ�����\r\n        /// </summary>\r\n        /// <param name=\"cacheKey\">��ӵ�����ı���</param>\r\n        /// <param name=\"cmdParms\">һ����Ҫ��ӵ������sql��������</param>\r\n        public static void CacheParameters(string cacheKey, params SqlParameter[] commandParameters)\r\n        {\r\n            parmCache[cacheKey] = commandParameters;\r\n        }\r\n\r\n        /// <summary>\r\n        /// �һỺ���������\r\n        /// </summary>\r\n        /// <param name=\"cacheKey\">�����һز����Ĺؼ���</param>\r\n        /// <returns>����Ĳ�������</returns>\r\n        public static SqlParameter[] GetCachedParameters(string cacheKey) {\r\n            SqlParameter[] cachedParms = (SqlParameter[])parmCache[cacheKey];\r\n\r\n            if (cachedParms == null)\r\n                return null;\r\n\r\n            SqlParameter[] clonedParms = new SqlParameter[cachedParms.Length];\r\n\r\n            for (int i = 0, j = cachedParms.Length; i < j; i++)\r\n                clonedParms[i] = (SqlParameter)((ICloneable)cachedParms[i]).Clone();\r\n\r\n            return clonedParms;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ׼��ִ��һ������\r\n        /// </summary>\r\n        /// <param name=\"cmd\">sql����</param>\r\n        /// <param name=\"conn\">Sql����</param>\r\n        /// <param name=\"trans\">Sql����</param>\r\n        /// <param name=\"cmdType\">������������ �洢���̻����ı�</param>\r\n        /// <param name=\"cmdText\">�����ı�,���磺Select * from Products</param>\r\n        /// <param name=\"cmdParms\">ִ������Ĳ���</param>\r\n        private static void PrepareCommand(SqlCommand cmd, SqlConnection conn, SqlTransaction trans, CommandType cmdType, string cmdText, SqlParameter[] cmdParms) {\r\n\r\n            if (conn.State != ConnectionState.Open)\r\n                conn.Open();\r\n\r\n            cmd.Connection = conn;\r\n            cmd.CommandText = cmdText;\r\n\r\n            if (trans != null)\r\n                cmd.Transaction = trans;\r\n\r\n            cmd.CommandType = cmdType;\r\n\r\n            if (cmdParms != null) {\r\n                foreach (SqlParameter parm in cmdParms)\r\n                    cmd.Parameters.Add(parm);\r\n            }\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:12', null, 'SqlHelper(PetShop).txt');
INSERT INTO `txt` VALUES ('737', '2', 'SqlHelper.united.cs.txt', 'using System;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing System.Collections.Generic;\r\nusing System.Collections;\r\nusing System.Text;\r\nusing System.Configuration;\r\n\r\nnamespace BjUnited.BjUnitedDAL.Sql\r\n{\r\n	/// <summary>\r\n    /// 数据访问基础类(基于SqlServer)\r\n    /// 可以用户可以修改满足自己项目的需要。\r\n    /// </summary>\r\n    public static class  SqlDBHelper\r\n    {\r\n        //private static string conString = \"server=.\\\\sqlexpress;uid=;pwd=;database=\";\r\n        public static string conString = ConfigurationManager.AppSettings[\"SqlconString\"];\r\n\r\n        private static Hashtable parmCache = Hashtable.Synchronized(new Hashtable());\r\n\r\n        /// <summary>\r\n        /// 执行增删改\r\n        /// </summary>\r\n        /// <param name=\"sql\">有效的insert update delete语句</param>\r\n        /// <param name=\"param\">参数集合</param>\r\n        /// <returns>影响的行数</returns>\r\n        public static int ExecuteNonQuery(string sql, SqlParameter[] param)\r\n        {\r\n            using (SqlConnection con = new SqlConnection(conString))\r\n            {\r\n                con.Open();\r\n                SqlCommand cmd = new SqlCommand(sql, con);\r\n                if (param != null)\r\n                    cmd.Parameters.AddRange(param);\r\n                int i = cmd.ExecuteNonQuery();\r\n                cmd.Parameters.Clear();\r\n                con.Close();\r\n                return i;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 执行查询 返回DataTable\r\n        /// </summary>\r\n        /// <param name=\"sql\">有效的select语句</param>\r\n        /// <returns>返回DataTable</returns>\r\n        public static DataTable ExecuteDataQuery(string sql)\r\n        {\r\n            using (SqlConnection con = new SqlConnection(conString))\r\n            {\r\n                con.Open();\r\n                SqlDataAdapter adap = new SqlDataAdapter(sql, con);\r\n                DataTable tbl = new DataTable();\r\n                adap.Fill(tbl);\r\n                con.Close();\r\n                return tbl;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 执行查询\r\n        /// </summary>\r\n        /// <param name=\"sql\">有效的select语句</param>\r\n        /// <returns>返回SqlDataReader</returns>\r\n        public static SqlDataReader ExecuteReader(string sql)\r\n        {\r\n            SqlConnection con = new SqlConnection(conString);\r\n            con.Open();\r\n            SqlCommand cmd = new SqlCommand(sql, con);\r\n            SqlDataReader reader=cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n            //con.Close();\r\n            return reader;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 执行查询\r\n        /// </summary>\r\n        /// <param name=\"sql\">有效的select语句</param>\r\n        /// <param name=\"param\">参数集合</param>\r\n        /// <returns>返回SqlDataReader</returns>\r\n        public static SqlDataReader ExecuteReader(string sql, SqlParameter[] param)\r\n        {\r\n            SqlConnection con = new SqlConnection(conString);\r\n            con.Open();\r\n            SqlCommand cmd = new SqlCommand(sql, con);\r\n            cmd.Parameters.AddRange(param);\r\n            SqlDataReader reader = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n            cmd.Parameters.Clear();\r\n            //con.Close();\r\n            return reader;    \r\n        }\r\n\r\n        /// <summary>\r\n        /// 执行查询 返回结果集中第一行第一列的值，忽略其他行列。\r\n        /// </summary>\r\n        /// <param name=\"sql\">有效的select语句</param>\r\n        /// <returns>返回结果集中第一行第一列的值，忽略其他行列。</returns>\r\n        public static object ExecuteScalar(string sql)\r\n        {\r\n            using (SqlConnection con = new SqlConnection(conString))\r\n            {\r\n                con.Open();\r\n                SqlCommand cmd = new SqlCommand(sql, con);\r\n                return cmd.ExecuteScalar();\r\n                \r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 执行sql语句，含事务。\r\n        /// </summary>\r\n        /// <param name=\"sql\">insert update delete语句\r\n        /// 例如：删除用户角色的同时，删除该角色下的所有用户。sql=\"delete from UserInfo where RoleID=1;delete from RoleInfo where RoleID=1\"</param>\r\n        /// <param name=\"param\">为sql语句中的参数提供值</param>\r\n        /// <returns>执行是否成功。</returns>\r\n        public static bool ExecuteNonQueryByTran(string sql, SqlParameter[] param)\r\n        {\r\n            SqlTransaction tran = null;\r\n            try\r\n            {\r\n                SqlConnection conn = new SqlConnection(conString);\r\n                conn.Open();\r\n                tran = conn.BeginTransaction();\r\n                SqlCommand cmd = new SqlCommand(sql, conn);\r\n                cmd.Parameters.AddRange(param);\r\n                cmd.Transaction = tran;\r\n                cmd.ExecuteNonQuery();\r\n                tran.Commit();\r\n                return true;\r\n            }\r\n            catch\r\n            {\r\n                tran.Rollback();\r\n                return false;\r\n            }\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns no resultset) against the database specified in the connection string \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  int result = ExecuteNonQuery(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">a valid connection string for a SqlConnection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of SqlParamters used to execute the command</param>\r\n        /// <returns>an int representing the number of rows affected by the command</returns>\r\n        public static int ExecuteNonQuery(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            using (SqlConnection conn = new SqlConnection(connectionString))\r\n            {\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n                \r\n                int val = cmd.ExecuteNonQuery();\r\n                cmd.Parameters.Clear();\r\n                conn.Close();\r\n                return val;\r\n                \r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns no resultset) against an existing database connection \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  int result = ExecuteNonQuery(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"conn\">an existing database connection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of SqlParamters used to execute the command</param>\r\n        /// <returns>an int representing the number of rows affected by the command</returns>\r\n        public static int ExecuteNonQuery(SqlConnection connection, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            connection.Close();\r\n            return val;\r\n        } \r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns no resultset) using an existing SQL Transaction \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  int result = ExecuteNonQuery(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"trans\">an existing sql transaction</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of SqlParamters used to execute the command</param>\r\n        /// <returns>an int representing the number of rows affected by the command</returns>\r\n        public static int ExecuteNonQuery(SqlTransaction trans, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n            SqlCommand cmd = new SqlCommand();\r\n            PrepareCommand(cmd, trans.Connection, trans, cmdType, cmdText, commandParameters);\r\n            int val = cmd.ExecuteNonQuery();\r\n            cmd.Parameters.Clear();\r\n            \r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 返回GUID\r\n        /// </summary>\r\n        /// <param name=\"trans\"></param>\r\n        /// <param name=\"cmdType\"></param>\r\n        /// <param name=\"cmdText\"></param>\r\n        /// <param name=\"commandParameters\"></param>\r\n        /// <returns></returns>\r\n        public static string ExecuteNonQueryString(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n            SqlCommand cmd = new SqlCommand();\r\n            using (SqlConnection conn = new SqlConnection(connectionString))\r\n            {\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n                string val = cmd.Parameters[\"@id\"].Value.ToString();\r\n                cmd.Parameters.Clear();\r\n                conn.Close();\r\n                return val;\r\n\r\n            }\r\n\r\n        }\r\n\r\n        /// <summary>\r\n        /// add parameter array to the cache\r\n        /// </summary>\r\n        /// <param name=\"cacheKey\">Key to the parameter cache</param>\r\n        /// <param name=\"cmdParms\">an array of SqlParamters to be cached</param>\r\n        public static void CacheParameters(string cacheKey, params SqlParameter[] commandParameters)\r\n        {\r\n            parmCache[cacheKey] = commandParameters;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Retrieve cached parameters\r\n        /// </summary>\r\n        /// <param name=\"cacheKey\">key used to lookup parameters</param>\r\n        /// <returns>Cached SqlParamters array</returns>\r\n        public static SqlParameter[] GetCachedParameters(string cacheKey)\r\n        {\r\n            SqlParameter[] cachedParms = (SqlParameter[])parmCache[cacheKey];\r\n\r\n            if (cachedParms == null)\r\n                return null;\r\n\r\n            SqlParameter[] clonedParms = new SqlParameter[cachedParms.Length];\r\n\r\n            for (int i = 0, j = cachedParms.Length; i < j; i++)\r\n                clonedParms[i] = (SqlParameter)((ICloneable)cachedParms[i]).Clone();\r\n\r\n            return clonedParms;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Prepare a command for execution\r\n        /// </summary>\r\n        /// <param name=\"cmd\">SqlCommand object</param>\r\n        /// <param name=\"conn\">SqlConnection object</param>\r\n        /// <param name=\"trans\">SqlTransaction object</param>\r\n        /// <param name=\"cmdType\">Cmd type e.g. stored procedure or text</param>\r\n        /// <param name=\"cmdText\">Command text, e.g. Select * from Products</param>\r\n        /// <param name=\"cmdParms\">SqlParameters to use in the command</param>\r\n        private static void PrepareCommand(SqlCommand cmd, SqlConnection conn, SqlTransaction trans, CommandType cmdType, string cmdText, SqlParameter[] cmdParms)\r\n        {\r\n\r\n            if (conn.State != ConnectionState.Open)\r\n                conn.Open();\r\n\r\n            cmd.Connection = conn;\r\n            cmd.CommandText = cmdText;\r\n            //cmd.CommandTimeout = 600;\r\n\r\n            if (trans != null)\r\n                cmd.Transaction = trans;\r\n\r\n            cmd.CommandType = cmdType;\r\n\r\n            if (cmdParms != null)\r\n            {\r\n                foreach (SqlParameter parm in cmdParms)\r\n                    cmd.Parameters.Add(parm);\r\n\r\n            }\r\n        }\r\n\r\n        #region ExecuteDataset\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns a resultset and takes no parameters) against the database specified in \r\n        /// the connection string. \r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  DataSet ds = ExecuteDataset(connString, CommandType.StoredProcedure, \"GetOrders\");\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">A valid connection string for a SqlConnection</param>\r\n        /// <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">The stored procedure name or T-SQL command</param>\r\n        /// <returns>A dataset containing the resultset generated by the command</returns>\r\n        public static DataSet ExecuteDataset(string connectionString, CommandType commandType, string commandText)\r\n        {\r\n            // Pass through the call providing null for the set of SqlParameters\r\n            return ExecuteDataset(connectionString, commandType, commandText, (SqlParameter[])null);\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns a resultset) against the database specified in the connection string \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  DataSet ds = ExecuteDataset(connString, CommandType.StoredProcedure, \"GetOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">A valid connection string for a SqlConnection</param>\r\n        /// <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">The stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">An array of SqlParamters used to execute the command</param>\r\n        /// <returns>A dataset containing the resultset generated by the command</returns>\r\n        public static DataSet ExecuteDataset(string connectionString, CommandType commandType, string commandText, params SqlParameter[] commandParameters)\r\n        {\r\n            if (connectionString == null || connectionString.Length == 0) throw new ArgumentNullException(\"connectionString\");\r\n\r\n            // Create & open a SqlConnection, and dispose of it after we are done\r\n            using (SqlConnection connection = new SqlConnection(connectionString))\r\n            {\r\n                connection.Open();\r\n\r\n                // Call the overload that takes a connection in place of the connection string\r\n                return ExecuteDataset(connection, commandType, commandText, commandParameters);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 添加返回INT\r\n        /// </summary>\r\n        /// <param name=\"connectionString\"></param>\r\n        /// <param name=\"commandType\"></param>\r\n        /// <param name=\"commandText\"></param>\r\n        /// <param name=\"commandParameters\"></param>\r\n        /// <returns></returns>\r\n        //public static int ExecuteInt(string connectionString, CommandType commandType, string commandText, params SqlParameter[] commandParameters)\r\n        //{\r\n        //    if (connectionString == null || connectionString.Length == 0) throw new ArgumentNullException(\"connectionString\");\r\n\r\n        //    // Create & open a SqlConnection, and dispose of it after we are done\r\n        //    using (SqlConnection connection = new SqlConnection(connectionString))\r\n        //    {\r\n        //        connection.Open();\r\n\r\n        //        // Call the overload that takes a connection in place of the connection string\r\n        //        return ExecuteDataset(connection, commandType, commandText, commandParameters);\r\n        //    }\r\n        //}\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns a resultset and takes no parameters) against the provided SqlConnection. \r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  DataSet ds = ExecuteDataset(conn, CommandType.StoredProcedure, \"GetOrders\");\r\n        /// </remarks>\r\n        /// <param name=\"connection\">A valid SqlConnection</param>\r\n        /// <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">The stored procedure name or T-SQL command</param>\r\n        /// <returns>A dataset containing the resultset generated by the command</returns>\r\n        public static DataSet ExecuteDataset(SqlConnection connection, CommandType commandType, string commandText)\r\n        {\r\n            // Pass through the call providing null for the set of SqlParameters\r\n            return ExecuteDataset(connection, commandType, commandText, (SqlParameter[])null);\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns a resultset) against the specified SqlConnection \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  DataSet ds = ExecuteDataset(conn, CommandType.StoredProcedure, \"GetOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connection\">A valid SqlConnection</param>\r\n        /// <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">The stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">An array of SqlParamters used to execute the command</param>\r\n        /// <returns>A dataset containing the resultset generated by the command</returns>\r\n        public static DataSet ExecuteDataset(SqlConnection connection, CommandType commandType, string commandText, params SqlParameter[] commandParameters)\r\n        {\r\n            if (connection == null) throw new ArgumentNullException(\"connection\");\r\n\r\n            // Create a command and prepare it for execution\r\n            SqlCommand cmd = new SqlCommand();\r\n            bool mustCloseConnection = false;\r\n            //PrepareCommand(cmd, connection, (SqlTransaction)null, commandType, commandText, commandParameters, out mustCloseConnection);\r\n            PrepareCommand(cmd, connection, (SqlTransaction)null, commandType, commandText, commandParameters);\r\n            // Create the DataAdapter & DataSet\r\n            using (SqlDataAdapter da = new SqlDataAdapter(cmd))\r\n            {\r\n                DataSet ds = new DataSet();\r\n\r\n                // Fill the DataSet using default values for DataTable names, etc\r\n                da.Fill(ds);\r\n\r\n                // Detach the SqlParameters from the command object, so they can be used again\r\n                cmd.Parameters.Clear();\r\n\r\n                if (mustCloseConnection)\r\n                    connection.Close();\r\n\r\n                // Return the dataset\r\n                return ds;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns a resultset and takes no parameters) against the provided SqlTransaction. \r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  DataSet ds = ExecuteDataset(trans, CommandType.StoredProcedure, \"GetOrders\");\r\n        /// </remarks>\r\n        /// <param name=\"transaction\">A valid SqlTransaction</param>\r\n        /// <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">The stored procedure name or T-SQL command</param>\r\n        /// <returns>A dataset containing the resultset generated by the command</returns>\r\n        public static DataSet ExecuteDataset(SqlTransaction transaction, CommandType commandType, string commandText)\r\n        {\r\n            // Pass through the call providing null for the set of SqlParameters\r\n            return ExecuteDataset(transaction, commandType, commandText, (SqlParameter[])null);\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand (that returns a resultset) against the specified SqlTransaction\r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  DataSet ds = ExecuteDataset(trans, CommandType.StoredProcedure, \"GetOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"transaction\">A valid SqlTransaction</param>\r\n        /// <param name=\"commandType\">The CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">The stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">An array of SqlParamters used to execute the command</param>\r\n        /// <returns>A dataset containing the resultset generated by the command</returns>\r\n        public static DataSet ExecuteDataset(SqlTransaction transaction, CommandType commandType, string commandText, params SqlParameter[] commandParameters)\r\n        {\r\n            if (transaction == null) throw new ArgumentNullException(\"transaction\");\r\n            if (transaction != null && transaction.Connection == null) throw new ArgumentException(\"The transaction was rollbacked or commited, please provide an open transaction.\", \"transaction\");\r\n\r\n            // Create a command and prepare it for execution\r\n            SqlCommand cmd = new SqlCommand();\r\n            //bool mustCloseConnection = false;\r\n            //PrepareCommand(cmd, transaction.Connection, transaction, commandType, commandText, commandParameters, out mustCloseConnection);\r\n            PrepareCommand(cmd, transaction.Connection, transaction, commandType, commandText, commandParameters);\r\n\r\n            // Create the DataAdapter & DataSet\r\n            using (SqlDataAdapter da = new SqlDataAdapter(cmd))\r\n            {\r\n                DataSet ds = new DataSet();\r\n\r\n                // Fill the DataSet using default values for DataTable names, etc\r\n                da.Fill(ds);\r\n\r\n                // Detach the SqlParameters from the command object, so they can be used again\r\n                cmd.Parameters.Clear();\r\n\r\n                // Return the dataset\r\n                return ds;\r\n            }\r\n        }\r\n\r\n        #endregion ExecuteDataset\r\n\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand that returns the first column of the first record against the database specified in the connection string \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">a valid connection string for a SqlConnection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of SqlParamters used to execute the command</param>\r\n        /// <returns>An object that should be converted to the expected type using Convert.To{Type}</returns>\r\n        public static object ExecuteScalar(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            using (SqlConnection connection = new SqlConnection(connectionString))\r\n            {\r\n                PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n                object val = cmd.ExecuteScalar();\r\n                cmd.Parameters.Clear();\r\n                connection.Close();\r\n                return val;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand that returns the first column of the first record against an existing database connection \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  Object obj = ExecuteScalar(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"conn\">an existing database connection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of SqlParamters used to execute the command</param>\r\n        /// <returns>An object that should be converted to the expected type using Convert.To{Type}</returns>\r\n        public static object ExecuteScalar(SqlTransaction trans, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            PrepareCommand(cmd, trans.Connection, trans, cmdType, cmdText, commandParameters);\r\n            object val = cmd.ExecuteScalar();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        public static object ExecuteScalar(SqlConnection connection, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n\r\n            SqlCommand cmd = new SqlCommand();\r\n\r\n            PrepareCommand(cmd, connection, null, cmdType, cmdText, commandParameters);\r\n            object val = cmd.ExecuteScalar();\r\n            cmd.Parameters.Clear();\r\n            return val;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Execute a SqlCommand that returns a resultset against the database specified in the connection string \r\n        /// using the provided parameters.\r\n        /// </summary>\r\n        /// <remarks>\r\n        /// e.g.:  \r\n        ///  SqlDataReader r = ExecuteReader(connString, CommandType.StoredProcedure, \"PublishOrders\", new SqlParameter(\"@prodid\", 24));\r\n        /// </remarks>\r\n        /// <param name=\"connectionString\">a valid connection string for a SqlConnection</param>\r\n        /// <param name=\"commandType\">the CommandType (stored procedure, text, etc.)</param>\r\n        /// <param name=\"commandText\">the stored procedure name or T-SQL command</param>\r\n        /// <param name=\"commandParameters\">an array of SqlParamters used to execute the command</param>\r\n        /// <returns>A SqlDataReader containing the results</returns>\r\n        public static SqlDataReader ExecuteReader(string connectionString, CommandType cmdType, string cmdText, params SqlParameter[] commandParameters)\r\n        {\r\n            SqlCommand cmd = new SqlCommand();\r\n            SqlConnection conn = new SqlConnection(connectionString);\r\n\r\n            // we use a try/catch here because if the method throws an exception we want to \r\n            // close the connection throw code, because no datareader will exist, hence the \r\n            // commandBehaviour.CloseConnection will not work\r\n            try\r\n            {\r\n                PrepareCommand(cmd, conn, null, cmdType, cmdText, commandParameters);\r\n                SqlDataReader rdr = cmd.ExecuteReader(CommandBehavior.CloseConnection);\r\n                cmd.Parameters.Clear();\r\n                return rdr;\r\n            }\r\n            catch\r\n            {\r\n                conn.Close();\r\n                throw;\r\n            }\r\n        }\r\n\r\n        //for (int i = 0; i < cmd.Parameters.Count; i++)\r\n        //{\r\n        //    string name = cmd.Parameters[i].ParameterName;\r\n        //    string value = cmd.Parameters[i].Value.ToString();\r\n        //}\r\n        //新增函数 开始\r\n  public static int GetMaxID(string FieldName, string TableName)\r\n        {\r\n            string strsql = \"select max(\" + FieldName + \")+1 from \" + TableName;\r\n            object obj = GetSingle(strsql);\r\n            if (obj == null)\r\n            {\r\n                return 1;\r\n            }\r\n            else\r\n            {\r\n                return int.Parse(obj.ToString());\r\n            }\r\n        }\r\n        public static bool Exists(string strSql)\r\n        {\r\n            object obj = GetSingle(strSql);\r\n            int cmdresult;\r\n            if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n            {\r\n                cmdresult = 0;\r\n            }\r\n            else\r\n            {\r\n                cmdresult = int.Parse(obj.ToString());\r\n            }\r\n            if (cmdresult == 0)\r\n            {\r\n                return false;\r\n            }\r\n            else\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 表是否存在\r\n        /// </summary>\r\n        /// <param name=\"TableName\"></param>\r\n        /// <returns></returns>\r\n        public static bool TabExists(string TableName)\r\n        {\r\n            string strsql = \"select count(*) from sysobjects where id = object_id(N\'[\" + TableName + \"]\') and OBJECTPROPERTY(id, N\'IsUserTable\') = 1\";\r\n            //string strsql = \"SELECT count(*) FROM sys.objects WHERE object_id = OBJECT_ID(N\'[dbo].[\" + TableName + \"]\') AND type in (N\'U\')\";\r\n            object obj = GetSingle(strsql);\r\n            int cmdresult;\r\n            if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n            {\r\n                cmdresult = 0;\r\n            }\r\n            else\r\n            {\r\n                cmdresult = int.Parse(obj.ToString());\r\n            }\r\n            if (cmdresult == 0)\r\n            {\r\n                return false;\r\n            }\r\n            else\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n        public static bool Exists(string strSql, params SqlParameter[] cmdParms)\r\n        {\r\n            object obj = GetSingle(strSql, cmdParms);\r\n            int cmdresult;\r\n            if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n            {\r\n                cmdresult = 0;\r\n            }\r\n            else\r\n            {\r\n                cmdresult = int.Parse(obj.ToString());\r\n            }\r\n            if (cmdresult == 0)\r\n            {\r\n                return false;\r\n            }\r\n            else\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n       \r\n\r\n        #region  执行简单SQL语句\r\n\r\n        /// <summary>\r\n        /// 执行SQL语句，返回影响的记录数\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL语句</param>\r\n        /// <returns>影响的记录数</returns>\r\n        public static int ExecuteSql(string SQLString)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                using (SqlCommand cmd = new SqlCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException e)\r\n                    {\r\n                        connection.Close();\r\n                        throw e;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n        public static int ExecuteSqlByTime(string SQLString, int Times)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                using (SqlCommand cmd = new SqlCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        cmd.CommandTimeout = Times;\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException e)\r\n                    {\r\n                        connection.Close();\r\n                        throw e;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n      \r\n    \r\n       \r\n       \r\n        /// <summary>\r\n        /// 执行带一个存储过程参数的的SQL语句。\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL语句</param>\r\n        /// <param name=\"content\">参数内容,比如一个字段是格式复杂的文章，有特殊符号，可以通过这个方式添加</param>\r\n        /// <returns>影响的记录数</returns>\r\n        public static object ExecuteSqlGet(string SQLString, string content)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                SqlCommand cmd = new SqlCommand(SQLString, connection);\r\n                System.Data.SqlClient.SqlParameter myParameter = new System.Data.SqlClient.SqlParameter(\"@content\", SqlDbType.NText);\r\n                myParameter.Value = content;\r\n                cmd.Parameters.Add(myParameter);\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    object obj = cmd.ExecuteScalar();\r\n                    if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                    {\r\n                        return null;\r\n                    }\r\n                    else\r\n                    {\r\n                        return obj;\r\n                    }\r\n                }\r\n                catch (System.Data.SqlClient.SqlException e)\r\n                {\r\n                    throw e;\r\n                }\r\n                finally\r\n                {\r\n                    cmd.Dispose();\r\n                    connection.Close();\r\n                }\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 向数据库里插入图像格式的字段(和上面情况类似的另一种实例)\r\n        /// </summary>\r\n        /// <param name=\"strSQL\">SQL语句</param>\r\n        /// <param name=\"fs\">图像字节,数据库的字段类型为image的情况</param>\r\n        /// <returns>影响的记录数</returns>\r\n        public static int ExecuteSqlInsertImg(string strSQL, byte[] fs)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                SqlCommand cmd = new SqlCommand(strSQL, connection);\r\n                System.Data.SqlClient.SqlParameter myParameter = new System.Data.SqlClient.SqlParameter(\"@fs\", SqlDbType.Image);\r\n                myParameter.Value = fs;\r\n                cmd.Parameters.Add(myParameter);\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    int rows = cmd.ExecuteNonQuery();\r\n                    return rows;\r\n                }\r\n                catch (System.Data.SqlClient.SqlException e)\r\n                {\r\n                    throw e;\r\n                }\r\n                finally\r\n                {\r\n                    cmd.Dispose();\r\n                    connection.Close();\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 执行一条计算查询结果语句，返回查询结果（object）。\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">计算查询结果语句</param>\r\n        /// <returns>查询结果（object）</returns>\r\n        public static object GetSingle(string SQLString)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                using (SqlCommand cmd = new SqlCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        object obj = cmd.ExecuteScalar();\r\n                        if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                        {\r\n                            return null;\r\n                        }\r\n                        else\r\n                        {\r\n                            return obj;\r\n                        }\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException e)\r\n                    {\r\n                        connection.Close();\r\n                        throw e;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n        public static object GetSingle(string SQLString, int Times)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                using (SqlCommand cmd = new SqlCommand(SQLString, connection))\r\n                {\r\n                    try\r\n                    {\r\n                        connection.Open();\r\n                        cmd.CommandTimeout = Times;\r\n                        object obj = cmd.ExecuteScalar();\r\n                        if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                        {\r\n                            return null;\r\n                        }\r\n                        else\r\n                        {\r\n                            return obj;\r\n                        }\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException e)\r\n                    {\r\n                        connection.Close();\r\n                        throw e;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n \r\n        /// <summary>\r\n        /// 执行查询语句，返回DataSet\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">查询语句</param>\r\n        /// <returns>DataSet</returns>\r\n        public static DataSet Query(string SQLString)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                DataSet ds = new DataSet();\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    SqlDataAdapter command = new SqlDataAdapter(SQLString, connection);\r\n                    command.Fill(ds, \"ds\");\r\n                }\r\n                catch (System.Data.SqlClient.SqlException ex)\r\n                {\r\n                    throw new Exception(ex.Message);\r\n                }\r\n                return ds;\r\n            }\r\n        }\r\n        public static DataSet Query(string SQLString, int Times)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                DataSet ds = new DataSet();\r\n                try\r\n                {\r\n                    connection.Open();\r\n                    SqlDataAdapter command = new SqlDataAdapter(SQLString, connection);\r\n                    command.SelectCommand.CommandTimeout = Times;\r\n                    command.Fill(ds, \"ds\");\r\n                }\r\n                catch (System.Data.SqlClient.SqlException ex)\r\n                {\r\n                    throw new Exception(ex.Message);\r\n                }\r\n                return ds;\r\n            }\r\n        }\r\n\r\n\r\n\r\n        #endregion\r\n\r\n        #region 执行带参数的SQL语句\r\n\r\n        /// <summary>\r\n        /// 执行SQL语句，返回影响的记录数\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">SQL语句</param>\r\n        /// <returns>影响的记录数</returns>\r\n        public static int ExecuteSql(string SQLString, params SqlParameter[] cmdParms)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                using (SqlCommand cmd = new SqlCommand())\r\n                {\r\n                    try\r\n                    {\r\n                        PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                        int rows = cmd.ExecuteNonQuery();\r\n                        cmd.Parameters.Clear();\r\n                        return rows;\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException e)\r\n                    {\r\n                        throw e;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 执行多条SQL语句，实现数据库事务。\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">SQL语句的哈希表（key为sql语句，value是该语句的SqlParameter[]）</param>\r\n        public static void ExecuteSqlTran(Hashtable SQLStringList)\r\n        {\r\n            using (SqlConnection conn = new SqlConnection(conString))\r\n            {\r\n                conn.Open();\r\n                using (SqlTransaction trans = conn.BeginTransaction())\r\n                {\r\n                    SqlCommand cmd = new SqlCommand();\r\n                    try\r\n                    {\r\n                        //循环\r\n                        foreach (DictionaryEntry myDE in SQLStringList)\r\n                        {\r\n                            string cmdText = myDE.Key.ToString();\r\n                            SqlParameter[] cmdParms = (SqlParameter[])myDE.Value;\r\n                            PrepareCommand(cmd, conn, trans, cmdText, cmdParms);\r\n                            int val = cmd.ExecuteNonQuery();\r\n                            cmd.Parameters.Clear();\r\n                        }\r\n                        trans.Commit();\r\n                    }\r\n                    catch\r\n                    {\r\n                        trans.Rollback();\r\n                        throw;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n       \r\n       \r\n        /// <summary>\r\n        /// 执行多条SQL语句，实现数据库事务。\r\n        /// </summary>\r\n        /// <param name=\"SQLStringList\">SQL语句的哈希表（key为sql语句，value是该语句的SqlParameter[]）</param>\r\n        public static void ExecuteSqlTranWithIndentity(Hashtable SQLStringList)\r\n        {\r\n            using (SqlConnection conn = new SqlConnection(conString))\r\n            {\r\n                conn.Open();\r\n                using (SqlTransaction trans = conn.BeginTransaction())\r\n                {\r\n                    SqlCommand cmd = new SqlCommand();\r\n                    try\r\n                    {\r\n                        int indentity = 0;\r\n                        //循环\r\n                        foreach (DictionaryEntry myDE in SQLStringList)\r\n                        {\r\n                            string cmdText = myDE.Key.ToString();\r\n                            SqlParameter[] cmdParms = (SqlParameter[])myDE.Value;\r\n                            foreach (SqlParameter q in cmdParms)\r\n                            {\r\n                                if (q.Direction == ParameterDirection.InputOutput)\r\n                                {\r\n                                    q.Value = indentity;\r\n                                }\r\n                            }\r\n                            PrepareCommand(cmd, conn, trans, cmdText, cmdParms);\r\n                            int val = cmd.ExecuteNonQuery();\r\n                            foreach (SqlParameter q in cmdParms)\r\n                            {\r\n                                if (q.Direction == ParameterDirection.Output)\r\n                                {\r\n                                    indentity = Convert.ToInt32(q.Value);\r\n                                }\r\n                            }\r\n                            cmd.Parameters.Clear();\r\n                        }\r\n                        trans.Commit();\r\n                    }\r\n                    catch\r\n                    {\r\n                        trans.Rollback();\r\n                        throw;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 执行一条计算查询结果语句，返回查询结果（object）。\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">计算查询结果语句</param>\r\n        /// <returns>查询结果（object）</returns>\r\n        public static object GetSingle(string SQLString, params SqlParameter[] cmdParms)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                using (SqlCommand cmd = new SqlCommand())\r\n                {\r\n                    try\r\n                    {\r\n                        PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                        object obj = cmd.ExecuteScalar();\r\n                        cmd.Parameters.Clear();\r\n                        if ((Object.Equals(obj, null)) || (Object.Equals(obj, System.DBNull.Value)))\r\n                        {\r\n                            return null;\r\n                        }\r\n                        else\r\n                        {\r\n                            return obj;\r\n                        }\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException e)\r\n                    {\r\n                        throw e;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n\r\n      \r\n        /// <summary>\r\n        /// 执行查询语句，返回DataSet\r\n        /// </summary>\r\n        /// <param name=\"SQLString\">查询语句</param>\r\n        /// <returns>DataSet</returns>\r\n        public static DataSet Query(string SQLString, params SqlParameter[] cmdParms)\r\n        {\r\n            using (SqlConnection connection = new SqlConnection(conString))\r\n            {\r\n                SqlCommand cmd = new SqlCommand();\r\n                PrepareCommand(cmd, connection, null, SQLString, cmdParms);\r\n                using (SqlDataAdapter da = new SqlDataAdapter(cmd))\r\n                {\r\n                    DataSet ds = new DataSet();\r\n                    try\r\n                    {\r\n                        da.Fill(ds, \"ds\");\r\n                        cmd.Parameters.Clear();\r\n                    }\r\n                    catch (System.Data.SqlClient.SqlException ex)\r\n                    {\r\n                        throw new Exception(ex.Message);\r\n                    }\r\n                    return ds;\r\n                }\r\n            }\r\n        }\r\n\r\n\r\n        private static void PrepareCommand(SqlCommand cmd, SqlConnection conn, SqlTransaction trans, string cmdText, SqlParameter[] cmdParms)\r\n        {\r\n            if (conn.State != ConnectionState.Open)\r\n                conn.Open();\r\n            cmd.Connection = conn;\r\n            cmd.CommandText = cmdText;\r\n            if (trans != null)\r\n                cmd.Transaction = trans;\r\n            cmd.CommandType = CommandType.Text;//cmdType;\r\n            if (cmdParms != null)\r\n            {\r\n\r\n\r\n                foreach (SqlParameter parameter in cmdParms)\r\n                {\r\n                    if ((parameter.Direction == ParameterDirection.InputOutput || parameter.Direction == ParameterDirection.Input) &&\r\n                        (parameter.Value == null))\r\n                    {\r\n                        parameter.Value = DBNull.Value;\r\n                    }\r\n                    cmd.Parameters.Add(parameter);\r\n                }\r\n            }\r\n        }\r\n\r\n        //新增函数 结束\r\n    }\r\n        #endregion\r\n}\r\n', '0', '2017-09-08 09:49:12', null, 'SqlHelper.united.cs.txt');
INSERT INTO `txt` VALUES ('738', '2', 'SqlMapperUtil.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.ComponentModel;\r\nusing System.Configuration;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing System.Linq;\r\nusing System.Reflection;\r\nusing System.Text;\r\nusing Dapper;\r\n\r\n\r\nnamespace Dapper\r\n{\r\n    public static class SqlMapperUtil\r\n    {\r\n        // Remember to add <remove name=\"LocalSqlServer\" > in ConnectionStrings section if using this, as otherwise it would be the first one.\r\n        private static string connectionString = @\"server=(local);database=CV;uid=sa;pwd=sa\";\r\n        /// <summary>\r\n        /// Gets the open connection.\r\n        /// </summary>\r\n        /// <param name=\"name\">The name of the connection string (optional).</param>\r\n        /// <returns></returns>\r\n        public static SqlConnection GetOpenConnection(string name = null)\r\n        {\r\n            // string connString = \"\";\r\n            //  connString = name == null ? connString = ConfigurationManager.ConnectionStrings[0].ConnectionString : connString = ConfigurationManager.ConnectionStrings[name].ConnectionString;\r\n            string conStr = name == null ? connectionString : name;\r\n            var connection = new SqlConnection(conStr);\r\n            connection.Open();\r\n            return connection;\r\n        }\r\n\r\n\r\n        public static int InsertMultiple<T>(string sql, IEnumerable<T> entities, string connectionName = null) where T : class, new()\r\n        {\r\n            using (SqlConnection cnn = GetOpenConnection(connectionName))\r\n            {\r\n                int records = 0;\r\n\r\n                foreach (T entity in entities)\r\n                {\r\n                    records += cnn.Execute(sql, entity);\r\n                }\r\n                return records;\r\n            }\r\n        }\r\n\r\n        public static DataTable ToDataTable<T>(this IList<T> list)\r\n        {\r\n            PropertyDescriptorCollection props = TypeDescriptor.GetProperties(typeof(T));\r\n            DataTable table = new DataTable();\r\n            for (int i = 0; i < props.Count; i++)\r\n            {\r\n                PropertyDescriptor prop = props[i];\r\n                table.Columns.Add(prop.Name, Nullable.GetUnderlyingType(prop.PropertyType) ?? prop.PropertyType);\r\n            }\r\n            object[] values = new object[props.Count];\r\n            foreach (T item in list)\r\n            {\r\n                for (int i = 0; i < values.Length; i++)\r\n                    values[i] = props[i].GetValue(item) ?? DBNull.Value;\r\n                table.Rows.Add(values);\r\n            }\r\n            return table;\r\n        }\r\n\r\n        public static DynamicParameters GetParametersFromObject(object obj, string[] propertyNamesToIgnore)\r\n        {\r\n            if (propertyNamesToIgnore == null) propertyNamesToIgnore = new string[] { String.Empty };\r\n            DynamicParameters p = new DynamicParameters();\r\n            PropertyInfo[] properties = obj.GetType().GetProperties(BindingFlags.Public | BindingFlags.Instance);\r\n\r\n            foreach (PropertyInfo prop in properties)\r\n            {\r\n                if (!propertyNamesToIgnore.Contains(prop.Name))\r\n                    p.Add(\"@\" + prop.Name, prop.GetValue(obj, null));\r\n            }\r\n            return p;\r\n        }\r\n\r\n        public static void SetIdentity<T>(IDbConnection connection, Action<T> setId)\r\n        {\r\n            dynamic identity = connection.Query(\"SELECT @@IDENTITY AS Id\").Single();\r\n            T newId = (T)identity.Id;\r\n            setId(newId);\r\n        }\r\n\r\n\r\n        public static object GetPropertyValue(object target, string propertyName)\r\n        {\r\n            PropertyInfo[] properties = target.GetType().GetProperties(BindingFlags.Public | BindingFlags.Instance);\r\n\r\n            object theValue = null;\r\n            foreach (PropertyInfo prop in properties)\r\n            {\r\n                if (string.Compare(prop.Name, propertyName, true) == 0)\r\n                {\r\n                    theValue = prop.GetValue(target, null);\r\n                }\r\n            }\r\n            return theValue;\r\n        }\r\n\r\n        public static void SetPropertyValue(object p, string propName, object value)\r\n        {\r\n            Type t = p.GetType();\r\n            PropertyInfo info = t.GetProperty(propName);\r\n            if (info == null)\r\n                return;\r\n            if (!info.CanWrite)\r\n                return;\r\n            info.SetValue(p, value, null);\r\n        }\r\n\r\n        /// <summary>\r\n        /// Stored proc.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"procname\">The procname.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <returns></returns>\r\n        public static List<T> StoredProcWithParams<T>(string procname, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Query<T>(procname, (object)parms, commandType: CommandType.StoredProcedure).ToList();\r\n            }\r\n\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// Stored proc with params returning dynamic.\r\n        /// </summary>\r\n        /// <param name=\"procname\">The procname.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <param name=\"connectionName\">Name of the connection.</param>\r\n        /// <returns></returns>\r\n        public static List<dynamic> StoredProcWithParamsDynamic(string procname, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Query(procname, (object)parms, commandType: CommandType.StoredProcedure).ToList();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Stored proc insert with ID.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\">The type of object</typeparam>\r\n        /// <typeparam name=\"U\">The Type of the ID</typeparam>\r\n        /// <param name=\"procName\">Name of the proc.</param>\r\n        /// <param name=\"parms\">instance of DynamicParameters class. This should include a defined output parameter</param>\r\n        /// <returns>U - the @@Identity value from output parameter</returns>\r\n        public static U StoredProcInsertWithID<T, U>(string procName, DynamicParameters parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = SqlMapperUtil.GetOpenConnection(connectionName))\r\n            {\r\n                var x = connection.Execute(procName, (object)parms, commandType: CommandType.StoredProcedure);\r\n                return parms.Get<U>(\"@ID\");\r\n            }\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// SQL with params.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"sql\">The SQL.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <returns></returns>\r\n        public static List<T> SqlWithParams<T>(string sql, dynamic parms, string connectionnName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionnName))\r\n            {\r\n                return connection.Query<T>(sql, (object)parms).ToList();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Insert update or delete SQL.\r\n        /// </summary>\r\n        /// <param name=\"sql\">The SQL.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <returns></returns>\r\n        public static int InsertUpdateOrDeleteSql(string sql, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Execute(sql, (object)parms);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// Insert update or delete stored proc.\r\n        /// </summary>\r\n        /// <param name=\"procName\">Name of the proc.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <returns></returns>\r\n        public static int InsertUpdateOrDeleteStoredProc(string procName, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Execute(procName, (object)parms, commandType: CommandType.StoredProcedure);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// SQLs the with params single.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"sql\">The SQL.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <param name=\"connectionName\">Name of the connection.</param>\r\n        /// <returns></returns>\r\n        public static T SqlWithParamsSingle<T>(string sql, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Query<T>(sql, (object)parms).FirstOrDefault();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        ///  proc with params single returning Dynamic object.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"sql\">The SQL.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <param name=\"connectionName\">Name of the connection.</param>\r\n        /// <returns></returns>\r\n        public static System.Dynamic.DynamicObject DynamicProcWithParamsSingle<T>(string sql, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Query(sql, (object)parms, commandType: CommandType.StoredProcedure).FirstOrDefault();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// proc with params returning Dynamic.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"sql\">The SQL.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <param name=\"connectionName\">Name of the connection.</param>\r\n        /// <returns></returns>\r\n        public static IEnumerable<dynamic> DynamicProcWithParams<T>(string sql, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Query(sql, (object)parms, commandType: CommandType.StoredProcedure);\r\n            }\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// Stored proc with params returning single.\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"procname\">The procname.</param>\r\n        /// <param name=\"parms\">The parms.</param>\r\n        /// <param name=\"connectionName\">Name of the connection.</param>\r\n        /// <returns></returns>\r\n        public static T StoredProcWithParamsSingle<T>(string procname, dynamic parms, string connectionName = null)\r\n        {\r\n            using (SqlConnection connection = GetOpenConnection(connectionName))\r\n            {\r\n                return connection.Query<T>(procname, (object)parms, commandType: CommandType.StoredProcedure).SingleOrDefault();\r\n            }\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:13', null, 'SqlMap