INSERT INTO `txt` VALUES ('739', '2', 'SqlParameter.param.参数.c#.asp.net.ado.net.txt', 'StringBuilder strSql = new StringBuilder();\r\n                        strSql.Append(\"insert into Article(\");\r\n                        strSql.Append(\"UserID,Title,ArticleContent,Author,CreateTime,CreateBy,Mark)\");\r\n                        strSql.Append(\" values (\");\r\n                        strSql.Append(\"@UserID,@Title,@ArticleContent,@Author,@CreateTime,@CreateBy,@Mark)\");\r\n                        strSql.Append(\";select @@IDENTITY\");\r\n                        SqlParameter[] parameters = {\r\n                        new SqlParameter(\"@UserID\", SqlDbType.Int,4),\r\n                        new SqlParameter(\"@Title\", SqlDbType.NVarChar,100),\r\n                        new SqlParameter(\"@ArticleContent\", SqlDbType.Text),\r\n                        new SqlParameter(\"@Author\", SqlDbType.NVarChar,20),\r\n                        new SqlParameter(\"@CreateTime\", SqlDbType.DateTime),\r\n                        new SqlParameter(\"@CreateBy\", SqlDbType.NVarChar,20),\r\n                        new SqlParameter(\"@Mark\",SqlDbType.NVarChar,20)};\r\n                        parameters[0].Value = model.UserID;\r\n                        parameters[1].Value = model.Title;\r\n                        parameters[2].Value = model.ArticleContent;\r\n                        parameters[3].Value = model.Author;\r\n                        parameters[4].Value = model.CreateTime;\r\n                        parameters[5].Value = model.CreateBy;\r\n                        parameters[6].Value = model.Mark;\r\n\r\nbject obj = SqlDBHelper.GetSingle(strSql.ToString(), parameters);\r\n', '0', '2017-09-08 09:49:13', null, 'SqlParameter.param.参数.c#.asp.net.ado.net.txt');
INSERT INTO `txt` VALUES ('740', '2', 'sqlserver. 无法重命名数据库名称.无法用排他锁锁定该数据库，以执行该操作。 (Microsoft SQL Server，错误 5030) .txt', '\r\n无法用排他锁锁定该数据库，以执行该操作。 (Microsoft SQL Server，错误: 5030)  \r\n\r\n\r\n\r\n主要还是确保，没有网站、服务等在使用数据库。\r\n\r\n解决方案\r\n\r\n     Use master \r\n      EXEC sp_dboption \'OldDbName\', \'Single User\', \'TRUE\' \r\n      EXEC sp_renamedb \'OldDbName\', \'NewDbName\' \r\n      EXEC sp_dboption \'NewDbName\', \'Single User\', \'FALSE\' \r\n      GO\r\n\r\n在有外来用户访问数据库的情况下执行\r\n\r\n  EXEC sp_dboption \'OldDbName\', \'Single User\', \'TRUE\' \r\n      EXEC sp_dboption \'NewDbName\', \'Single User\', \'FALSE\' \r\n      这两条语句的意思是将数据库转换为单用户模式和非单用户模式，这样可以避免在重命名数据库的时候有其它用户对数据库进行操作，造成死锁。 \r\n\r\n\r\n可以查询有没有人访问\r\n\r\nselect spid \r\n      from master.dbo.sysprocesses \r\n      where dbid=db_id(\'OldDbName\') \r\n\r\n 结果集中显示的是当前连接到数据库 OldDbName 的连接，比如结果是  \r\n      34\r\n\r\n      67\r\n      当然啦，实际值应该不是这两个。然后执行  \r\n      kill 34\r\n\r\n      kill 67\r\n      关闭已建立的连接，再执行上面的三行代码就可以完成数据库的重命名。\r\n', '0', '2017-09-08 09:49:13', null, 'sqlserver. 无法重命名数据库名称.无法用排他锁锁定该数据库，以执行该操作。 (Microsoft SQL Server，错误 5030) .txt');
