INSERT INTO `txt` VALUES ('748', '2', 'SqlServer.通过脚本创建.作业.job.txt', 'ͨ���ű�����JOB��  \r\nif   exists   (select   *   from   dbo.sysobjects   where   id   =   object_id(N\'[dbo].[p_createjob]\')   and   OBJECTPROPERTY(id,   N\'IsProcedure\')   =   1)  \r\n  drop   procedure   [dbo].[p_createjob]  \r\n  GO  \r\n   \r\n  create   proc   p_createjob  \r\n  @jobname   varchar(100),                           --��ҵ����  \r\n  @sql   varchar(8000),                                 --Ҫִ�е�����  \r\n  @serverName   sysname=\'\',                         --job   server��  \r\n  @dbname   sysname=\'\',                                 --Ĭ��Ϊ��ǰ�����ݿ���  \r\n  @freqtype   varchar(6)=\'day\',                 --ʱ������,month   ��,week   ��,day   ��  \r\n  @fsinterval   int=1,                                   --�����ÿ�յ��ظ�����  \r\n  @time   int=170000                                       --��ʼִ��ʱ��,�����ظ�ִ�е���ҵ,����0�㵽23:59��  \r\n  as  \r\n  if   isnull(@dbname,\'\')=\'\'   set   @dbname=db_name()  \r\n   \r\n  --������ҵ  \r\n  exec   msdb..sp_add_job   @job_name=@jobname  \r\n   \r\n  --������ҵ����  \r\n  exec   msdb..sp_add_jobstep   @job_name=@jobname,  \r\n                  @step_name   =   \'���ݴ���\',  \r\n                  @subsystem   =   \'TSQL\',  \r\n                  @database_name=@dbname,  \r\n                  @command   =   @sql,  \r\n                  @retry_attempts   =   5,   --���Դ���  \r\n                  @retry_interval   =   5     --���Լ��  \r\n   \r\n  --��������  \r\n  declare   @ftype   int,@fstype   int,@ffactor   int  \r\n  select   @ftype=case   @freqtype   when   \'day\'   then   4  \r\n                                                                                  when   \'week\'   then   8  \r\n                                                                                  when   \'month\'   then   16   end  \r\n                  ,@fstype=case   @fsinterval   when   1   then   0   else   8   end  \r\n  if   @fsinterval<>1   set   @time=0  \r\n  set   @ffactor=case   @freqtype   when   \'day\'   then   0   else   1   end  \r\n   \r\n  EXEC   msdb..sp_add_jobschedule   @job_name=@jobname,    \r\n                  @name   =   \'ʱ�䰲��\',  \r\n                  @freq_type=@ftype   ,                                                 --ÿ��,8   ÿ��,16   ÿ��  \r\n                  @freq_interval=1,                                                     --�ظ�ִ�д���  \r\n                  @freq_subday_type=@fstype,                                   --�Ƿ��ظ�ִ��  \r\n                  @freq_subday_interval=@fsinterval,                   --�ظ�����  \r\n                  @freq_recurrence_factor=@ffactor,  \r\n                  @active_start_time=@time                                       --����17:00:00��ִ��  \r\n   \r\n  if   @servername=\'\'  \r\n  set   @servername=@@servername  \r\n  EXEC   msdb..sp_add_jobserver   @job_name   =   @jobname,    \r\n            @server_name   =   @servername  \r\n   \r\n  go    \r\n   \r\n  --����  \r\n  --ÿ��ִ�е���ҵ  \r\n  exec   p_createjob   @jobname=\'dd\'  \r\n                                    ,@sql=\'insert   B   select   convert(char(10),getdate(),120),1+(select   max([text])   from   B)\'  \r\n                                    ,@servername=\'job��������\'  \r\n                                    ,@dbname=\'���ݿ���\'  \r\n                                    ,@freqtype=\'day\'  \r\n                                    ,@time=\'000000\' \r\n\r\n\r\n===============================================================\r\n\r\n��������һ���汾�����Բ���԰\r\nhttp://www.cnblogs.com/datasky/archive/2006/12/28/606354.html\r\n\r\n--ÿ��ִ�е���ҵ\r\nexec p_createjob @jobname=\'mm\',@sql=\'select * from syscolumns\',@freqtype=\'month\'\r\n--ÿ��ִ�е���ҵ\r\nexec p_createjob @jobname=\'ww\',@sql=\'select * from syscolumns\',@freqtype=\'week\'\r\n--ÿ��ִ�е���ҵ\r\nexec p_createjob @jobname=\'a\',@sql=\'select * from syscolumns\'\r\n--ÿ��ִ�е���ҵ,ÿ���4Сʱ�ظ�����ҵ\r\nexec p_createjob @jobname=\'b\',@sql=\'select * from syscolumns\',@fsinterval=4\r\n--*/\r\nif exists (select * from dbo.sysobjects where id = object_id(N\'[dbo].[p_createjob]\') and OBJECTPROPERTY(id, N\'IsProcedure\') = 1)\r\ndrop procedure [dbo].[p_createjob]\r\nGO\r\ncreate proc p_createjob\r\n@jobname varchar(100),--��ҵ����\r\n@sql varchar(8000),--Ҫִ�е�����\r\n@dbname sysname=\'\',--Ĭ��Ϊ��ǰ�����ݿ���\r\n@freqtype varchar(6)=\'day\',--ʱ������,month ��,week ��,day ��\r\n@fsinterval int=1,--�����ÿ�յ��ظ�����\r\n@time int=170000--��ʼִ��ʱ��,�����ظ�ִ�е���ҵ,����0�㵽23:59��\r\nas\r\nif isnull(@dbname,\'\')=\'\' set @dbname=db_name()\r\n--������ҵ\r\nexec msdb..sp_add_job @job_name=@jobname\r\n--������ҵ����\r\nexec msdb..sp_add_jobstep @job_name=@jobname,\r\n@step_name = \'���ݴ���\',\r\n@subsystem = \'TSQL\',\r\n@database_name=@dbname,\r\n@command = @sql,\r\n@retry_attempts = 5, --���Դ���\r\n@retry_interval = 5  --���Լ��\r\n--��������\r\ndeclare @ftype int,@fstype int,@ffactor int\r\nselect @ftype=case @freqtype when \'day\' then 4\r\nwhen \'week\' then 8\r\nwhen \'month\' then 16 end\r\n,@fstype=case @fsinterval when 1 then 0 else 8 end\r\nif @fsinterval<>1 set @time=0\r\nset @ffactor=case @freqtype when \'day\' then 0 else 1 end\r\nEXEC msdb..sp_add_jobschedule @job_name=@jobname,\r\n@name = \'ʱ�䰲��\',\r\n@freq_type=@ftype , --ÿ��,8 ÿ��,16 ÿ��\r\n@freq_interval=1,--�ظ�ִ�д���\r\n@freq_subday_type=@fstype,--�Ƿ��ظ�ִ��\r\n@freq_subday_interval=@fsinterval, --�ظ�����\r\n@freq_recurrence_factor=@ffactor,\r\n@active_start_time=@time  --����17:00:00��ִ��\r\n-- ���Ŀ�������\r\nEXEC msdb.dbo.sp_add_jobserver\r\n@job_name = @jobname ,\r\n@server_name = N\'(local)\'\r\ngo\r\n', '0', '2017-09-08 09:49:13', null, 'SqlServer.通过脚本创建.作业.job.txt');
