INSERT INTO `txt` VALUES ('752', '2', 'sql_java职位候选人&联系记录.txt', 'select  a.ID,a.Source,a.Email,a.ChineseName,a.MobilePhone,a.GENDER,a.LATESTCOMPANY,a.LatestPosition,a.CurrentCity,a.DesiredJobAddress,a.TotalYears,a.SalaryRange,a.Result,a.IsHaveCV,a.Rate\r\n,a.Department1,a.Department2,a.Department3,a.TagIndustry,a.TagIndustry2,a.TagJobType,a.TagJobType2,a.UpdateTime,a.CreateTime,a.UserID,a.createID,em.EmpName,a.updateId,em2.EmpName\r\n,c.CVID,c.Comment,c.ContactDate,c.UpdateTime,c.CreateID,C.CreateName,c.EmpName\r\n,d.TextCn,e.TextCn,f.TextCn,g.TextCn\r\n,o1.OrgName,o2.OrgName,o3.OrgName\r\n,c.CVID,c.Comment,c.ContactDate,c.CreateID,C.CreateName\r\n from CVInformation a  left join CVFunction d on (a.TagIndustry=d.ID)\r\n left join CVFunction e on (a.TagIndustry2=e.ID)\r\n left join CVFunction f on (a.TagJobType=f.ID)\r\n left join CVFunction g on (a.TagJobType2=g.ID)\r\n left join Organization o1 on (a.Department1=o1.OrgID)\r\n left join Organization o2 on (a.Department2=o2.OrgID)\r\n left join Organization o3 on (a.Department3=o3.OrgID)\r\n left join Employees em on (a.createID=em.EmpID)\r\n left join Employees em2 on (a.updateID=em2.EmpID)\r\nOUTER APPLY (select top(1) b.*,e1.EmpName from CVEvent b  left join Employees e1 on b.CreateId=e1.EmpID where a.ID=b.CVID order by ContactDate desc) c \r\nwhere a.UpdateTime >= \'2015-11-01 00:00:00\' and a.LatestPosition like \'%java%\'\r\norder by a.UpdateTime desc\r\n', '0', '2017-09-08 09:49:13', null, 'sql_java职位候选人&联系记录.txt');
