INSERT INTO `txt` VALUES ('754', '2', 'SQL存储过程事务和优化方法（包括查询方式语句结合）.txt', 'CREATE PROCEDURE  IR_rb_User_UpdateUser\r\n(\r\n    @UserID        int,\r\n    @Name    nvarchar(50),\r\n    @Email           nvarchar(100),\r\n    @Password    nvarchar(20),\r\n    @RoleID        int,\r\n    @iSuccess    int output\r\n)\r\nAS\r\n\r\nset @iSuccess = -1    --�ж��Ƿ�ɹ��д���ӦΪ�����ظ���\r\n\r\nIF EXISTS (SELECT Email  FROM rb_Users WHERE Email = @Email and UserID<>@UserID)\r\nBEGIN\r\n  set @iSuccess = -2\r\n  return\r\nEND\r\n\r\nBEGIN TRAN\r\n\r\n    UPDATE    rb_Users  SET    Name   = @Name,    Email    = @Email,    Password = @Password  WHERE    UserID   = @UserID\r\n\r\n    IF @@ERROR <> 0\r\n    BEGIN\r\n        ROLLBACK TRAN\r\n        return\r\n    END\r\n    set @iSuccess = -3\r\n\r\n    update rb_UserRoles set RoleID=@RoleID  where UserID=@UserID\r\n\r\n    IF @@ERROR <> 0\r\n    BEGIN\r\n        ROLLBACK TRAN\r\n        return\r\n    END\r\n\r\nCOMMIT TRAN\r\n\r\nset @iSuccess = 1\r\nGO\r\n\r\n/////////////////////////////////////////////////////////////////////////////\r\nCREATE PROCEDURE IR_SysAuthorization_GetAllSysAuthorization\r\n(\r\n    @Type int,\r\n    @UserID varchar(20)\r\n)\r\n AS\r\n\r\ndeclare     @strsql    varchar(1500)        --��ʱsql���\r\ndeclare     @strwhere    varchar(1000)        --��ʱsql���������\r\n\r\nset @strsql = \'\'\r\nset @strsql = @strsql + \'select UserID,Role,(select typename from SysAuthorizationType where type=t.type) as typeTmp, \'\r\nset @strsql = @strsql + \'(CASE WHEN State = 1 THEN \' + \'\'\'��Ч\'\'\' + \' ELSE \' + \'\'\'��Ч\'\'\' + \' END) AS StateTmp \'\r\nset @strsql = @strsql + \'FROM SysAuthorization t \'\r\n\r\nset @strwhere = \'\'\r\n\r\n\r\nIF  @UserID <> \'\' \r\nBEGIN\r\n    set @strwhere = @strwhere + \' and (UserID LIKE \' + \'\'\'\' + \'%\' + @UserID + \'%\' + \'\'\'\' + \')\'\r\nEND\r\n\r\nIF  @Type >0\r\nBEGIN\r\n    set @strwhere = @strwhere + \' and Type =  \' + CAST(@Type AS varchar(12))\r\nEND\r\n\r\nif @strwhere <>\'\'\r\nBEGIN\r\n    set @strsql = @strsql + \' where \' + right(@strwhere, len(@strwhere)-4)\r\nEND\r\n\r\n--���û�������\r\nset  @strsql = @strsql + \' ORDER BY Type,UserID\'\r\n\r\nexec (@strsql)\r\nGO\r\n', '0', '2017-09-08 09:49:13', null, 'SQL存储过程事务和优化方法（包括查询方式语句结合）.txt');
