INSERT INTO `txt` VALUES ('760', '2', 'sql语句去随机数的方法.txt', '����SQL�����ѯ��䣬[���ݿ�ʹ�ü���]SQL����������������������ʾsql��ѯ����ѯ�����ݼ�¼���е�20�������ȡ���������¼��SQL��䣬ʹ��SQL���ȡ������ķ���\r\n\r\n�������£�\r\n\r\nSQL Server��\r\nSelect TOP N * From TABLE order By NewID()\r\n\r\nAccess��\r\ndim r\r\nrandomize\r\nr = rnd\r\nsql = \"select top 10 * from table order by rnd(\" & r & \"-id)\"\r\nrnd(\" & r & \"-id) ���е�id���Զ�����ֶΣ��������������κ���ֵ����ɣ������������ֶ�(UserName)\r\nselect top 10 * from table order by rnd(\" & r & \"-Len(UserName))\r\n\r\nMySql��\r\nSelect * From TABLE order By Rand() Limit 10\r\n\r\norACLE��\r\nselect *from (select *from t order by dbms_random.value) where rownum<10 \r\n \r\n', '0', '2017-09-08 09:49:13', null, 'sql语句去随机数的方法.txt');
