INSERT INTO `txt` VALUES ('763', '2', 'String.handler.helper.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\nusing System.Drawing;\r\nusing System.Web;\r\nusing System.Text.RegularExpressions;\r\n\r\nnamespace Permission.Lib\r\n{\r\n    public class StringHandle\r\n    {\r\n        public static string GetUniquelyString() //��ȡһ�����ظ����ļ���\r\n        {\r\n            Random rnd = new Random(); //��ȡһ�������\r\n            const int RANDOM_MAX_VALUE = 1000;\r\n            string strTemp, strYear, strMonth, strDay, strHour, strMinute, strSecond, strMillisecond;\r\n            DateTime dt = DateTime.Now;\r\n            int rndNumber = rnd.Next(RANDOM_MAX_VALUE);\r\n            strYear = dt.Year.ToString();\r\n            strMonth = (dt.Month > 9) ? dt.Month.ToString() : \"0\" + dt.Month.ToString();\r\n            strDay = (dt.Day > 9) ? dt.Day.ToString() : \"0\" + dt.Day.ToString();\r\n            strHour = (dt.Hour > 9) ? dt.Hour.ToString() : \"0\" + dt.Hour.ToString();\r\n            strMinute = (dt.Minute > 9) ? dt.Minute.ToString() : \"0\" + dt.Minute.ToString();\r\n            strSecond = (dt.Second > 9) ? dt.Second.ToString() : \"0\" + dt.Second.ToString();\r\n            strMillisecond = dt.Millisecond.ToString();\r\n            strTemp = strYear + strMonth + strDay + \"_\" + strHour + strMinute + strSecond + \"_\" + strMillisecond + \"_\" + rndNumber.ToString();\r\n            return strTemp;\r\n        }\r\n        /// <summary>\r\n        /// Gets the random code.\r\n        /// </summary>\r\n        /// <param name=\"strLength\">Length of the STR.</param>\r\n        /// <param name=\"strBegin\">The STR begin.</param>\r\n        /// <param name=\"tic\">The tic.</param>\r\n        /// <returns></returns>\r\n        /// <remarks></remarks>\r\n        public string GetRandomCode(int strLength, string strBegin, int tic)\r\n        {\r\n            string chkCode = string.Empty;\r\n            char[] character = { \'2\', \'3\', \'4\', \'5\', \'6\', \'8\', \'9\', \'A\', \'B\', \'C\', \'D\', \'E\', \'F\', \'G\', \'H\', \'J\', \'K\', \'L\', \'M\', \'N\', \'P\', \'R\', \'S\', \'T\', \'W\', \'X\', \'Y\' };\r\n\r\n            Random random = new Random(tic * (int)DateTime.Now.Ticks);\r\n            //����������ַ���  \r\n            for (int i = 0; i < strLength; i++)\r\n            {\r\n                chkCode += character[random.Next(character.Length)];\r\n            }\r\n            return string.IsNullOrEmpty(strBegin) ? chkCode : strBegin + chkCode;\r\n        }\r\n\r\n        /// <summary>\r\n        /// \r\n        /// </summary>\r\n        /// <param name=\"strLength\"></param>\r\n        /// <param name=\"strBegin\"></param>\r\n        /// <param name=\"tic\"></param>\r\n        /// <returns></returns>\r\n        public string GetLowerRandomCode(int strLength, string strBegin, int tic)\r\n        {\r\n            string chkCode = string.Empty;\r\n            char[] character = { \'2\', \'3\', \'4\', \'5\', \'6\', \'8\', \'9\', \'a\', \'b\', \'c\', \'d\', \'e\', \'f\', \'g\', \'h\', \'j\', \'k\', \'l\', \'m\', \'n\', \'p\', \'r\', \'s\', \'t\', \'w\', \'x\', \'y\' };\r\n\r\n            Random random = new Random(tic * (int)DateTime.Now.Ticks);\r\n            //����������ַ���  \r\n            for (int i = 0; i < strLength; i++)\r\n            {\r\n                chkCode += character[random.Next(character.Length)];\r\n            }\r\n            return string.IsNullOrEmpty(strBegin) ? chkCode : strBegin + chkCode;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ȡ�ַ����еķ������ַ�\r\n        /// </summary>\r\n        /// <param name=\"str\">�ַ���</param>\r\n        /// <returns></returns>\r\n        public static string ReturnStr(string str)\r\n        {\r\n            string ret = \"\";\r\n            for (int i = 0; i < str.Length; i++)\r\n            {\r\n                string tmp = str.Substring(i, 1);\r\n                if (!TypeParse.IsNumeric(tmp))\r\n                {\r\n                    ret = ret + tmp;\r\n                }\r\n            }\r\n            return ret;\r\n        }\r\n\r\n        /// <summary>\r\n        /// �������뷽��\r\n        /// </summary>\r\n        /// <param name=\"Value\">��Ҫ���������ֵ</param>\r\n        /// <param name=\"Digits\">��Ҫ��ȷ��λ��������λΪ����С��λΪ����������ȷ��С���ڶ�λ����-2����ȷ�������ڶ�λ����2</param>\r\n        /// <returns>����������ֵ</returns>\r\n        public static double MathRound(double Value, int Digits)\r\n        {\r\n            double d = Convert.ToDouble(Value);\r\n            if (Digits > 0)\r\n                d = d / Math.Pow(10, Digits);\r\n            string fractionString = \"\";   //С�������ַ���\r\n            //ȡ��С�����λ�ã����ַ�����Ϊ�������ֺ�С�����֣�û��С�����־�Ϊ�ա�\r\n            int dotPosition = d.ToString().IndexOf(\".\");\r\n            if (dotPosition != -1)\r\n                fractionString = d.ToString().Substring(dotPosition + 1);\r\n            if (fractionString.Length <= -Digits + 1)\r\n                d += Math.Pow(10, Digits - 2);\r\n            if (Digits <= 0)\r\n                return Math.Round(d, -Digits);\r\n            else\r\n                return Math.Round(d, 0) * Math.Pow(10, Digits);\r\n        }\r\n\r\n        /// <summary>\r\n        /// ���ַ���ת��ΪColor\r\n        /// </summary>\r\n        /// <param name=\"color\"></param>\r\n        /// <returns></returns>\r\n        public static Color ToColor(string color)\r\n        {\r\n            int red, green, blue = 0;\r\n            char[] rgb;\r\n            color = color.TrimStart(\'#\');\r\n            color = Regex.Replace(color.ToLower(), \"[g-zG-Z]\", \"\");\r\n            switch (color.Length)\r\n            {\r\n                case 3:\r\n                    rgb = color.ToCharArray();\r\n                    red = Convert.ToInt32(rgb[0].ToString() + rgb[0].ToString(), 16);\r\n                    green = Convert.ToInt32(rgb[1].ToString() + rgb[1].ToString(), 16);\r\n                    blue = Convert.ToInt32(rgb[2].ToString() + rgb[2].ToString(), 16);\r\n                    return Color.FromArgb(red, green, blue);\r\n                case 6:\r\n                    rgb = color.ToCharArray();\r\n                    red = Convert.ToInt32(rgb[0].ToString() + rgb[1].ToString(), 16);\r\n                    green = Convert.ToInt32(rgb[2].ToString() + rgb[3].ToString(), 16);\r\n                    blue = Convert.ToInt32(rgb[4].ToString() + rgb[5].ToString(), 16);\r\n                    return Color.FromArgb(red, green, blue);\r\n                default:\r\n                    return Color.FromName(color);\r\n\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// �ж��ַ����Ƿ���yy-mm-dd�ַ���\r\n        /// </summary>\r\n        /// <param name=\"str\">���ж��ַ���</param>\r\n        /// <returns>�жϽ��</returns>\r\n        public static bool IsDateString(string str)\r\n        {\r\n            return Regex.IsMatch(str, @\"(\\d{4})-(\\d{1,2})-(\\d{1,2})\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// �Ƴ�Html���\r\n        /// </summary>\r\n        /// <param name=\"content\"></param>\r\n        /// <returns></returns>\r\n        public static string RemoveHtml(string content)\r\n        {\r\n            string regexstr = @\"<[^>]*>\";\r\n            return Regex.Replace(content, regexstr, string.Empty, RegexOptions.IgnoreCase);\r\n        }\r\n\r\n        /// <summary>\r\n        /// ����HTML�еĲ���ȫ��ǩ\r\n        /// </summary>\r\n        /// <param name=\"content\"></param>\r\n        /// <returns></returns>\r\n        public static string RemoveUnsafeHtml(string content)\r\n        {\r\n            content = Regex.Replace(content, @\"(\\<|\\s+)o([a-z]+\\s?=)\", \"$1$2\", RegexOptions.IgnoreCase);\r\n            content = Regex.Replace(content, @\"(script|frame|form|meta|behavior|style)([\\s|:|>])+\", \"$1.$2\", RegexOptions.IgnoreCase);\r\n            return content;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ����Ƿ���SqlΣ���ַ�\r\n        /// </summary>\r\n        /// <param name=\"str\">Ҫ�ж��ַ���</param>\r\n        /// <returns>�жϽ��</returns>\r\n        public static bool IsSafeSqlString(string str)\r\n        {\r\n            return !Regex.IsMatch(str, @\"[-|;|,|\\/|\\(|\\)|\\[|\\]|\\}|\\{|%|@|\\*|!|\\\']\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// ����Ƿ���Σ�յĿ����������ӵ��ַ���\r\n        /// </summary>\r\n        /// <param name=\"str\">Ҫ�ж��ַ���</param>\r\n        /// <returns>�жϽ��</returns>\r\n        public static bool IsSafeUserInfoString(string str)\r\n        {\r\n            return !Regex.IsMatch(str, @\"^\\s*$|^c:\\\\con\\\\con$|[%,\\*\" + \"\\\"\" + @\"\\s\\t\\<\\>\\&]|�ο�|^Guest\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// ȡ��ϵͳ·��\r\n        /// </summary>\r\n        public static string AppPath\r\n        {\r\n            get\r\n            {\r\n                string args = null;\r\n                try\r\n                {\r\n                    args = System.Web.HttpContext.Current.Request.PhysicalApplicationPath;\r\n                }\r\n                catch (Exception) { ;}\r\n                if (String.IsNullOrEmpty(args)) args = System.Environment.CurrentDirectory;\r\n                return args;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ȡ��Դ����ĵ�ַ\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static string GetFurl()\r\n        {\r\n            string ret = string.Empty;\r\n            string[] url = HttpContext.Current.Request.ServerVariables[\"URL\"].Split(\'/\');\r\n            if (HttpContext.Current.Request.ServerVariables[\"QUERY_STRING\"] == \"\")\r\n            {\r\n                ret = url[url.Length - 1];\r\n            }\r\n            else\r\n            {\r\n                ret = url[url.Length - 1] + \"?\" + HttpContext.Current.Request.ServerVariables[\"QUERY_STRING\"];\r\n            }\r\n\r\n            return ret;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��õ�ǰҳ��ͻ��˵�IP\r\n        /// </summary>\r\n        /// <returns>��ǰҳ��ͻ��˵�IP</returns>\r\n        public static string GetIP()\r\n        {\r\n            string result = String.Empty;\r\n\r\n            result = HttpContext.Current.Request.ServerVariables[\"HTTP_X_FORWARDED_FOR\"];\r\n            if (null == result || result == String.Empty)\r\n            {\r\n                result = HttpContext.Current.Request.ServerVariables[\"REMOTE_ADDR\"];\r\n            }\r\n\r\n            if (null == result || result == String.Empty)\r\n            {\r\n                result = HttpContext.Current.Request.UserHostAddress;\r\n            }\r\n\r\n            if (null == result || result == String.Empty || !VerifyData.IsIP(result))\r\n            {\r\n                return \"0.0.0.0\";\r\n            }\r\n\r\n            return result;\r\n\r\n        }\r\n\r\n        /// <summary>\r\n        /// ����:�Ա� ������ݽ���ת��HTML����,\r\n        /// </summary>\r\n        /// <param name=\"s\">html�ַ���</param>\r\n        /// <returns></returns>\r\n        public static string HtmlCode(string s)\r\n        {\r\n            string str = \"\";\r\n            str = s.Replace(\">\", \"&gt;\");\r\n            str = s.Replace(\"<\", \"&lt;\");\r\n            str = s.Replace(\" \", \"&nbsp;\");\r\n            str = s.Replace(\"\\n\", \"<br />\");\r\n            str = s.Replace(\"\\r\", \"<br />\");\r\n            str = s.Replace(\"\\r\\n\", \"<br />\");\r\n\r\n            return str;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ����:�Ա� ������ݽ���ת��HTML����,\r\n        /// </summary>\r\n        /// <param name=\"s\"></param>\r\n        /// <returns></returns>\r\n        public static string CodeHtml(string s)\r\n        {\r\n            string str = \"\";\r\n            str = s.Replace(\"&gt;\", \">\");\r\n            str = s.Replace(\"&lt;\", \"<\");\r\n            str = s.Replace(\"&nbsp;\", \" \");\r\n            str = s.Replace(\"<br />\", \"\\n\");\r\n            str = s.Replace(\"<br />\", \"\\r\");\r\n            str = s.Replace(\"<br />\", \"\\r\\n\");\r\n\r\n            return str;\r\n        }\r\n\r\n        /// <summary>     \r\n        /// ����xss�����ű�     \r\n        /// </summary>     \r\n        /// <param name=\"input\">�����ַ���</param>     \r\n        /// <returns>���˺���ַ���</returns>     \r\n        public static string FilterXSS(string html)\r\n        {\r\n            if (string.IsNullOrEmpty(html)) return string.Empty;\r\n\r\n            // CR(0a) ��LF(0b) ��TAB(9) ���⣬���˵����еĲ���ӡ�����ַ�.     \r\n            // Ŀ�ķ�ֹ������ʽ������ ��java\\0script��     \r\n            // ע�⣺\\n, \\r,  \\t ������Ҫ�����������Ϊ���ܻ�Ҫ�õ�     \r\n            string ret = System.Text.RegularExpressions.Regex.Replace(\r\n                html, \"([\\x00-\\x08][\\x0b-\\x0c][\\x0e-\\x20])\", string.Empty);\r\n\r\n            ret = ret.Replace(\"\\t\", \"\");  //(���䣬����TAB�ո���Ҳ��Σ�յ�XSS�ַ�)\r\n\r\n            //�滻���п��ܵ�16��10���ƹ����Ķ������     \r\n            //<IMG SRC=&#X40&#X61&#X76&#X61&#X73&#X63&#X72&#X69&#X70&#X74&#X3A&#X61&_#X6C&#X65&#X72&#X74&#X28&#X27&#X58&#X53&#X53&#X27&#X29>            \r\n            ret = System.Text.RegularExpressions.Regex.Replace(ret, @\"(&#[x|X]?\\d+);?\", \"\", System.Text.RegularExpressions.RegexOptions.IgnoreCase);\r\n\r\n            //����Javascript�¼������Ķ������   \r\n            string[] keywords = {\r\n                                    \"javascript\", \"vbscript\", \"expression\", \"applet\", \"meta\", \r\n                                    \"xml\", \"blink\", \"script\", \"embed\", \"object\", \r\n                                    \"iframe\", \"frame\", \"frameset\", \"ilayer\", \"layer\", \"bgsound\", \"title\", \"base\",   \r\n                                    \"onabort\", \"onactivate\", \"onafterprint\", \"onafterupdate\", \"onbeforeactivate\", \r\n                                    \"onbeforecopy\", \"onbeforecut\", \"onbeforedeactivate\", \"onbeforeeditfocus\", \"onbeforepaste\", \r\n                                    \"onbeforeprint\", \"onbeforeunload\", \"onbeforeupdate\", \"onblur\", \"onbounce\", \"oncellchange\", \r\n                                    \"onchange\", \"onclick\", \"oncontextmenu\", \"oncontrolselect\", \"oncopy\", \"oncut\", \"ondataavailable\", \r\n                                    \"ondatasetchanged\", \"ondatasetcomplete\", \"ondblclick\", \"ondeactivate\", \"ondrag\", \"ondragend\",\r\n                                    \"ondragenter\", \"ondragleave\", \"ondragover\", \"ondragstart\", \"ondrop\", \"onerror\", \"onerrorupdate\", \r\n                                    \"onfilterchange\", \"onfinish\", \"onfocus\", \"onfocusin\", \"onfocusout\", \"onhelp\", \"onkeydown\", \r\n                                    \"onkeypress\", \"onkeyup\", \"onlayoutcomplete\", \"onload\", \"onlosecapture\", \"onmousedown\", \r\n                                    \"onmouseenter\", \"onmouseleave\", \"onmousemove\", \"onmouseout\", \"onmouseover\", \"onmouseup\", \r\n                                    \"onmousewheel\", \"onmove\", \"onmoveend\", \"onmovestart\", \"onpaste\", \"onpropertychange\", \r\n                                    \"onreadystatechange\", \"onreset\", \"onresize\", \"onresizeend\", \"onresizestart\", \r\n                                    \"onrowenter\", \"onrowexit\", \"onrowsdelete\", \"onrowsinserted\", \"onscroll\", \"onselect\", \r\n                                    \"onselectionchange\", \"onselectstart\", \"onstart\", \"onstop\", \"onsubmit\", \"onunload\"};\r\n\r\n            bool found = true;\r\n            while (found)\r\n            {\r\n                var retBefore = ret;\r\n                for (int i = 0; i < keywords.Length; i++)\r\n                {\r\n                    //string pattern = \"/\"; (����, ����ǰ̨��/���˲���)\r\n                    string pattern = \"\";\r\n                    for (int j = 0; j < keywords[i].Length; j++)\r\n                    {\r\n                        if (j > 0)\r\n                            pattern = string.Concat(pattern, \'(\', \"(&#[x|X]0{0,8}([9][a][b]);?)?\", \"|(&#0{0,8}([9][10][13]);?)?\",\r\n                                \")?\");\r\n                        pattern = string.Concat(pattern, keywords[i][j]);\r\n                    }\r\n                    string replacement = string.Concat(keywords[i].Substring(0, 2), \"��x��\", keywords[i].Substring(2));\r\n                    ret = System.Text.RegularExpressions.Regex.Replace(ret, pattern, replacement, System.Text.RegularExpressions.RegexOptions.IgnoreCase);\r\n                    if (ret == retBefore)\r\n                        found = false;\r\n                }\r\n\r\n            }\r\n\r\n            return ret;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ���ݿ���״ֵ̬�õ�״̬����ʾֵ\r\n        /// </summary>\r\n        /// <param name=\"state\">״ֵ̬</param>\r\n        /// <returns>��ʾ״̬</returns>\r\n        public string CardStateShow(object state)\r\n        {\r\n            String str = String.Empty;\r\n            switch ((int)state)\r\n            {\r\n                case 0:\r\n                    str = \"δʹ��\";\r\n                    break;\r\n                case 1:\r\n                    str = \"�Ŷ���\";\r\n                    break;\r\n                case 2:\r\n                    str = \"�Ŷӳɹ�\";\r\n                    break;\r\n                case 3:\r\n                    str = \"�ѷ�Ǯ\";\r\n                    break;\r\n                default:\r\n                    break;\r\n            }\r\n            return str;\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:13', null, 'String.handler.helper.txt');
INSERT INTO `txt` VALUES ('764', '2', 'StringUtil.cs.txt', '\r\nusing System;\r\nusing System.Collections;\r\n\r\nusing System.Text;\r\nusing Microsoft.VisualBasic;\r\nusing System.Text.RegularExpressions;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n\r\n\r\n\r\n\r\n    /// <summary>\r\n    /// 字符串处理的相关函数集\r\n    /// </summary>\r\n    public sealed class StringUtil\r\n    {\r\n        /// <summary>\r\n        /// 构造函数\r\n        /// </summary>\r\n        private StringUtil()\r\n        {\r\n\r\n        }\r\n\r\n        #region 我添加的\r\n\r\n        #endregion 我添加的\r\n     \r\n        /// <summary>\r\n        /// 得到一个字符重复多遍后生成的字符串\r\n        /// </summary>\r\n        /// <param name=\"c\">字符</param>\r\n        /// <param name=\"count\">重复次数</param>\r\n        /// <returns>结果字符串</returns>\r\n        public static string RepeatChar(char c, int count)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n\r\n            for (int i = 0; i < count; i++)\r\n            {\r\n                sb.Append(c);\r\n            }\r\n            return sb.ToString();\r\n        }\r\n        /// <summary>\r\n        /// 移除指定的字符串\r\n        /// </summary>\r\n        /// <param name=\"sourceStr\">源字符串</param>\r\n        /// <param name=\"replaceStr\">要移除的字符串</param>\r\n        /// <returns></returns>\r\n        public static string RemoveEndString(string sourceStr, string replaceStr)\r\n        {\r\n            if (sourceStr.EndsWith(replaceStr))\r\n            {\r\n               sourceStr= sourceStr.Remove(sourceStr.LastIndexOf(replaceStr));\r\n            }\r\n            return sourceStr;\r\n        }\r\n        /// <summary>\r\n        /// 得到一个字符串重复多遍后生成的字符串\r\n        /// </summary>\r\n        /// <param name=\"c\">字符串</param>\r\n        /// <param name=\"count\">重复次数</param>\r\n        /// <returns>结果字符串</returns>\r\n        public static string RepeatChar(string c, int count)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n\r\n            for (int i = 0; i < count; i++)\r\n            {\r\n                sb.Append(c);\r\n            }\r\n            return sb.ToString();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 去除HTML标记\r\n        /// </summary>\r\n        /// <param name=\"sHtml\">包括HTML的源码 </param>\r\n        /// <returns>已经去除后的文字</returns>  \r\n        public static string RemoveHtml(string sHtml)\r\n        {\r\n            string[] aryReg ={\r\n          @\"<script[^>]*?>.*?</script>\",\r\n          @\"<(\\/\\s*)?!?((\\w+:)?\\w+)(\\w+(\\s*=?\\s*(([\"\"\'])(\\\\[\"\"\'tbnr]|[^\\7])*?\\7|\\w+)|.{0})|\\s)*?(\\/\\s*)?>\",\r\n          @\"([ ])[\\s]+\",\r\n          @\"&(quot|#34);\",\r\n          @\"&(amp|#38);\",\r\n          @\"&(lt|#60);\",\r\n          @\"&(gt|#62);\", \r\n          @\"&(nbsp|#160);\", \r\n          @\"&(iexcl|#161);\",\r\n          @\"&(cent|#162);\",\r\n          @\"&(pound|#163);\",\r\n          @\"&(copy|#169);\",\r\n          @\"&#(\\d+);\",\r\n          @\"-->\",\r\n          @\"<!--.* \"\r\n         \r\n         };\r\n            string[] aryRep = {\r\n           \"\",\r\n           \"\",\r\n           \"\",\r\n           \"\\\"\",\r\n           \"&\",\r\n           \"<\",\r\n           \">\",\r\n           \" \",\r\n           \"\\xa1\",//chr(161),\r\n           \"\\xa2\",//chr(162),\r\n           \"\\xa3\",//chr(163),\r\n           \"\\xa9\",//chr(169),\r\n           \"\",\r\n           \" \",\r\n           \"\"\r\n          };\r\n            string newReg = aryReg[0];\r\n            string strOutput = sHtml;\r\n            for (int i = 0; i < aryReg.Length; i++)\r\n            {\r\n                Regex regex = new Regex(aryReg[i], RegexOptions.IgnoreCase);\r\n                strOutput = regex.Replace(strOutput, aryRep[i]);\r\n            }\r\n            strOutput.Replace(\"<\", \"\");\r\n            strOutput.Replace(\">\", \"\");\r\n            strOutput.Replace(\" \", \"\");\r\n            return strOutput;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 得到由空格组成的指定长度的字符串\r\n        /// </summary>\r\n        /// <param name=\"count\">指定长度</param>\r\n        /// <returns>得到的字符串</returns>\r\n        public static string Space(int count)\r\n        {\r\n            return RepeatChar(\' \', count);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 判断一个字符串是否为数字串\r\n        /// </summary>\r\n        /// <param name=\"s\">待判断的字符串</param>\r\n        /// <returns>是否数字串</returns>\r\n        public static bool IsNumber(string s)\r\n        {\r\n            try\r\n            {\r\n                Int32.Parse(s);\r\n                return true;\r\n            }\r\n            catch\r\n            {\r\n                return false;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 判断一个字符串是否为浮点型数字串\r\n        /// </summary>\r\n        /// <param name=\"s\">待判断的字符串</param>\r\n        /// <returns>是否数字串</returns>\r\n        public static bool IsDouble(string s)\r\n        {\r\n            try\r\n            {\r\n                Double.Parse(s);\r\n                return true;\r\n            }\r\n            catch\r\n            {\r\n                return false;\r\n            }\r\n        }\r\n\r\n        #region FormatCurrency\r\n\r\n        /// <summary>\r\n        /// 对金额进行格式化\r\n        /// </summary>\r\n        /// <param name=\"amt\">金额对象</param>\r\n        /// <param name=\"thousandSep\">是否加千位间隔</param>\r\n        /// <param name=\"precision\">精度</param>\r\n        /// <returns>格式化后的字符串</returns>\r\n        public static string FormatCurrency(object amt, bool thousandSep, int precision)\r\n        {\r\n            double fNum;\r\n            fNum = ConvertUtil.ToFloat(amt);\r\n\r\n            string formatStr;\r\n            if (thousandSep)\r\n                formatStr = \"#,##0\";\r\n            else\r\n                formatStr = \"0\";\r\n\r\n            if (precision > 0)\r\n                formatStr += \".\";\r\n\r\n            formatStr += RepeatChar(\'0\', precision);\r\n\r\n            return fNum.ToString(formatStr);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 对金额进行格式化\r\n        /// </summary>\r\n        /// <param name=\"amt\">金额对象</param>\r\n        /// <returns>格式化后的字符串。带千位分隔符，2位精度。</returns>\r\n        public static string FormatCurrency(object amt)\r\n        {\r\n            return FormatCurrency(amt, true, 2);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 对金额进行格式化\r\n        /// </summary>\r\n        /// <param name=\"amt\">金额对象</param>\r\n        /// <param name=\"thousandSep\">是否加千位间隔</param>\r\n        /// <returns>格式化后的字符串。2位精度。</returns>\r\n        public static string FormatCurrency(object amt, bool thousandSep)\r\n        {\r\n            return FormatCurrency(amt, thousandSep, 2);\r\n        }\r\n\r\n        #endregion FormatCurrency\r\n\r\n        #region QuotedToDBStr\r\n\r\n        /// <summary>\r\n        /// 对字符串格式化为符合SQL语句的带引号的字符串\r\n        /// </summary>\r\n        /// <param name=\"str\">原始字符串</param>\r\n        /// <returns>格式化的字符串</returns>\r\n        /// <remarks>该函数做两件事：\r\n        /// 1. 将字符串内的单引号变为两个单引号；\r\n        /// 2. 前后加上单引号；\r\n        /// </remarks>\r\n        public static string QuotedToDBStr(string str)\r\n        {\r\n            return \"\'\" + str.Replace(\"\'\", \"\'\'\") + \"\'\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象格式化对符合SQL语句的单引号的字符串\r\n        /// </summary>\r\n        /// <param name=\"str\">对象</param>\r\n        /// <param name=\"defaultStr\">缺省字符串</param>\r\n        /// <returns>格式化后的字符串</returns>\r\n        /// <remarks>如缺省的字符串为null值，则返回的字符串为null</remarks>\r\n        public static string QuotedToDBStr(object str, string defaultStr)\r\n        {\r\n            if (str == null || str is System.DBNull)\r\n            {\r\n                if (defaultStr == null)\r\n                {\r\n                    return \"null\";\r\n                }\r\n                else\r\n                {\r\n                    return QuotedToDBStr((string)defaultStr);\r\n                }\r\n            }\r\n            else\r\n            {\r\n                return QuotedToDBStr((string)str);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象格式化对符合SQL语句的单引号的字符串\r\n        /// </summary>\r\n        /// <param name=\"str\">对象</param>\r\n        /// <returns>格式化后的字符串，缺省为\"\"。</returns>\r\n        public static string QuotedToDBStr(object str)\r\n        {\r\n            return QuotedToDBStr(str, \"\");\r\n        }\r\n\r\n        #endregion QuotedToDBStr\r\n\r\n        #region BoolToDBStr\r\n\r\n        /// <summary>\r\n        /// 将布尔值转换为SQL语句的字符串\r\n        /// </summary>\r\n        /// <param name=\"flag\">布尔值</param>\r\n        /// <returns>得到的字符串，取值非1即0</returns>\r\n        public static string BoolToDBStr(bool flag)\r\n        {\r\n            if (flag)\r\n            {\r\n                return \"1\";\r\n            }\r\n            else\r\n            {\r\n                return \"0\";\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为SQL语句的字符串\r\n        /// </summary>\r\n        /// <param name=\"objFlag\">对象</param>\r\n        /// <returns>得到的字符串，取值非1即0</returns>\r\n        public static string BoolToDBStr(object objFlag)\r\n        {\r\n            bool flag = false;\r\n            try\r\n            {\r\n                flag = Boolean.Parse(objFlag.ToString());\r\n            }\r\n            catch\r\n            {\r\n                flag = false;\r\n            }\r\n            return BoolToDBStr(flag);\r\n        }\r\n        #endregion BoolToDBStr\r\n\r\n        /// <summary>\r\n        /// 计数某子串在字符串中产生的次数\r\n        /// </summary>\r\n        /// <param name=\"sSubStr\">待寻找的子串</param>\r\n        /// <param name=\"sString\">包含子串的字符串</param>\r\n        /// <returns>计数得到的次数</returns>\r\n        public static int Occurs(string sSubStr, string sString)\r\n        {\r\n            int nRet = 0;\r\n            bool bFound = true;\r\n\r\n            while (bFound)\r\n            {\r\n                int nFindPos = sString.IndexOf(sSubStr);\r\n                if (nFindPos != -1)\r\n                {\r\n                    nRet++;\r\n                    sString = sString.Substring(nFindPos + sSubStr.Length);\r\n                }\r\n                else\r\n                    bFound = false;\r\n            }\r\n\r\n            return nRet;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 分隔字符串为数组\r\n        /// </summary>\r\n        /// <param name=\"str\">字符串</param>\r\n        /// <param name=\"sSplitStr\">分隔符</param>\r\n        /// <returns>经分隔的字符串数组</returns>\r\n        /// <remarks>.net自带的split方法只能用一个字符分隔</remarks>\r\n        public static string[] Split(string str, string sSplitStr)\r\n        {\r\n            int nPos;\r\n            string[] arrStr;\r\n            ArrayList arrList = new ArrayList();\r\n            nPos = -1;\r\n            nPos = str.IndexOf(sSplitStr);\r\n            while (nPos >= 0)\r\n            {\r\n                arrList.Add(str.Substring(0, nPos));\r\n                str = str.Substring(nPos + sSplitStr.Length);\r\n                nPos = str.IndexOf(sSplitStr);\r\n            }\r\n            arrList.Add(str);\r\n            arrStr = new string[arrList.Count];\r\n            arrStr = (string[])arrList.ToArray(Type.GetType(\"System.String\"));\r\n            arrList = null;\r\n            return arrStr;\r\n        }\r\n\r\n\r\n        ///// <summary>\r\n        ///// 根據一個d\r\n        ///// </summary>\r\n        ///// <returns></returns>\r\n        //public static string DllName(string sDllSpace)\r\n        //{\r\n\r\n        //}\r\n\r\n        /// <summary>\r\n        /// 将文本转换为HTML的格式\r\n        /// </summary>\r\n        /// <param name=\"sText\">待转换的文本</param>\r\n        /// <returns>HTML格式的文本</returns>\r\n        public static string ConvertText2Html(string sText)\r\n        {\r\n            sText = sText.Replace(\">\", \"&gt;\");\r\n            sText = sText.Replace(\"<\", \"&lt;\");\r\n            sText = sText.Replace(\"\\r\\n\", \"<br>\");\r\n            sText = sText.Replace(\"\\n\\r\", \"<br>\");\r\n            sText = sText.Replace(\"\\r\", \"<br>\");\r\n            sText = sText.Replace(\"\\n\", \"<br>\");\r\n            sText = sText.Replace(\" \", \"&nbsp;\");\r\n            return sText;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将HTML格式转换为普通文本格式\r\n        /// </summary>\r\n        /// <param name=\"sText\">HTML格式字符串</param>\r\n        /// <returns>普通文本格式字符串</returns>\r\n        public static string ConvertHtml2Text(string sText)\r\n        {\r\n            sText = sText.Replace(\"&nbsp;\", \" \");\r\n            sText = sText.Replace(\"<br>\", \"\\n\");\r\n            sText = sText.Replace(\"&lt;\", \"<\");\r\n            sText = sText.Replace(\"&gt;\", \">\");\r\n\r\n            return sText;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到字符串的字节长度\r\n        /// </summary>\r\n        /// <param name=\"sText\">字符串</param>\r\n        /// <returns>长度</returns>\r\n        /// <remarks>中文的长度为两个字节，英文为一个字节</remarks>\r\n        public static int GetTextLength(string sText)\r\n        {\r\n            char[] arrText = sText.ToCharArray();\r\n            int length = 0;\r\n            for (int i = 0; i < arrText.Length; i++)\r\n            {\r\n                if (((int)arrText[i]) > 255)//19900\r\n                    length = length + 2;\r\n                else\r\n                    length = length + 1;\r\n            }\r\n            arrText = null;\r\n            return length;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Unicode碼字符串截取不截斷中文 \r\n        /// </summary>\r\n        /// <param name=\"sString\">要截取的字符串</param>\r\n        /// <param name=\"nLength\">要截取的长度</param>       \r\n        /// <returns></returns>\r\n        public static string SubUnString(string sString, int nLength)\r\n        {\r\n            return SubString(sString, 0, nLength);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取一个字符串的长度(汉字算两个字节)\r\n        /// </summary>\r\n        /// <param name=\"sString\">字符串</param> \r\n        /// <returns></returns>\r\n        public static int Length(string sString)\r\n        {\r\n            return System.Text.Encoding.Default.GetBytes(sString).Length;\r\n        }\r\n\r\n        /// <summary>\r\n        /// Unicode碼字符串截取不截斷中文\r\n        /// </summary>\r\n        /// <param name=\"sString\">要截取的字符串</param>  \r\n        /// <param name=\"nStartIndex\">要截取的长度</param> \r\n        /// <param name=\"nLength\">要截取的长度</param>        \r\n        /// <returns></returns>\r\n        public static string SubUnString(string sString, int nStartIndex, int nLength)\r\n        {\r\n            //=================== 1.0 数据验证 ==================\r\n            if (sString == null || sString == \"\")\r\n            {\r\n                return \"\";\r\n            }\r\n\r\n            //=================== 2.0 变量定义与赋值 ==================\r\n            int nCount = nStartIndex + nLength;\r\n            char[] arrText = sString.ToCharArray();\r\n            int length = 0;\r\n            string sText = \"\";\r\n\r\n            //=================== 3.0 变量定义与赋值 ==================\r\n            for (int i = 0; i < arrText.Length; i++)\r\n            {\r\n                if (((int)arrText[i]) > 255)//19900\r\n                {\r\n                    length = length + 2;\r\n                    if (length > nStartIndex && length <= nCount)\r\n                    {\r\n                        sText += arrText[i].ToString();\r\n                    }\r\n                    else if (length > nCount)\r\n                    {\r\n                        return sText;\r\n                    }\r\n                }\r\n                else\r\n                {\r\n                    length = length + 1;\r\n                    if (length > nStartIndex && length <= nCount)\r\n                    {\r\n                        sText += arrText[i].ToString();\r\n                    }\r\n                    else if (length > nCount)\r\n                    {\r\n                        return sText;\r\n                    }\r\n                }\r\n            }\r\n            return sText;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 判断是否为汉字字符\r\n        /// </summary>\r\n        /// <param name=\"ch\">字符</param>\r\n        /// <returns>如果是汉字字符则返回true，否则返回false</returns>\r\n        public static bool IsHZChar(char ch)\r\n        {\r\n            if ((int)ch > 255) // 19900\r\n                return true;\r\n            else\r\n                return false;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 判断是否为英文字母\r\n        /// </summary>\r\n        /// <param name=\"ch\">字符</param>\r\n        /// <returns>如果是英文字母则返回true，否则返回false</returns>\r\n        public static bool IsEnglishLetter(char ch)\r\n        {\r\n            if (ch >= \'A\' && ch <= \'Z\')\r\n                return true;\r\n            if (ch >= \'a\' && ch <= \'z\')\r\n                return true;\r\n            return false;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从字符串中解析出参数值\r\n        /// </summary>\r\n        /// <param name=\"QueryString\">查询参数串</param>\r\n        /// <param name=\"ParameterName\">参数名称</param>\r\n        /// <returns>获得的参数值</returns>\r\n        /// <remarks>字符串按urlLink的参数格式编码</remarks>\r\n        public static string GetParameterValue(string QueryString, string ParameterName)\r\n        {\r\n            int nPos = QueryString.IndexOf(ParameterName + \"=\");\r\n            if (nPos >= 0)\r\n            {\r\n                int nPos2 = QueryString.IndexOf(\"&\", nPos);\r\n                if (nPos2 > 0)\r\n                    return QueryString.Substring(nPos + ParameterName.Length + 1,\r\n                            nPos2 - nPos - ParameterName.Length - 1);\r\n                else\r\n                    return QueryString.Substring(nPos + ParameterName.Length + 1);\r\n            }\r\n            return \"\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 转化为string 类型\r\n        /// </summary>\r\n        /// <param name=\"obj\">待转化的object类型</param>\r\n        /// <returns>转化结果</returns>\r\n        public static string DbToString(object obj)\r\n        {\r\n            if (obj is DBNull)\r\n            {\r\n                return \"\";\r\n            }\r\n            else\r\n            {\r\n                return obj.ToString();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 字符串截取添加.为后辍\r\n        /// </summary>\r\n        /// <param name=\"sString\">要截取的字符串</param>\r\n        /// <param name=\"nLength\">要截取的长度</param>  \r\n        /// <returns></returns>\r\n        public static string SubString(string sString, int nLength)\r\n        {\r\n            return SubString(sString, nLength, \"\", 3);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 字符串截取添加.为后辍\r\n        /// </summary>\r\n        /// <param name=\"sString\">要截取的字符串</param>\r\n        /// <param name=\"nLength\">要截取的长度</param>  \r\n        /// <returns></returns>\r\n        public static string SubString3(string sString, int nLength)\r\n        {\r\n            return SubString(sString, nLength, \".\", 3);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 字符串截取  添加*为后辍\r\n        /// </summary>\r\n        /// <param name=\"sString\">要截取的字符串</param>\r\n        /// <param name=\"nLength\">要截取的长度</param>  \r\n        /// <param name=\"nPostfixNum\">后辍字符长度</param>\r\n        /// <returns></returns>\r\n        public static string SubString(string sString, int nLength, int nPostfixNum)\r\n        {\r\n            return SubString(sString, nLength, \".\", nPostfixNum);\r\n        }\r\n\r\n\r\n\r\n        /// <summary>\r\n        /// 字符串截取\r\n        /// </summary>\r\n        /// <param name=\"sString\">要截取的字符串</param>\r\n        /// <param name=\"nLength\">要截取的长度</param>  \r\n        /// <param name=\"sPostfix\">后辍字符</param>\r\n        /// <param name=\"nPostfixNum\">后辍字符长度</param>\r\n        /// <returns></returns>\r\n        public static string SubString(string sString, int nLength, string sPostfix, int nPostfixNum)\r\n        {\r\n            if (sString == null || sString == \"\")\r\n            {\r\n                return \"\";\r\n            }\r\n            else if (sString.Length < nLength)\r\n            {\r\n                return sString;\r\n            }\r\n            else\r\n            {\r\n                string sPost = \"\";\r\n                if (sPostfix != \"\" && nPostfixNum > 0)\r\n                {\r\n                    for (int i = 0; i < nPostfixNum; i++)\r\n                    {\r\n                        sPost += sPostfix;\r\n                    }\r\n                }\r\n                return sString.Substring(0, nLength) + sPost;\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 截取字符串用于数据库中的\r\n        /// </summary>\r\n        /// <param name=\"sOldString\"></param>\r\n        /// <param name=\"sType\"></param>\r\n        /// <returns>varchar 的“in”</returns>\r\n        public static string SplitDBIn(string sOldString, string sType)\r\n        {\r\n            return SplitDBIn(sOldString, sType, false);\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 截取字符串用于数据库中的“in”\r\n        /// </summary>\r\n        /// <param name=\"sOldString\"></param>\r\n        /// <param name=\"sType\"></param>\r\n        /// <param name=\"isInt\"></param>\r\n        /// <returns></returns>\r\n        public static string SplitDBIn(string sOldString, string sType, bool isInt)\r\n        {\r\n            string[] arrType = Split(sOldString, sType);\r\n            StringBuilder str = new StringBuilder();\r\n            for (int i = 0; i < arrType.Length; i++)\r\n            {\r\n                if (i != 0)\r\n                {\r\n                    str.Append(\",\");\r\n                }\r\n                if (isInt)\r\n                {\r\n                    str.Append(arrType[i]);\r\n                }\r\n                else\r\n                {\r\n                    str.Append(QuotedToDBStr(arrType[i]));\r\n                }\r\n            }\r\n            return str.ToString();\r\n        }\r\n    }\r\n}\r\n\r\n\r\n', '0', '2017-09-08 09:49:13', null, 'StringUtil.cs.txt');
INSERT INTO `txt` VALUES ('765', '2', 'Sublime Text.代码编辑器.百度百科.txt', 'Sublime Text 编辑\r\nSublime Text 是一个代码编辑器（Sublime Text 2是收费软件，但可以无限期试用），也是HTML和散文先进的文本编辑器。Sublime Text是由程序员Jon Skinner于2008年1月份所开发出来，它最初被设计为一个具有丰富扩展功能的Vim。\r\nSublime Text具有漂亮的用户界面和强大的功能，例如代码缩略图，Python的插件，代码段等。还可自定义键绑定，菜单和工具栏。Sublime Text 的主要功能包括：拼写检查，书签，完整的 Python API ， Goto 功能，即时项目切换，多选择，多窗口等等。Sublime Text 是一个跨平台的编辑器，同时支持Windows、Linux、Mac OS X等操作系统。\r\n中文名 崇高文本 外文名 Sublime Text 开发者 Jon Skinner 推出时间 2008年1月 作    用 代码编辑器 新版本 Sublime Text 3\r\n目录\r\n1 功能特性\r\n2 插件管理\r\n3 推荐插件\r\n4 优点\r\n5 新版发布\r\n功能特性编辑\r\nSublime Text 支持多种编程语言的语法高亮、拥有优秀的代码自动完成功能，还拥有代码片段（Snippet\r\n）的功能，可以将常用的代码片段保存起来，在需要时随时调用。支持 VIM 模式，可以使用Vim模式下的多数命令。支持宏，简单地说就是把操作录制下来或者自己编写命令，然后播放刚才录制的操作或者命令。\r\nSublime Text 还具有良好的扩展能力和完全开放的用户自定义配置与神奇实用的编辑状态恢复功能。支持强大的多行选择和多行编辑。强大的快捷命令“可以实时搜索到相应的命令、选项、snippet 和 syntex， 按下回车就可以直接执行，减少了查找的麻烦。即时的文件切换。随心所欲的跳转到任意文件的任意位置。多重选择功能允许在页面中同时存在多个光标。\r\n该编辑器在界面上比较有特色的是支持多种布局和代码缩略图，右侧的文件略缩图滑动条，方便地观察当前窗口在文件的那个位置。也提供了 F11 和 Shift+F11 进入全屏免打扰模式。代码缩略图、多标签页和多种布局设置，在大屏幕或需同时编辑多文件时尤为方便 全屏免打扰模式，更加专心于编辑。代码缩略图的功能在更早的编辑器TextMate中就已经存在，TextMate已经开源。Sublime Text 2支持文件夹浏览，可以打开文件夹，在左侧会有导航栏，方便在同时处理多个文件。3、多个位置同时编辑，按住ctrl，用鼠标选择多个位置，可以同时在对应位置进行相同操作。[1] \r\nSublimeText 还有编辑状态恢复的能力，即当你修改了一个文件，但没有保存，这时退出软件，软件不询问用户是否要保存的，因为无论是用户自发退出还是意外崩溃退出，下次启动软件后，之前的编辑状态都会被完整恢复，就像退出前时一样。\r\n快捷键\r\nCtrl+L 选择整行（按住-继续选择下行）\r\nCtrl+KK 从光标处删除至行尾\r\nCtrl+K Backspace 从光标处删除至行首\r\nCtrl+J 合并行（已选择需要合并的多行时）\r\nCtrl+KU 改为大写\r\nCtrl+KL 改为小写\r\nCtrl+D 选择字符串 （按住-继续选择下个相同的字符串）\r\nCtrl+M 光标移动至括号内开始或结束的位置\r\nCtrl+/ 注释整行（如已选择内容，同“Ctrl+Shift+/”效果）\r\nCtrl+Shift+c转换为utf8\r\nCtrl+R 搜索指定文件的函数标签\r\nCtrl+G 跳转到指定行\r\nCtrl+KT 折叠属性\r\nCtrl+K0 展开所有\r\nCtrl+U 软撤销\r\nCtrl+T 词互换\r\nTab 缩进 自动完成\r\nShift+Tab 去除缩进\r\nCtrl+F2 设置书签.\r\nF2 下一个书签\r\nShift+F2 上一个书签\r\nshift+鼠标右键 列选择\r\nAlt+F3 选中文本按下快捷键，即可一次性选择全部的相同文本进行同时编辑\r\nAlt+. 闭合当前标签\r\nF6 检测语法错误\r\nF9 行排序(按a-z)\r\nF11 全屏模式\r\nCtrl+Enter 光标后插入行\r\nCtrl+Shift+Enter 光标前插入行\r\nCtrl+Shift+[ 折叠代码\r\nCtrl+Shift+] 展开代码\r\nCtrl+Shift+↑ 与上行互换\r\nCtrl+Shift+↓ 与下行互换\r\nCtrl+Shift+A 选择光标位置父标签对儿\r\nCtrl+Shift+D 复制光标所在整行，插入在该行之前\r\nctrl+shift+F 在文件夹内查找，与普通编辑器不同的地方是sublime允许添加多个文件夹进行查找\r\nCtrl+Shift+K 删除整行\r\nCtrl+Shift+L 鼠标选中多行（按下快捷键），即可同时编辑这些行\r\nCtrl+Shift+M 选择括号内的内容（按住-继续选择父括号）\r\nCtrl+Shift+P 打开命令面板\r\nCtrl+Shift+/ 注释已选择内容\r\nCtrl+PageDown 、Ctrl+PageUp 文件按开启的前后顺序切换\r\nCtrl+鼠标左键 可以同时选择要编辑的多处文本\r\nShift+鼠标右键（或使用鼠标中键）可以用鼠标进行竖向多行选择\r\nShift+Tab 去除缩进\r\nAlt+Shift+1~9（非小键盘）屏幕显示相等数字的小窗口\r\n插件管理编辑\r\n1、安装Package Control\r\n按Ctrl+`调出console\r\n粘贴安装代码（见扩展阅读）到底部命令行并回车：\r\n重启Sublime Text 2。\r\n如果在Perferences->package settings中看到package control这一项，则安装成功。\r\n可以到官网链接（见扩展阅读）下载Package Control.sublime-package放到sublime安装目录里的data里installed package文件夹（这个文件夹位置可能会不一样）\r\n2、用Package Control安装其他插件\r\n按下Ctrl+Shift+P调出命令面板\r\n输入install 调出 Install Package 选项并回车，然后在列表中选中要安装的插件。\r\n推荐插件编辑\r\nConvert To UTF8\r\n将文件编码从GBK转换成UTF8，快捷键Ctrl+Shift+C\r\nzenCoding\r\nzenCoding是一种快速的html、css编写方式。默认用tab键.\r\n已经更名为Emmet。\r\nJS Format\r\n一个JS代码格式化插件。默认ctrl+alt+f\r\nBracketHighlighter\r\n括弧高亮插件，清晰明了\r\nGit\r\n代码版本管理\r\nChineseLocalization[2] \r\n汉化插件，支持无缝切换中文、日语、英文.\r\n优点编辑\r\n主流前端开发编辑器\r\n体积较小，运行速度快\r\n文本功能强大\r\n支持编译功能且可在控制台看到输出\r\n内嵌python解释器支持插件开发以达到可扩展目的\r\nPackage Control：ST支持的大量插件可通过其进行管理[3] \r\n', '0', '2017-09-08 09:49:13', null, 'Sublime Text.代码编辑器.百度百科.txt');
