INSERT INTO `txt` VALUES ('782', '2', 'using.connection.transaction.cts.dapper.cts.事务.txt', '        public string MethodName(string id)\r\n        {\r\n            using (IDbConnection conn = ConnectionManager.GetDbConnection(\"DBStr\"))\r\n            {\r\n                conn.Open();\r\n                IDbTransaction trans = conn.BeginTransaction();\r\n                try\r\n                {\r\n                    var flag = CVInformationDAL.GetInstance().MethodName(id, conn, trans);\r\n                    trans.Commit();\r\n                    return flag;\r\n                }\r\n                catch (Exception)\r\n                {\r\n                    trans.Rollback();\r\n                    throw;\r\n                }\r\n\r\n            }\r\n        }\r\n', '0', '2017-09-08 09:49:13', null, 'using.connection.transaction.cts.dapper.cts.事务.txt');
INSERT INTO `txt` VALUES ('783', '2', 'ValidateCode.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Drawing;\r\nusing System.Drawing.Drawing2D;\r\nusing System.Drawing.Imaging;\r\nusing System.IO;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    public class ValidateCode\r\n    {\r\n        public ValidateCode()\r\n        {\r\n        }\r\n        /// <summary>\r\n        /// 验证码的最大长度\r\n        /// </summary>\r\n        public int MaxLength\r\n        {\r\n            get { return 10; }\r\n        }\r\n        /// <summary>\r\n        /// 验证码的最小长度\r\n        /// </summary>\r\n        public int MinLength\r\n        {\r\n            get { return 1; }\r\n        }\r\n        /// <summary>\r\n        /// 生成验证码\r\n        /// </summary>\r\n        /// <param name=\"length\">指定验证码的长度</param>\r\n        /// <returns></returns>\r\n        public string CreateValidateCode(int length)\r\n        {\r\n            int[] randMembers = new int[length];\r\n            int[] validateNums = new int[length];\r\n            string validateNumberStr = \"\";\r\n            //生成起始序列值\r\n            int seekSeek = unchecked((int)DateTime.Now.Ticks);\r\n            Random seekRand = new Random(seekSeek);\r\n            int beginSeek = (int)seekRand.Next(0, Int32.MaxValue - length * 10000);\r\n            int[] seeks = new int[length];\r\n            for (int i = 0; i < length; i++)\r\n            {\r\n                beginSeek += 10000;\r\n                seeks[i] = beginSeek;\r\n            }\r\n            //生成随机数字\r\n            for (int i = 0; i < length; i++)\r\n            {\r\n                Random rand = new Random(seeks[i]);\r\n                int pownum = 1 * (int)Math.Pow(10, length);\r\n                randMembers[i] = rand.Next(pownum, Int32.MaxValue);\r\n            }\r\n            //抽取随机数字\r\n            for (int i = 0; i < length; i++)\r\n            {\r\n                string numStr = randMembers[i].ToString();\r\n                int numLength = numStr.Length;\r\n                Random rand = new Random();\r\n                int numPosition = rand.Next(0, numLength - 1);\r\n                validateNums[i] = Int32.Parse(numStr.Substring(numPosition, 1));\r\n            }\r\n            //生成验证码\r\n            for (int i = 0; i < length; i++)\r\n            {\r\n                validateNumberStr += validateNums[i].ToString();\r\n            }\r\n            return validateNumberStr;\r\n        }\r\n\r\n        //C# MVC 升级版\r\n        /// <summary>\r\n        /// 创建验证码的图片\r\n        /// </summary>\r\n        /// <param name=\"containsPage\">要输出到的page对象</param>\r\n        /// <param name=\"validateNum\">验证码</param>\r\n        public byte[] CreateValidateGraphic(string validateCode)\r\n        {\r\n            Bitmap image = new Bitmap((int)Math.Ceiling(validateCode.Length * 12.0), 22);\r\n            Graphics g = Graphics.FromImage(image);\r\n            try\r\n            {\r\n                //生成随机生成器\r\n                Random random = new Random();\r\n                //清空图片背景色\r\n                g.Clear(Color.White);\r\n                //画图片的干扰线\r\n                for (int i = 0; i < 25; i++)\r\n                {\r\n                    int x1 = random.Next(image.Width);\r\n                    int x2 = random.Next(image.Width);\r\n                    int y1 = random.Next(image.Height);\r\n                    int y2 = random.Next(image.Height);\r\n                    g.DrawLine(new Pen(Color.Silver), x1, y1, x2, y2);\r\n                }\r\n                Font font = new Font(\"Arial\", 12, (FontStyle.Bold | FontStyle.Italic));\r\n                LinearGradientBrush brush = new LinearGradientBrush(new Rectangle(0, 0, image.Width, image.Height),\r\n                 Color.Blue, Color.DarkRed, 1.2f, true);\r\n                g.DrawString(validateCode, font, brush, 3, 2);\r\n                //画图片的前景干扰点\r\n                for (int i = 0; i < 100; i++)\r\n                {\r\n                    int x = random.Next(image.Width);\r\n                    int y = random.Next(image.Height);\r\n                    image.SetPixel(x, y, Color.FromArgb(random.Next()));\r\n                }\r\n                //画图片的边框线\r\n                g.DrawRectangle(new Pen(Color.Silver), 0, 0, image.Width - 1, image.Height - 1);\r\n                //保存图片数据\r\n                MemoryStream stream = new MemoryStream();\r\n                image.Save(stream, ImageFormat.Jpeg);\r\n                //输出图片流\r\n                return stream.ToArray();\r\n            }\r\n            finally\r\n            {\r\n                g.Dispose();\r\n                image.Dispose();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到验证码图片的长度\r\n        /// </summary>\r\n        /// <param name=\"validateNumLength\">验证码的长度</param>\r\n        /// <returns></returns>\r\n        public static int GetImageWidth(int validateNumLength)\r\n        {\r\n            return (int)(validateNumLength * 12.0);\r\n        }\r\n        /// <summary>\r\n        /// 得到验证码的高度\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static double GetImageHeight()\r\n        {\r\n            return 22.5;\r\n        }\r\n\r\n    }\r\n}\r\n\r\n', '0', '2017-09-08 09:49:13', null, 'ValidateCode.cs.txt');
