INSERT INTO `txt` VALUES ('810', '2', 'xml.字符串.转化为.DataTable.jun(测试过).txt', 'using System.Data;\r\nusing System.IO;\r\nusing System.Text;\r\nusing System.Xml; \r\n		 \r\n		 \r\n		 \r\n		 \r\n		 \r\n		 /// <summary>\r\n        /// 拼接xml串\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public string GetXMLStr()\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"<?xml version=\'1.0\' encoding=\'utf-8\'?>\");\r\n            sb.Append(\"<res>\");\r\n            sb.Append(\"<person>\");\r\n            sb.AppendFormat(\"<name>{0}</name>\", \"刘谦\");\r\n            sb.AppendFormat(\"<email>{0}</email>\", \"liuqian@qq.com\");\r\n            sb.AppendFormat(\"<address>{0}</address>\", \"中国香港\");\r\n            sb.Append(\"</person>\");\r\n            sb.Append(\"<person>\");\r\n            sb.AppendFormat(\"<name>{0}</name>\", \"周杰伦\");\r\n            sb.AppendFormat(\"<email>{0}</email>\", \"jay@qq.com\");\r\n            sb.AppendFormat(\"<address>{0}</address>\", \"中国台湾\");\r\n            sb.Append(\"</person>\");\r\n            sb.Append(\"</res>\");\r\n            return sb.ToString();\r\n        }\r\n        /// <summary>\r\n        /// 根据xml串解析出DataTable\r\n        /// </summary>\r\n        /// <param name=\"xml\"></param>\r\n        /// <returns></returns>\r\n        public DataTable GetDataTableByXMLStr(string xml)\r\n        {\r\n            StringReader stream = null;\r\n            XmlTextReader reader = null;\r\n            DataSet xmlDS = new DataSet();\r\n            stream = new StringReader(xml);\r\n            reader = new XmlTextReader(stream);\r\n            xmlDS.ReadXml(reader);\r\n            DataTable result = xmlDS.Tables[0];\r\n            return result;\r\n        }\r\n        /// <summary>\r\n        /// 测试xml串转化为DataTable\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public DataTable Test()\r\n        {\r\n            var xml = GetXMLStr();\r\n            var dt=GetDataTableByXMLStr(xml);\r\n            return dt;\r\n        }\r\n', '0', '2017-09-08 09:49:14', null, 'xml.字符串.转化为.DataTable.jun(测试过).txt');
INSERT INTO `txt` VALUES ('811', '2', 'XMLToDataSet.jun.txt', '  /// <summary>\r\n        /// Xmlת��ΪDataSet\r\n        /// </summary>\r\n        /// <param name=\"xmlData\"></param>\r\n        /// <returns></returns>\r\n        private DataSet ConvertXMLToDataSet(string xmlData)\r\n        {\r\n            StringReader stream = null;\r\n            XmlTextReader reader = null;\r\n            try\r\n            {\r\n                DataSet xmlDS = new DataSet();\r\n                stream = new StringReader(xmlData);\r\n                reader = new XmlTextReader(stream);\r\n                xmlDS.ReadXml(reader);\r\n                return xmlDS;\r\n            }\r\n            catch (Exception ex)\r\n            {\r\n                string strTest = ex.Message;\r\n                return null;\r\n            }\r\n            finally\r\n            {\r\n                if (reader != null)\r\n                    reader.Close();\r\n            }\r\n        }\r\n', '0', '2017-09-08 09:49:14', null, 'XMLToDataSet.jun.txt');
INSERT INTO `txt` VALUES ('812', '2', 'Xml中SelectSingleNode方法中的xpath用法.csdn.blog.txt', '    最常见的XML数据类型有：Element, Attribute，Comment, Text.  \r\n      \r\n       Element, 指形如<Name>Tom<Name>的节点。它可以包括：Element, Text, Comment, ProcessingInstruction, CDATA, and EntityReference.  \r\n      \r\n       Attribute, 指在<Employee >中的粗体部分。  \r\n      \r\n       Comment，指形如：<!-- my comment --> 的节点。  \r\n      \r\n       Text，指在<Name>Tom<Name>的粗体部分。  \r\n      \r\n      在XML中，可以用XmlNode对象来参照各种XML数据类型。  \r\n      \r\n       2.1 查询已知绝对路径的节点（集）  \r\n      \r\n       objNodeList = objDoc.SelectNodes(“Company/Department/Employees/Employee”)  \r\n      \r\n      或者  \r\n      \r\n       objNodeList = objNode.SelectNodes(“/Company/Department/Employees/Employee”)  \r\n      \r\n      以上两种方法可返回一个NodeList对象，如果要返回单个节点可使用SelectSingleNode方法，该方法如果查询到一个或多个节点，返回第一个节点；如果没有查询的任何节点返回 Nothing。例如：  \r\n      \r\n       objNode = objNode.SelectSingleNode(“/Company/Department/Employees/Employee”)  \r\n      \r\n       If Not (objNode is Nothing) then  \r\n      \r\n       ‘- Do process  \r\n      \r\n       End If  \r\n      \r\n       2.2 查询已知相对路径的节点（集）  \r\n      \r\n      可使用类似于文件路径的相对路径的方式来查询XML的数据  \r\n      \r\n       objNode = objDoc.SelectSingleNode(“Company/Department”)  \r\n      \r\n       objNodeList = objNode.SelectNodes(“../Department)  \r\n      \r\n       objNode = objNode.SelectNode(“Employees/Employee”)  \r\n      \r\n       2.3 查询已知元素名的节点（集）  \r\n      \r\n      在使用不规则的层次文档时，由于不知道中间层次的元素名，可使用//符号来越过中间的节点，查询其子，孙或多层次下的其他所有元素。例如：  \r\n      \r\n       objNodeList = objDoc.SelectNodes(“Company//Employee”)  \r\n      \r\n       2.4 查询属性（attribute）节点  \r\n      \r\n      以上的各种方法都返回元素（element)节点（集），返回属性（attribute），只需要采用相应的方法，在属性名前加一个@符号即可，例如：  \r\n      \r\n       objNodeList = objDoc.SelectNodes(“Company/Department/Employees/Employee/@id”)  \r\n      \r\n       objNodeList = objDoc.SelectNodes(“Company//@id”)  \r\n      \r\n       2.5 查询Text节点  \r\n      \r\n      使用text()来获取Text节点。  \r\n      \r\n       objNode = objDoc.SelectSingleNode(“Company/Department/Deparmt_Name/text()”)  \r\n      \r\n       2.6 查询特定条件的节点  \r\n      \r\n      使用[]符号来查询特定条件的节点。例如：  \r\n      \r\n       a. 返回id号为 10102的Employee节点  \r\n      \r\n       objNode = objDoc.SelectSingleNode(“Company/Department/Employees/Employee[@id=’10102’]”)  \r\n      \r\n       b. 返回Name为Zhang Qi的Name 节点  \r\n      \r\n       objNode = objDoc.SelectSingleNode(“Company/Department/Employees/Employee/Name[text()=’Zhang Qi’]”)  \r\n      \r\n       c. 返回部门含有职员22345的部门名称节点  \r\n      \r\n       objNode = objDoc.SelectSingleNode(\"Company/Department[Employees/Employee/@id=\'22345\']/Department_Name\")  \r\n      \r\n       2.7 查询多重模式的节点  \r\n      \r\n      使用 | 符号可以获得多重模式的节点。例如：  \r\n      \r\n       objNodeList = objDoc.SelectNodes(“Company/Department/Department_Name | Company/Department/Manager”)  \r\n      \r\n       2.8 查询任意子节点  \r\n      \r\n      使用*符号可以返回当前节点的所有子节点。  \r\n      \r\n       objNodeList = objDoc.SelectNodes(“Company/*/Manager)  \r\n      \r\n      或者  \r\n      \r\n       objNodeList = objNode.ChildNodes  \r\n      \r\n        \r\n      \r\n       3 XML数据的编辑  \r\n      \r\n       3.1 增加一个元素的属性（attribute）节点  \r\n      \r\n       Dim objNodeAttr As XmlNode  \r\n      \r\n       objNodeAttr = objDoc.CreateAttribute(\"id\", Nothing)  \r\n      \r\n       objNodeAttr.InnerXml = \"101\"  \r\n      \r\n       objNode.Attributes.Append(objNodeAttr)  \r\n      \r\n       3.2 删除一个元素的属性  \r\n      \r\n       objNode.Attributes.Remove(objNodeAttr)  \r\n      \r\n       3.3 增加一个子元素（Element）  \r\n      \r\n       Dim objNodeChild As XmlNode  \r\n      \r\n       objNodeChild = objDoc.CreateElement(Nothing, \"ID\", Nothing)  \r\n      \r\n       objNodeChild.InnerXml = \"101\"  \r\n      \r\n       objNode.AppendChild(objNodeChild)  \r\n      \r\n       3.4 删除一个子元素  \r\n      \r\n       objNode.RemoveChild(objNodeChild)  \r\n      \r\n       3.5 替换一个子元素  \r\n      \r\n       objNOde.ReplaceChild(newChild,oldChild)  \r\n      \r\n        \r\n      \r\n       4 参考数据  \r\n      \r\n       <?xml version=\"1.0\" encoding=\"UTF-8\"?>  \r\n      \r\n       <Company>  \r\n      \r\n       <Department >  \r\n      \r\n       <Department_Name>Cai WuBu</Department_Name>  \r\n      \r\n       <Manager>Zhang Bin</Manager>  \r\n      \r\n       <Employees>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>12345</Employee_ID>  \r\n      \r\n       <Name>Zhang Bin</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>10101</Employee_ID>  \r\n      \r\n       <Name>Zhang QI</Name>  \r\n      \r\n       <Gender>female</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>10102</Employee_ID>  \r\n      \r\n       <Name>Zhang Xia</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>10201</Employee_ID>  \r\n      \r\n       <Name>ZhangChuang</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>10202</Employee_ID>  \r\n      \r\n       <Name>Zhang Jun</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       </Employees>  \r\n      \r\n       </Department>  \r\n      \r\n       <Department >  \r\n      \r\n       <Department_Name>KaiFa Bu</Department_Name>  \r\n      \r\n       <Manager>Wang Bin</Manager>  \r\n      \r\n       <Employees>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>22345</Employee_ID>  \r\n      \r\n       <Name>Wang Bin</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>20101</Employee_ID>  \r\n      \r\n       <Name>Wang QI</Name>  \r\n      \r\n       <Gender>female</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>20102</Employee_ID>  \r\n      \r\n       <Name>Wang Xia</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>20201</Employee_ID>  \r\n      \r\n       <Name>Wang Chuang</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       <Employee >  \r\n      \r\n       <Employee_ID>20201</Employee_ID>  \r\n      \r\n       <Name>Wang Jun</Name>  \r\n      \r\n       <Gender>male</Gender>  \r\n      \r\n       </Employee>  \r\n      \r\n       </Employees>  \r\n      \r\n       </Department>  \r\n      \r\n       </Company>  \r\n', '0', '2017-09-08 09:49:14', null, 'Xml中SelectSingleNode方法中的xpath用法.csdn.blog.txt');
