INSERT INTO `txt` VALUES ('821', '2', '从List转成DataTable.ListToDataTable.txt', '  public static class Extender\r\n    {\r\n        public static DataTable ToDataTable<T>(this IEnumerable<T> collection)\r\n        {\r\n            var dtReturn = new DataTable();\r\n            var oProps = typeof(T).GetProperties();\r\n            foreach (var pi in oProps)\r\n            {\r\n                var colType = pi.PropertyType;\r\n                if ((colType.IsGenericType) && (colType.GetGenericTypeDefinition() == typeof(Nullable<>)))\r\n                {\r\n                    colType = colType.GetGenericArguments()[0];\r\n                }\r\n                dtReturn.Columns.Add(new DataColumn(pi.Name, colType));\r\n            }\r\n            foreach (var rec in collection)\r\n            {\r\n                var dr = dtReturn.NewRow();\r\n                foreach (var pi in oProps)\r\n                {\r\n                    dr[pi.Name] = pi.GetValue(rec, null) ?? DBNull.Value;\r\n                }\r\n                dtReturn.Rows.Add(dr);\r\n            }\r\n            return dtReturn;\r\n        }\r\n    }\r\n', '0', '2017-09-08 09:49:14', null, '从List转成DataTable.ListToDataTable.txt');
