nt sysLength = maxLength * 1024;\r\n            if (fileLength > sysLength)\r\n            {\r\n                ///״̬��Ϣ\r\n                ret = false;\r\n                refName = \"ͼƬ����200KB���ޣ�\";\r\n                 return false;\r\n            }\r\n            ///ͼƬ�ϴ�\r\n            try\r\n            {\r\n                string picPath = SavePath;\r\n\r\n                string fileName, fileExtension;\r\n                ///�ϴ��ļ����ļ���\r\n                fileName = System.IO.Path.GetFileName(FileUpload1.Value);\r\n                if (fileName != \"\")\r\n                {\r\n                    ///�ϴ��ļ�����չ��\r\n                    fileExtension = System.IO.Path.GetExtension(fileName);\r\n                    ///�����µ��ļ���\r\n                    fileName = DateTime.Now.ToString(\"yyyyMMddHHmmffff\") + fileExtension;\r\n                    ///�ɸ�����չ���ֵĲ�ͬ���浽��ͬ���ļ���\r\n                    string date = DateTime.Now.ToString(\"yyyyMM\");\r\n                    picPath = Server.MapPath(picPath + date + \"/\");\r\n                    System.IO.DirectoryInfo di = new System.IO.DirectoryInfo(picPath);\r\n                    if (!di.Exists)\r\n                    {\r\n                        di.Create();\r\n                    }\r\n                    ///ע�⣺����Ҫ�޸������ļ��е�����д��Ȩ�ޡ�\r\n                    FileUpload1.PostedFile.SaveAs(picPath + fileName);\r\n                    refName = date + \"/\" + fileName;\r\n                    GetThumbnailImage(SavePath , date + \"/\", fileName, width, height);\r\n                }\r\n\r\n            }\r\n            catch \r\n            {\r\n\r\n                ret = false;\r\n                refName = \"�ϴ�ʧ�ܣ�\";\r\n                return false;\r\n            }\r\n            return ret;\r\n        }\r\n \r\n ��������ͼ����:\r\n \r\n\r\n\r\n/// <param name=\"SavePath\">ԭʼ·��</param>\r\n        /// <param name=\"pathDate\">��ӵ��·�·��</param>\r\n        /// <param name=\"picFilePath\">������ļ���</param>\r\n        /// <param name=\"width\">��</param>\r\n        /// <param name=\"height\">��</param>\r\n        public void GetThumbnailImage(string SavePath, string pathDate,string picFilePath, int width, int height)\r\n        {\r\n            //���small100��ǰ׺��С\r\n       //��������Եķ�����·��СͼƬ\r\n      string strRelativeSmallPath = SavePath + CommEnum.Pictures_Small100 + pathDate;\r\n\r\n            string SmallPath100 = Server.MapPath(strRelativeSmallPath + picFilePath);\r\n            string machpath = Server.MapPath(SavePath + pathDate + picFilePath);\r\n            string isDir = SmallPath100.Substring(0, SmallPath100.LastIndexOf(\'\'\\\'\'));\r\n            System.IO.DirectoryInfo di = new System.IO.DirectoryInfo(isDir);\r\n            if (!di.Exists)\r\n            {\r\n                di.Create();\r\n            }\r\n            Bitmap img = new Bitmap(machpath);  //read picture to memory\r\n\r\n            int h = img.Height;\r\n            int w = img.Width;\r\n            int ss, os;// source side and objective side\r\n            double temp1, temp2;\r\n            //compute the picture\'\'s proportion\r\n            temp1 = (h * 1.0D) / height;\r\n            temp2 = (w * 1.0D) / width;\r\n            if (temp1 < temp2)\r\n            {\r\n                ss = w;\r\n                os = width;\r\n            }\r\n            else\r\n            {\r\n                ss = h;\r\n                os = height;\r\n            }\r\n\r\n            double per = (os * 1.0D) / ss;\r\n            if (per < 1.0D)\r\n            {\r\n                h = (int)(h * per);\r\n                w = (int)(w * per);\r\n            }\r\n            // create the thumbnail image\r\n            System.Drawing.Image imag2 = img.GetThumbnailImage(w, h,\r\n                 new System.Drawing.Image.GetThumbnailImageAbort(ImageAbort),\r\n                 IntPtr.Zero);\r\n            Bitmap tempBitmap = new Bitmap(w, h);\r\n            System.Drawing.Image tempImg = System.Drawing.Image.FromHbitmap(tempBitmap.GetHbitmap());\r\n            System.Drawing.Graphics g = System.Drawing.Graphics.FromImage(tempImg);\r\n            g.Clear(Color.White);\r\n\r\n            int x, y;\r\n\r\n            x = (tempImg.Width - imag2.Width) / 2;\r\n            y = (tempImg.Height - imag2.Height) / 2;\r\n\r\n            g.DrawImage(imag2, x, y, imag2.Width, imag2.Height);\r\n\r\n            try\r\n            {\r\n                if (img != null)\r\n                    img.Dispose();\r\n                if (imag2 != null)\r\n                    imag2.Dispose();\r\n                if (tempBitmap != null)\r\n                    tempBitmap.Dispose();\r\n\r\n                string fileExtension = System.IO.Path.GetExtension(machpath).ToLower(); \r\n                //��ԭͼƬ���ͱ�������ͼƬ,����ԭ��ʽͼƬ�����ģ��,��ݵ�����.  \r\n                switch (fileExtension)\r\n                {\r\n                    case \".gif\": tempImg.Save(SmallPath100, ImageFormat.Gif); break;\r\n                    case \".jpg\": tempImg.Save(SmallPath100, ImageFormat.Jpeg); break;\r\n                    case \".bmp\": tempImg.Save(SmallPath100, ImageFormat.Bmp); break;\r\n                    case \".png\": tempImg.Save(SmallPath100, ImageFormat.Png); break;\r\n                }                                \r\n            }\r\n            catch\r\n            {\r\n                throw new Exception(\"ͼƬ�ϴ�ʧ��\");\r\n            }\r\n            finally\r\n            {\r\n                //�ͷ��ڴ�\r\n                if (tempImg != null)\r\n                    tempImg.Dispose();\r\n                if (g != null)\r\n                    g.Dispose();\r\n            }\r\n        }\r\n', '0', '2017-09-08 09:49:15', null, '图片上传并生成缩略图.txt');
INSERT INTO `txt` VALUES ('853', '2', '在.NET中利用XMLHTTP下载文件.txt', '����XMLHTTP�����ļ�������ǰ�ķ���һ������������ã�COM��Microsoft Xml 3.0��Ȼ���ڴ��뿪ʼ��д��\r\n\r\nusing MSXML2;\r\n���������Ҫ�Ĵ��룺\r\n\r\nprivate void Page_Load(object sender, System.EventArgs e)\r\n{	\r\n	string Url = \"http://dotnet.aspx.cc/Images/logoSite.gif\";\r\n	string StringFileName = Url.Substring(Url.LastIndexOf(\"/\") + 1);\r\n	string StringFilePath = Request.PhysicalApplicationPath;\r\n	if(!StringFilePath.EndsWith(\"/\")) StringFilePath += \"/\";\r\n	MSXML2.XMLHTTP _xmlhttp = new MSXML2.XMLHTTPClass();\r\n	_xmlhttp.open(\"GET\",Url,false,null,null);\r\n	_xmlhttp.send(\"\");\r\n	if( _xmlhttp.readyState == 4 )\r\n	{\r\n		if(System.IO.File.Exists(StringFilePath + StringFileName)) \r\n			System.IO.File.Delete(StringFilePath + StringFileName);\r\n		System.IO.FileStream fs = new System.IO.FileStream(StringFilePath + StringFileName, System.IO.FileMode.CreateNew);\r\n		System.IO.BinaryWriter w = new System.IO.BinaryWriter(fs);\r\n		w.Write((byte[])_xmlhttp.responseBody);\r\n		w.Close();\r\n		fs.Close();\r\n		Response.Write (\"�ļ��Ѿ��õ���<br><a href=\'\" + Request.ApplicationPath + StringFileName +\"\' target=\'_blank\'>\");\r\n		Response.Write (\"�鿴\" + StringFileName + \"</a>\");\r\n	}\r\n	else\r\n	Response.Write (_xmlhttp.statusText);\r\n	Response.End();\r\n}\r\n', '0', '2017-09-08 09:49:15', null, '在.NET中利用XMLHTTP下载文件.txt');
INSERT INTO `txt` VALUES ('854', '2', '在ASP.NET中实现POST发送数据.txt', '����Ĵ���ʵ��������ǰXMLHTTP���ƵĹ��ܡ��������£�\r\n\r\nHttpSendData.aspx\r\n\r\n<%@ Page language=\"c#\"%>\r\n<%@ Import Namespace = \"System\"%>\r\n<%@ Import Namespace = \"System.Collections\"%>\r\n<%@ Import Namespace = \"System.Web\"%>\r\n<%@ Import Namespace = \"System.Web.UI\"%>\r\n<%@ Import Namespace = \"System.Web.UI.WebControls\"%>\r\n<%@ Import Namespace = \"System.Net\"%>\r\n<%@ Import Namespace = \"System.IO\"%>\r\n<%@ Import Namespace = \"System.Text\"%>\r\n\r\n<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.0 Transitional//EN\" >\r\n<HTML>\r\n<HEAD>\r\n<script runat=\"server\">\r\n	void Button1_Click(object sender, System.EventArgs e)\r\n    {\r\n      string strTitle = TextBox1.Text;\r\n      string strDesc = TextBox2.Text;\r\n\r\n      Encoding encoding = Encoding.GetEncoding(\"GB2312\");\r\n\r\n      string postData = \"Title=\" + strTitle;\r\n      string strUrl = \"http://xml.sz.luohuedu.net/HttpReceiveData.aspx\";\r\n      postData += (\"&Desc=\" + strDesc);\r\n      byte[] data = encoding.GetBytes(postData);\r\n\r\n      // ׼������...\r\n      HttpWebRequest myRequest = (HttpWebRequest)WebRequest.Create(strUrl);\r\n      myRequest.Method = \"POST\";\r\n      myRequest.ContentType=\"application/x-www-form-urlencoded\";\r\n      myRequest.ContentLength = data.Length;\r\n      Stream newStream=myRequest.GetRequestStream();\r\n      // ��������\r\n      newStream.Write(data,0,data.Length);\r\n      newStream.Close();\r\n      Response.Redirect(\"HttpSendData.aspx\");\r\n    }\r\n</script>\r\n</HEAD>\r\n<body>\r\n  <form id=\"HTTPPost\" method=\"post\" runat=\"server\">\r\n    ���⣺<asp:TextBox id=\"TextBox1\" runat=\"server\"></asp:TextBox>\r\n    <br>\r\n    ���ݣ�\r\n    <br>\r\n    <asp:TextBox id=\"TextBox2\" runat=\"server\" TextMode=\"MultiLine\" Rows=\"10\" Columns=\"100\"></asp:TextBox>\r\n    <br>\r\n    <asp:Button id=\"Button1\" runat=\"server\" Text=\" ��  �� \" onclick=\"Button1_Click\"></asp:Button>\r\n  </form>\r\n</body>\r\n</HTML>\r\nHttpReceiveData.aspx\r\n\r\n<%@ Page Language=\"vb\"%>\r\n<%@ import Namespace = \"System\" %>\r\n<%@ import Namespace = \"System.Web.UI\" %>\r\n<%@ import Namespace = \"System.Web\" %>\r\n<script runat=\"server\">\r\nSub Page_Load(ByVal sender As System.Object, ByVal e As System.EventArgs)\r\n  If Request.ServerVariables(\"REQUEST_METHOD\").ToString() = \"POST\" Then\r\n    Dim connstr As String\r\n    connstr = \"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=\" + Server.MapPath(\"Test.mdb\")\r\n    Dim cn As New System.Data.OleDb.OleDbConnection(connstr)\r\n    Dim strSQL As String = \"INSERT INTO TestTable (Title,Description) VALUES(\'\" _\r\n    	+ Request.Form(\"Title\").ToString() + \"\',\'\" + Request.Form(\"Desc\").ToString() + \"\')\"\r\n    cn.Open()\r\n    Dim cmd As New System.Data.OleDb.OleDbCommand(strSQL, cn)\r\n    cmd.ExecuteNonQuery()\r\n    cn.Close()\r\n    cn.Dispose()\r\n    cmd.Dispose()\r\n  End If\r\nEnd Sub\r\n</script>\r\n', '0', '2017-09-08 09:49:15', null, '在ASP.NET中实现POST发送数据.txt');
