RT + 11;\r\n        private const int WM_CAP_SAVEDIB = WM_CAP_START + 25;\r\n        private const int WM_CAP_GRAB_FRAME = WM_CAP_START + 60;\r\n        private const int WM_CAP_SEQUENCE = WM_CAP_START + 62;\r\n        private const int WM_CAP_FILE_SET_CAPTURE_FILEA = WM_CAP_START + 20;\r\n        private const int WM_CAP_SEQUENCE_NOFILE =WM_CAP_START+ 63;\r\n        private const int WM_CAP_SET_OVERLAY =WM_CAP_START+ 51;\r\n        private const int WM_CAP_SET_PREVIEW =WM_CAP_START+ 50;\r\n        private const int WM_CAP_SET_CALLBACK_VIDEOSTREAM = WM_CAP_START +6;\r\n        private const int WM_CAP_SET_CALLBACK_ERROR=WM_CAP_START +2;\r\n        private const int WM_CAP_SET_CALLBACK_STATUSA= WM_CAP_START +3;\r\n        private const int WM_CAP_SET_CALLBACK_FRAME= WM_CAP_START +5;\r\n        private const int WM_CAP_SET_SCALE=WM_CAP_START+ 53;\r\n        private const int WM_CAP_SET_PREVIEWRATE=WM_CAP_START+ 52;\r\n        private IntPtr hWndC;\r\n        private bool bStat = false;\r\n        private IntPtr mControlPtr;\r\n        private int mWidth;\r\n        private int mHeight;\r\n        private int mLeft;\r\n        private int mTop;\r\n        ///\r\n        /// ��ʼ������ͷ\r\n        ///\r\n        /// �ؼ��ľ��\r\n        /// ��ʼ��ʾ����߾�\r\n        /// ��ʼ��ʾ���ϱ߾�\r\n        /// Ҫ��ʾ�Ŀ��\r\n        /// Ҫ��ʾ�ĳ���\r\n        public Pick(IntPtr handle, int left, int top, int width,int height)\r\n        {\r\n            mControlPtr = handle;\r\n            mWidth = width;\r\n            mHeight = height;\r\n            mLeft = left;\r\n            mTop = top;\r\n        }\r\n        [DllImport(\"avicap32.dll\")]\r\n        private static extern IntPtr capCreateCaptureWindowA(byte[] lpszWindowName, int dwStyle, int x, int y, int nWidth, int nHeight, IntPtr hWndParent, int nID);\r\n        [DllImport(\"avicap32.dll\")]\r\n        private static extern int capGetVideoFormat(IntPtr hWnd, IntPtr psVideoFormat, int wSize );\r\n        [DllImport(\"User32.dll\")]\r\n        private static extern bool SendMessage(IntPtr hWnd, int wMsg, int wParam, int lParam);\r\n        ///\r\n        /// ��ʼ��ʾͼ��\r\n        ///\r\n        public void Start()\r\n        {\r\n            if (bStat)\r\n            return;\r\n            bStat = true;\r\n            byte[] lpszName = new byte[100];\r\n            hWndC = capCreateCaptureWindowA(lpszName,WS_CHILD|WS_VISIBLE ,mLeft,mTop,mWidth,mHeight,mControlPtr,0);\r\n            if (hWndC.ToInt32() != 0)\r\n            {\r\n                SendMessage(hWndC, WM_CAP_SET_CALLBACK_VIDEOSTREAM, 0, 0);\r\n                SendMessage(hWndC, WM_CAP_SET_CALLBACK_ERROR, 0, 0);\r\n                SendMessage(hWndC, WM_CAP_SET_CALLBACK_STATUSA, 0, 0);\r\n                SendMessage(hWndC, WM_CAP_DRIVER_CONNECT, 0, 0);\r\n                SendMessage(hWndC, WM_CAP_SET_SCALE, 1, 0);\r\n                SendMessage(hWndC, WM_CAP_SET_PREVIEWRATE, 66, 0);\r\n                SendMessage(hWndC, WM_CAP_SET_OVERLAY, 1, 0);\r\n                SendMessage(hWndC, WM_CAP_SET_PREVIEW, 1, 0);\r\n            }\r\n            return;\r\n        }\r\n        ///\r\n        /// ֹͣ��ʾ\r\n        ///\r\n        public void Stop()\r\n        {\r\n            SendMessage(hWndC, WM_CAP_DRIVER_DISCONNECT, 0, 0);\r\n            bStat = false;\r\n        } \r\n        ///\r\n        /// ץͼ\r\n        ///\r\n        /// Ҫ����bmp�ļ���·��\r\n        public void GrabImage(string path)\r\n       // public void GrabImage( )\r\n        {\r\n            IntPtr hBmp = Marshal.StringToHGlobalAnsi(path);\r\n            SendMessage(hWndC,WM_CAP_SAVEDIB,0,hBmp.ToInt32());\r\n        }\r\n        ///\r\n        /// ¼��\r\n        ///\r\n        /// Ҫ����avi�ļ���·��\r\n        public void Kinescope(string path)\r\n        {\r\n            IntPtr hBmp = Marshal.StringToHGlobalAnsi(path);\r\n            SendMessage(hWndC,WM_CAP_FILE_SET_CAPTURE_FILEA,0,hBmp.ToInt32());\r\n            SendMessage(hWndC, WM_CAP_SEQUENCE, 0, 0);\r\n        }\r\n        ///\r\n        /// ֹͣ¼��\r\n        ///\r\n        public void StopKinescope()\r\n        {\r\n            SendMessage(hWndC, WM_CAP_STOP, 0, 0);\r\n        }\r\n     }\r\n} \r\n', '0', '2017-09-08 09:49:16', null, '摄像头.txt');
