INSERT INTO `txt` VALUES ('901', '2', '本地xml和List和互转.将List转化为xml.然后转化为List.泛型.txt', '  /// <summary>\r\n        /// ������ǩxml�ļ���������\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static string GenerateTagsXMLFile()\r\n        {\r\n            string MapPath = AppDomain.CurrentDomain.BaseDirectory;\r\n            string filePath = MapPath + \"TagsXML\";\r\n            if (!Directory.Exists(filePath))\r\n            {\r\n                Directory.CreateDirectory(filePath);\r\n            }\r\n            string fileFullName = filePath + \"\\\\Tags.xml\";\r\n            XmlDocument xmlDoc = GenerateTagsXml();\r\n            xmlDoc.Save(fileFullName);\r\n            return \"yes\";\r\n        }\r\n        /// <summary>\r\n        /// ����Profile XmlDocument\r\n        /// </summary>\r\n        /// <param name=\"emp\"></param>\r\n        /// <returns></returns>\r\n        private static XmlDocument GenerateTagsXml()\r\n        {\r\n            XmlDocument xmlDoc = new XmlDocument();\r\n\r\n            //xml ����\r\n            XmlDeclaration declaration = xmlDoc.CreateXmlDeclaration(\"1.0\", \"UTF-8\", \"yes\");\r\n            xmlDoc.AppendChild(declaration);\r\n\r\n            ////Profile\r\n            XmlElement rootElement = xmlDoc.CreateElement(\"TagsList\");\r\n            xmlDoc.AppendChild(rootElement);\r\n            List<Tags> tagElements = _interface.GetALLTags();//��ȡȫ����ǩ\r\n\r\n            foreach (var item in tagElements)\r\n            {\r\n                XmlElement element = xmlDoc.CreateElement<Tags>(item);\r\n                rootElement.AppendChild(element);\r\n            }\r\n            return xmlDoc;\r\n        }\r\n        /// <summary>\r\n        /// ��ȡxml�ļ���List\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static List<Tags> GetTagsByXML()\r\n        {\r\n            try\r\n            {\r\n                System.Xml.XmlDocument xmlDoc = new System.Xml.XmlDocument();\r\n                string MapPath = AppDomain.CurrentDomain.BaseDirectory;\r\n                string filePath = MapPath + \"TagsXML\";\r\n                string fileFullName = filePath + \"\\\\Tags.xml\";\r\n                xmlDoc.Load(fileFullName);\r\n                List<Tags> list = XMLHelper.GetEntityByXml<Tags>(xmlDoc);\r\n                if (list == null)\r\n                {\r\n                    GenerateTagsXMLFile();\r\n                    return _interface.GetALLTags();//��ȡȫ����ǩ\r\n                }\r\n                else\r\n                {\r\n                    return list;\r\n                }\r\n            }\r\n            catch (Exception)\r\n            {\r\n                GenerateTagsXMLFile();\r\n                return _interface.GetALLTags();//��ȡȫ����ǩ\r\n            }\r\n        }\r\n   \r\n\r\n\r\n\r\n\r\n=========================\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nnamespace System.Xml\r\n{\r\n\r\n    public static class XMLHelper\r\n    {\r\n        public static T GetEntityByXml<T>(XmlElement element) where T : new()\r\n        {\r\n            T temp = new T();\r\n            \r\n            foreach (var p in typeof(T).GetProperties())\r\n            {\r\n                switch (p.PropertyType.ToString())\r\n                {\r\n                    case \"System.Int32\":\r\n                        p.SetValue(temp, element.GetAttribute(p.Name) != \"\" ? int.Parse(element.GetAttribute(p.Name)) : 0, null);\r\n                        break;\r\n                    case \"System.Decimal\":\r\n                        p.SetValue(temp, element.GetAttribute(p.Name) != \"\" ? decimal.Parse(element.GetAttribute(p.Name)) : 0, null);\r\n                        break;\r\n                    case \"System.DateTime\":\r\n                        p.SetValue(temp, element.GetAttribute(p.Name) != \"\" ? DateTime.Parse(element.GetAttribute(p.Name)) : DateTime.Now, null);\r\n                        break;\r\n                    case \"System.Nullable`1[System.DateTime]\":\r\n                        p.SetValue(temp, element.GetAttribute(p.Name) != \"\" ? DateTime.Parse(element.GetAttribute(p.Name)) : DateTime.Now, null);\r\n                        break;\r\n                    case \"System.Boolean\":\r\n                        p.SetValue(temp, element.GetAttribute(p.Name) != \"\" ? bool.Parse(element.GetAttribute(p.Name)) :false, null);\r\n                        break;\r\n                    default:\r\n                        p.SetValue(temp, element.GetAttribute(p.Name), null);\r\n                        break;\r\n                }\r\n\r\n            }\r\n            return temp;\r\n        }\r\n\r\n        public static List<T> GetEntityByXml<T>(this XmlDocument xmlDoc) where T : new()\r\n        {\r\n            List<T> temp = new List<T>();\r\n            foreach (XmlElement element in xmlDoc.GetElementsByTagName(typeof(T).Name.ToString()))\r\n            {\r\n                temp.Add(GetEntityByXml<T>(element));\r\n            }\r\n            return temp;\r\n        }\r\n\r\n        public static XmlElement CreateElement<T>(this XmlDocument xmlDoc, Object data)\r\n        {\r\n            XmlElement element = xmlDoc.CreateElement(typeof(T).Name.ToString());\r\n            foreach (var temp in typeof(T).GetProperties())\r\n            {\r\n                element.SetAttribute(temp.Name, XML_SpecialCharacters_Escape( temp.GetValue(data, null)==null?\"\":(temp.GetValue(data, null).ToString())));\r\n            }\r\n            return element;\r\n        }\r\n\r\n        public static string XML_SpecialCharacters_Escape(string str)\r\n        {\r\n            var ctemp = str.Replace(\"&\", \"&amp;\");//&#38\r\n            ctemp = ctemp.Replace(\"\'\", \"&apos;\");//&#39\r\n            ctemp = ctemp.Replace(\"\\\"\", \"&quot;\");//&#34\r\n            ctemp = ctemp.Replace(\">\", \"&gt;;\");//&#62\r\n            ctemp = ctemp.Replace(\"<\", \"&lt;\");//&#60\r\n            return ctemp;\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:16', null, '本地xml和List和互转.将List转化为xml.然后转化为List.泛型.txt');
INSERT INTO `txt` VALUES ('902', '2', '本地路径转化为URL相对路径.txt', '   //本地路径转换成URL相对路径\r\n     private string urlconvertor(string imagesurl1)\r\n    {\r\n        string tmpRootDir = Server.MapPath(System.Web.HttpContext.Current.Request.ApplicationPath.ToString());//获取程序根目录\r\n        string imagesurl2 = imagesurl1.Replace(tmpRootDir, \"\"); //转换成相对路径\r\n        imagesurl2 = imagesurl2.Replace(@\"\\\", @\"/\");\r\n        //imagesurl2 = imagesurl2.Replace(@\"Aspx_Uc/\", @\"\");\r\n        return imagesurl2;\r\n    }\r\n    //相对路径转换成服务器本地物理路径\r\n    private string urlconvertorlocal(string imagesurl1)\r\n    {\r\n        string tmpRootDir = Server.MapPath(System.Web.HttpContext.Current.Request.ApplicationPath.ToString());//获取程序根目录\r\n        string imagesurl2 = tmpRootDir + imagesurl1.Replace(@\"/\", @\"\\\"); //转换成绝对路径\r\n         return imagesurl2;\r\n    }\r\n', '0', '2017-09-08 09:49:16', null, '本地路径转化为URL相对路径.txt');
