INSERT INTO `txt` VALUES ('931', '2', '百度地图.api.txt', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<meta name=\"viewport\" content=\"initial-scale=1.0, user-scalable=no\" />\r\n<script type=\"text/javascript\" src=\"http://api.map.baidu.com/api?v=1.5&ak=F9431c689fa95f23c02b848f764f461d\"></script>\r\n<title>�ٶȵ�ͼ��Hello, World</title>\r\n<style type=\"text/css\">\r\nbody, html,#allmap {width: 100%;height: 100%;overflow: hidden;margin:0;}\r\n</style>\r\n</head>\r\n<body>\r\n<div id=\"allmap\"></div>\r\n</body>\r\n</html>\r\n<script type=\"text/javascript\">\r\nvar map = new BMap.Map(\"allmap\");            // ����Mapʵ��\r\nvar point = new BMap.Point(116.404, 39.915);    // ����������\r\nmap.centerAndZoom(point,15);                     // ��ʼ����ͼ,�������ĵ�����͵�ͼ����\r\nmap.enableScrollWheelZoom();                            //���ù��ַŴ���С\r\n</script>\r\n\r\n\r\n====================�Լ�����key:F9431c689fa95f23c02b848f764f461d==================\r\n2013��7��23��22:59:25  �ٶ������key.Email:20444465@qq.com 13466699775\r\n', '0', '2017-09-08 09:49:17', null, '百度地图.api.txt');
INSERT INTO `txt` VALUES ('932', '2', '百度地图.接口.api.txt', '<!--���ðٶȵ�ͼAPI--><style type=\"text/css\">\r\n\r\nhtml,body{margin:0;padding:0;}\r\n\r\n    .iw_poi_title {color:#CC5522;font-size:14px;font-weight:bold;overflow:hidden;padding-right:13px;white-space:nowrap}\r\n\r\n    .iw_poi_content {font:12px arial,sans-serif;overflow:visible;padding-top:4px;white-space:-moz-pre-wrap;word-wrap:break-word}</style>\r\n\r\n<script type=\"text/javascript\" src=\"http://api.map.baidu.com/api?key=&v=1.1&services=true\"></script><!--�ٶȵ�ͼ����--><div id=\"dituContent\" style=\"border-right: #ccc 1px solid; border-top: #ccc 1px solid; border-left: #ccc 1px solid; width: 697px; border-bottom: #ccc 1px solid; height: 550px\">\r\n\r\n&nbsp;</div>\r\n\r\n<script type=\"text/javascript\">\r\n\r\n    //�����ͳ�ʼ����ͼ������\r\n\r\n    function initMap(){\r\n\r\n        createMap();//������ͼ\r\n\r\n        setMapEvent();//���õ�ͼ�¼�\r\n\r\n        addMapControl();//���ͼ��ӿؼ�\r\n\r\n        addMarker();//���ͼ�����marker\r\n\r\n    }\r\n\r\n    \r\n\r\n    //������ͼ������\r\n\r\n    function createMap(){\r\n\r\n        var map = new BMap.Map(\"dituContent\");//�ڰٶȵ�ͼ�����д���һ����ͼ\r\n\r\n        var point = new BMap.Point(113.962194,22.542047);//����һ�����ĵ�����\r\n\r\n        map.centerAndZoom(point,18);//�趨��ͼ�����ĵ�����겢����ͼ��ʾ�ڵ�ͼ������\r\n\r\n        window.map = map;//��map�����洢��ȫ��\r\n\r\n    }\r\n\r\n    \r\n\r\n    //��ͼ�¼����ú�����\r\n\r\n    function setMapEvent(){\r\n\r\n        map.enableDragging();//���õ�ͼ��ק�¼���Ĭ������(�ɲ�д)\r\n\r\n        map.enableScrollWheelZoom();//���õ�ͼ���ַŴ���С\r\n\r\n        map.enableDoubleClickZoom();//�������˫���Ŵ�Ĭ������(�ɲ�д)\r\n\r\n        map.enableKeyboard();//���ü����������Ҽ��ƶ���ͼ\r\n\r\n    }\r\n\r\n    \r\n\r\n    //��ͼ�ؼ���Ӻ�����\r\n\r\n    function addMapControl(){\r\n\r\n        //���ͼ��������ſؼ�\r\n\r\nvar ctrl_nav = new BMap.NavigationControl({anchor:BMAP_ANCHOR_TOP_LEFT,type:BMAP_NAVIGATION_CONTROL_LARGE});\r\n\r\nmap.addControl(ctrl_nav);\r\n\r\n        //���ͼ���������ͼ�ؼ�\r\n\r\nvar ctrl_ove = new BMap.OverviewMapControl({anchor:BMAP_ANCHOR_BOTTOM_RIGHT,isOpen:0});\r\n\r\nmap.addControl(ctrl_ove);\r\n\r\n        //���ͼ����ӱ����߿ؼ�\r\n\r\nvar ctrl_sca = new BMap.ScaleControl({anchor:BMAP_ANCHOR_BOTTOM_LEFT});\r\n\r\nmap.addControl(ctrl_sca);\r\n\r\n    }\r\n\r\n    \r\n\r\n    //��ע������\r\n\r\n    var markerArr = [{title:\"��ŵ������\",content:\"�����б�����������޺�Ṥҵ��\",point:\"113.870212|22.634686\",isOpen:0,icon:{w:21,h:21,l:46,t:0,x:6,lb:5}}\r\n\r\n,{title:\"TELNOVA\",content:\"Office&nbsp;address:F/7,&nbsp;Jiuzhou&nbsp;Electric&nbsp;Building,&nbsp;Southern&nbsp;No.12&nbsp;Rd.&nbsp;High-tech&nbsp;Industrial&nbsp;Park,&nbsp;Nanshan&nbsp;District,\",point:\"113.962325|22.542089\",isOpen:0,icon:{w:21,h:21,l:46,t:0,x:6,lb:5}}\r\n\r\n];\r\n\r\n    //����marker\r\n\r\n    function addMarker(){\r\n\r\n        for(var i=0;i<markerArr.length;i++){\r\n\r\n            var json = markerArr[i];\r\n\r\n            var p0 = json.point.split(\"|\")[0];\r\n\r\n            var p1 = json.point.split(\"|\")[1];\r\n\r\n            var point = new BMap.Point(p0,p1);\r\n\r\nvar iconImg = createIcon(json.icon);\r\n\r\n            var marker = new BMap.Marker(point,{icon:iconImg});\r\n\r\nvar iw = createInfoWindow(i);\r\n\r\nvar label = new BMap.Label(json.title,{\"offset\":new BMap.Size(json.icon.lb-json.icon.x+10,-20)});\r\n\r\nmarker.setLabel(label);\r\n\r\n            map.addOverlay(marker);\r\n\r\n            label.setStyle({\r\n\r\n                        borderColor:\"#808080\",\r\n\r\n                        color:\"#333\",\r\n\r\n                        cursor:\"pointer\"\r\n\r\n            });\r\n\r\n(function(){\r\n\r\nvar index = i;\r\n\r\nvar _iw = createInfoWindow(i);\r\n\r\nvar _marker = marker;\r\n\r\n_marker.addEventListener(\"click\",function(){\r\n\r\n   this.openInfoWindow(_iw);\r\n\r\n   });\r\n\r\n   _iw.addEventListener(\"open\",function(){\r\n\r\n   _marker.getLabel().hide();\r\n\r\n   })\r\n\r\n   _iw.addEventListener(\"close\",function(){\r\n\r\n   _marker.getLabel().show();\r\n\r\n   })\r\n\r\nlabel.addEventListener(\"click\",function(){\r\n\r\n   _marker.openInfoWindow(_iw);\r\n\r\n   })\r\n\r\nif(!!json.isOpen){\r\n\r\nlabel.hide();\r\n\r\n_marker.openInfoWindow(_iw);\r\n\r\n}\r\n\r\n})()\r\n\r\n        }\r\n\r\n    }\r\n\r\n    //����InfoWindow\r\n\r\n    function createInfoWindow(i){\r\n\r\n        var json = markerArr[i];\r\n\r\n        var iw = new BMap.InfoWindow(\"<b class=\'iw_poi_title\' title=\'\" + json.title + \"\'>\" + json.title + \"</b><div class=\'iw_poi_content\'>\"+json.content+\"</div>\");\r\n\r\n        return iw;\r\n\r\n    }\r\n\r\n    //����һ��Icon\r\n\r\n    function createIcon(json){\r\n\r\n        var icon = new BMap.Icon(\"http://openapi.baidu.com/map/images/us_mk_icon.png\", new BMap.Size(json.w,json.h),{imageOffset: new BMap.Size(-json.l,-json.t),infoWindowOffset:new BMap.Size(json.lb+5,1),offset:new BMap.Size(json.x,json.h)})\r\n\r\n        return icon;\r\n\r\n    }\r\n\r\n    \r\n\r\n    initMap();//�����ͳ�ʼ����ͼ\r\n\r\n</script>\r\n', '0', '2017-09-08 09:49:17', null, '百度地图.接口.api.txt');
INSERT INTO `txt` VALUES ('933', '2', '省市二级联动(从数据库读取).txt', '==========================================Aspx===================================  \r\n      <asp:DropDownList ID=\"DropDownList1\" runat=\"server\" AutoPostBack=\"True\" \r\n            onselectedindexchanged=\"DropDownList1_SelectedIndexChanged\">\r\n            <asp:ListItem>��ѡ��ʡ��</asp:ListItem>\r\n        </asp:DropDownList>\r\n        <asp:DropDownList ID=\"DropDownList2\" runat=\"server\">\r\n        </asp:DropDownList>\r\n==========================================CodeBehind===================================\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Web;\r\nusing System.Web.UI;\r\nusing System.Web.UI.WebControls;\r\nusing DAL;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\n\r\npublic partial class _Default : System.Web.UI.Page \r\n{\r\n    Province province = null;\r\n    City city = null;\r\n    private DataTable dt = null;\r\n    protected void Page_Load(object sender, EventArgs e)\r\n    {\r\n        if (!IsPostBack)\r\n        {\r\n            province = new Province();\r\n            dt = new DataTable();\r\n            dt = province.QueryProvince();\r\n            DropDownList1.DataSource = dt;\r\n            DropDownList1.DataTextField = \"provinceName\";\r\n            DropDownList1.DataValueField = \"provinceId\";\r\n            DropDownList1.DataBind();\r\n        }\r\n        \r\n    }\r\n    protected void DropDownList1_SelectedIndexChanged(object sender, EventArgs e)\r\n    {\r\n        city = new City();\r\n        dt = new DataTable();\r\n        dt = city.QueryCity(DropDownList1.SelectedValue.ToString());\r\n        DropDownList2.DataSource = dt;\r\n        DropDownList2.DataTextField = \"cityName\";\r\n        DropDownList2.DataValueField = \"cityId\";\r\n        DropDownList2.DataBind();\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:17', null, '省市二级联动(从数据库读取).txt');
