INSERT INTO `txt` VALUES ('939', '2', '级联删除(存储过程).txt', 'use mybbs_db\r\n----------------------------------\r\n--ɾ������\r\n--exec p_deletesubject \"2\"  --���ô洢����\r\n-----------------------------------\r\ncreate PROCEDURE p_deletesubject\r\n@subjectId int\r\nas\r\nset nocount on\r\nbegin tran\r\n	declare @temp int\r\n	 delete from restores where postid in (select postid from posts where clumpid in (select clumpId from clumps where subjectId=@subjectId)) --ɾ���ظ�\r\n		set @temp=@@error\r\n	 delete from posts where postid in (select postid from posts where clumpId in (select clumpId from clumps where subjectId=@subjectId))    --ɾ������\r\n		set @temp=@@error\r\n	          update roles set roleFlag=0,subjectId=null,clumpId=null where clumpId in (select clumpId from clumps where subjectId=@subjectId)--������ð��İ�����ݽ�����ͨ��Ա\r\n		set @temp=@@error\r\n              delete from clumps where clumpId in (select clumpId from clumps where subjectId=@subjectId) --ɾ�����\r\n	set @temp=@@error\r\n              delete from subjects where subjectId=@subjectId             --ɾ�� ����\r\n    set @temp=@@error\r\n	set @temp=@temp+@@error\r\n\r\n	if @temp<>0\r\n		rollback\r\n	else\r\n		commit\r\n----------------------------------\r\n--ɾ�����\r\n--execute p_deleteclump \"2\" --����\r\n-----------------------------------\r\ncreate PROCEDURE p_deleteclump\r\n@clumpId int\r\nas\r\nset nocount on\r\nbegin tran\r\n	declare @temp int\r\n	--declare @idvalue int\r\n	 delete from restores where postid in (select postid from posts where clumpid=@clumpid) --ɾ���ظ�\r\n		set @temp=@@error\r\n	          delete from posts where postid in (select postid from posts where clumpId=@clumpId)    --ɾ������\r\n		set @temp=@@error\r\n	          update roles set roleFlag=0,subjectId=null,clumpId=null where clumpId=@clumpid   --������ð��İ�����ݽ�����ͨ��Ա\r\n		set @temp=@@error\r\n              delete from clumps where clumpId=@clumpId \r\n	\r\n	set @temp=@temp+@@error\r\n\r\n	if @temp<>0\r\n		rollback\r\n	else\r\n		commit\r\n------------------------------\r\n--ɾ������\r\n--exec p_deletepost \"14\"--����\r\n-------------------------------\r\nselect * from posts\r\nexec p_deletepost \"4\"\r\ncreate PROCEDURE p_deletepost\r\n@postId int\r\nas\r\nset nocount on\r\nbegin tran\r\n	declare @temp int,\r\n    @restorecount int\r\n	select @restorecount =count(*) from restores where postId=@postId\r\n	--declare @idvalue int\r\n		if @restorecount>0\r\n		begin\r\n		 delete from restores where postId =@postId --ɾ���ظ�\r\n				set @temp=@@error\r\n		end\r\n	\r\n	          delete from posts where postid=@postId --ɾ������\r\n		set @temp=@@error\r\n	set @temp=@temp+@@error\r\n\r\n	if @temp<>0\r\n		rollback\r\n	else\r\n		commit\r\n\r\n------------------------------------------------------------\r\n--ɾ���û�\r\n--exec P_deleteuser \"1\"  --����\r\n-----------------------------------------------------------\r\nselect * from users\r\nexec p_deleteuser \"1\"\r\ncreate PROCEDURE p_deleteuser\r\n@userId int\r\nas\r\nset nocount on\r\nbegin tran\r\n	 declare @temp int\r\n		set @temp=@@error\r\n	          delete from  roles where roleId in (select roleId from Roles where userId=@userId)        --ɾ����ɫ\r\n        set @temp=@@error\r\n              delete from restores where restoreId in (select restoreId from Restores where postId in (select PostId from posts where userId=@userId))\r\n                         --ɾ�����Ӷ�Ӧ�Ļظ�\r\n		set @temp=@@error\r\n		delete from posts where postId in (select postId from posts where userid=@userId)  --ɾ������\r\n	    set @temp=@@error\r\n              delete from  restores where restoreId in (select restoreId from restores where userId=@userId )--ɾ���û���Ӧ�Ļظ�\r\n        set @temp=@@error\r\n       delete from  friends where userId=@userId or userFriendId=@userId          --ɾ������\r\n              delete from users where userId=@userId                           --ɾ���û�\r\n	    set @temp=@temp+@@error\r\n	if @temp<>0\r\n		rollback\r\n	else\r\n		commit\r\n--------------------------------------------------------------------\r\n', '0', '2017-09-08 09:49:17', null, '级联删除(存储过程).txt');
INSERT INTO `txt` VALUES ('940', '2', '纯JS限制文本框字数(ASP.NET).txt', '<%@ Page Language=\"C#\" AutoEventWireup=\"true\" CodeFile=\"text_area.aspx.cs\" Inherits=\"text_area\" %>\r\n\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head runat=\"server\">\r\n    <title>��JS�����ı�������</title>\r\n    <script type=\"text/javascript\">\r\n        window.onload = function() {\r\n            document.getElementById(\"txtContent\").onkeyup = function() {\r\n                var len = this.value.length;\r\n                var tmp = 10 - len;\r\n                if (tmp <= 0) {\r\n                    this.value = this.value.substring(0, 10);\r\n                    document.getElementById(\"mes\").innerHTML = \"������������ 0 ���ַ�\";\r\n                } else {\r\n                    document.getElementById(\"mes\").innerHTML = \"������������ \" + tmp + \" ���ַ�\";\r\n                }\r\n            }\r\n        }\r\n    </script>\r\n\r\n</head>\r\n<body>\r\n    <form id=\"form1\" runat=\"server\">\r\n    <asp:TextBox ID=\"txtContent\" TextMode=\"MultiLine\" runat=\"server\" Height=\"190px\" Width=\"439px\"></asp:TextBox>\r\n    <div id=\"mes\">\r\n        ������������ 10 ���ַ�\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:17', null, '纯JS限制文本框字数(ASP.NET).txt');
INSERT INTO `txt` VALUES ('941', '2', '线点过滤掉RPO的2017年的数据.txt', '#region ����RPO2017���Ժ������(��Щ���ݲ���������2017��1��16��09)\r\n\r\n            List<queryClause> queryClauseListgroup_RPO = new List<queryClause>();\r\n            queryClauseListgroup_RPO.Add(new queryClause()\r\n            {\r\n                booleanQ = FacetSearchServiceHelp.GetPatternOfSearch(\r\n                new SearchQuery\r\n                {\r\n                    fields = new string[] { \"DepartmentA\" },\r\n                    values = new string[] { \"0102\" },\r\n                    QCtype = 0,\r\n                    searchType = \"JQ\"\r\n                }),\r\n                type = 2,\r\n                logic = \"+\"\r\n            });\r\n            queryClauseListgroup_RPO.Add(new queryClause()\r\n            {\r\n                booleanQ = FacetSearchServiceHelp.GetPatternOfSearch(\r\n                 new SearchQuery\r\n                 {\r\n                     fields = new string[] { \"BaseUpdateTime\" },\r\n                     values = new string[] { \"2017\" + \"-\" + \"01\" + \"-\" + \"01\" + \"\\\\T00:00:00.000\\\\Z\" + \"*\" + \"2054\" + \"-\" + \"01\" + \"-\" + \"01\" + \"\\\\T00:00:00.000\\\\Z\" },\r\n                     QCtype = 1,\r\n                     searchType = \"JQ\"\r\n                 }),\r\n                type = 2,\r\n                logic = \"+\"\r\n            });\r\n\r\n            queryClause All_Pattern_RPO = new queryClause()\r\n            {\r\n                booleanQ = new booleanQuery()\r\n                {\r\n                    clasuses = queryClauseListgroup_RPO.ToArray()\r\n                },\r\n                type = 2,\r\n                logic = \"-\"\r\n            };\r\n\r\n            MainQueryClauseList.Add(All_Pattern_RPO);\r\n#endregion\r\n', '0', '2017-09-08 09:49:17', null, '线点过滤掉RPO的2017年的数据.txt');
INSERT INTO `txt` VALUES ('942', '2', '组织结构.html.css.js.txt', '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>JS����֯�ṹͼ</title>\r\n    <style type=\"text/css\">\r\n        .OrgBox {\r\n            font-size: 12px;\r\n            padding: 5px 5px 5px 5px;\r\n            clear: left;\r\n            float: left;\r\n            text-align: center;\r\n            position: absolute;\r\n            width: 70px;\r\n            height: 106px;\r\n            border: 1px solid #000;\r\n        }\r\n\r\n            .OrgBox img {\r\n                width: 60px;\r\n                height: 70px;\r\n            }\r\n\r\n            .OrgBox div {\r\n                color: #FFA500;\r\n                font-weight: 800;\r\n            }\r\n    </style>\r\n</head>\r\n<body>\r\n    <script language=\"javascript\">\r\n        function $(id) {\r\n            return document.getElementById(id)\r\n        }\r\n        function offset(node) {\r\n            var x = node.offsetLeft;\r\n            var y = node.offsetTop;\r\n            var w = node.offsetWidth;\r\n            var h = node.offsetHeight;\r\n            var parent = node.offsetParent;\r\n            while (parent != null) {\r\n                x += parent.offsetLeft;\r\n                y += parent.offsetTop;\r\n                parent = parent.offsetParent;\r\n            }\r\n            if (w == 0) {\r\n                w += parseInt(node.currentStyle.width);\r\n                w += parseInt(node.currentStyle.paddingRight);\r\n                w += parseInt(node.currentStyle.paddingLeft);\r\n                w += parseInt(node.currentStyle.borderWidth) * 2;\r\n            }\r\n            if (h == 0) {\r\n                h += parseInt(node.currentStyle.height);\r\n                h += parseInt(node.currentStyle.paddingTop);\r\n                h += parseInt(node.currentStyle.paddingBottom);\r\n                h += parseInt(node.currentStyle.borderWidth) * 2;\r\n            }\r\n            return {\r\n                x: x,\r\n                y: y,\r\n                w: w,\r\n                h: h\r\n            };\r\n        }\r\n\r\n        function OrgNode() {\r\n            this.Text = null;\r\n            this.Link = null;\r\n            this.Description = null;\r\n            this.BoxWidth = null;\r\n            this.BoxHeight = null;\r\n            this.parentNode = null;\r\n            this.NodeGroupId = null; //ͬһ��ļ������,���㿪ʼ\r\n            this.NodeOrderId = null; //ͬһ���е����,���㿪ʼ\r\n            this.TopLine = null;\r\n            this.BottomLine = null;\r\n            this.Depth = null;\r\n            this.Top = null;\r\n            this.Left = null;\r\n            this.Type = null;\r\n            this.Nodes = [];\r\n            this.customParam = []; //�ڵ��Զ������\r\n            var This = this;\r\n            this.Nodes.Add = function (OrgNode_) {\r\n                OrgNode_.parentNode = This;\r\n                This.Nodes[This.Nodes.length] = OrgNode_;\r\n            }\r\n            this.Box = null;\r\n            this.Templet = null;\r\n            this.Id = \"OrgNode_\" + GetRandomId(20);\r\n            this.inIt = function () {\r\n                if (this.inIted == true)\r\n                    return;\r\n                var tempDiv = document.createElement(\"DIV\");\r\n                document.body.appendChild(tempDiv);\r\n                var tempHTML = this.Templet;\r\n                tempHTML = tempHTML.replace(\"{Id}\", this.Id);\r\n                tempHTML = tempHTML.replace(\"{Text}\", this.Text);\r\n                tempHTML = (this.Link == null) ? tempHTML.replace(\"{Link}\",\r\n                        \"JavaScript:void(0)\") : tempHTML.replace(\"{Link}\",\r\n                        this.Link);\r\n                tempHTML = tempHTML.replace(\"{Description}\", this.Description);\r\n                for (var Param_ in this.customParam) {\r\n                    tempHTML = tempHTML.replace(\"{\" + Param_ + \"}\",\r\n                            this.customParam[Param_]);\r\n                }\r\n                tempDiv.innerHTML = tempHTML;\r\n                this.Box = $(this.Id);\r\n\r\n                if (this.BoxWidth != null) {\r\n                    if (offset(this.Box).w < this.BoxWidth) {\r\n                        this.Box.style.width = this.BoxWidth + \"px\";\r\n                        if (offset(this.Box).w > this.BoxWidth) {\r\n                            this.Box.style.width = (this.BoxWidth - (offset(this.Box).w - this.BoxWidth))\r\n                                    + \"px\";\r\n                        }\r\n                    }\r\n                }\r\n\r\n                if (this.BoxHeight != null) {\r\n                    if (offset(this.Box).h < this.BoxHeight) {\r\n                        this.Box.style.height = this.BoxHeight + \"px\";\r\n                        if (offset(this.Box).h > this.BoxHeight) {\r\n                            this.Box.style.height = (this.BoxHeight - (offset(this.Box).h - this.BoxHeight))\r\n                                    + \"px\";\r\n                        }\r\n                    }\r\n                }\r\n                this.Width = offset(this.Box).w;\r\n                this.Height = offset(this.Box).h;\r\n                this.inIted = true;\r\n            }\r\n            function GetRandomId(n_) {\r\n                var litter = \"abcdefghijklmnopqrstuvwxyz\"\r\n                litter += litter.toUpperCase()\r\n                litter += \"1234567890\";\r\n                var idRnd = \"\";\r\n                for (var i = 1; i <= n_; i++) {\r\n                    idRnd += litter.substr((0 + Math.round(Math.random()\r\n                            * (litter.length - 0))), 1)\r\n                }\r\n                return idRnd;\r\n            }\r\n        }\r\n        function OrgShow(OrgNode_) {\r\n            this.LineSize = 2;\r\n            this.LineColor = \"#000000\";\r\n            this.IntervalWidth = 100;\r\n            this.IntervalHeight = 50;\r\n            this.Top = 0;\r\n            this.Left = 0;\r\n            this.Depth = 0;\r\n            this.Nodes = [];\r\n            this.DepthGroup = []; //this.DepthGroup[n].Nodes ����ڵ㼯��\r\n            //this.DepthGroup[n].NodeGroups[m] ����ڵ㰴�ϲ���༯�� this.DepthGroup[n].NodeGroups[m][k]����ڵ�\r\n            var This = this;\r\n            this.BoxWidth = null;\r\n            this.BoxHeight = null;\r\n            this.BoxTemplet = null;\r\n            this.ShowType = null;\r\n            this.Run = function () {\r\n                BoxInit(OrgNode_);\r\n                GetDepth(OrgNode_);\r\n                SetDepthsHeight()//���ò���߶�\r\n\r\n                //***************************\r\n                for (var n = 1; n <= this.Depth; n++) {//���ö�����\r\n                    var tempTop = this.Top + GetDepthHeightToRoot(n);\r\n                    var tempNodes = this.DepthGroup[n].Nodes;\r\n                    for (var m = 0; m < tempNodes.length; m++) {\r\n                        tempNodes[m].Top = tempTop;\r\n                    }\r\n                }\r\n                //***************************\r\n                for (var n = this.Depth; n >= 1; n--) {// ���������\r\n                    var DepthNodes = this.DepthGroup[n].Nodes;\r\n                    if (n == this.Depth) {\r\n                        for (var m = 0; m < DepthNodes.length; m++) {\r\n                            if (m == 0) {\r\n                                DepthNodes[m].Left = 0;\r\n                            } else {\r\n                                DepthNodes[m].Left = DepthNodes[m - 1].Left\r\n                                        + DepthNodes[m - 1].Width + this.IntervalWidth;\r\n                            }\r\n                        }\r\n                    } else {\r\n                        var flag = false;\r\n                        for (var m = 0; m < DepthNodes.length; m++) {\r\n                            // �����ӽڵ�Ľڵ��Ƿ����\r\n                            if (DepthNodes[m].Nodes.length != 0) {\r\n                                flag = true\r\n                                var tempNodeLeft_ = GetParentLeftByNode(DepthNodes[m].Nodes[0]);\r\n                                tempNodeLeft_ -= (DepthNodes[m].Width / 2);\r\n                                DepthNodes[m].Left = tempNodeLeft_;\r\n                            } else {\r\n                                if (flag) {\r\n                                    adjustBox(DepthNodes[m], m);\r\n                                }\r\n                            }\r\n                        }\r\n                        for (var m = 0; m < DepthNodes.length; m++) {\r\n                            if (DepthNodes[m].Left == null) {\r\n                                SetLeftByDepthNode(DepthNodes, m, \"LTR\");\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n                SetDepthGroupWidth();//����Ⱥ����\r\n                var MaxLeftValue = this.Nodes[0].Left;\r\n                for (var n = 1; n < this.Nodes.length; n++) {//ȡ����С�����\r\n                    if (MaxLeftValue > this.Nodes[n].Left) {\r\n                        MaxLeftValue = this.Nodes[n].Left;\r\n                    }\r\n                }\r\n                MaxLeftValue = (-MaxLeftValue) + this.Left;\r\n                for (var n = 0; n < this.Nodes.length; n++) {//�������þ���\r\n                    this.Nodes[n].Left += MaxLeftValue;\r\n                    this.Nodes[n].Box.style.left = this.Nodes[n].Left + \"px\"\r\n                    this.Nodes[n].Box.style.top = this.Nodes[n].Top + \"px\"\r\n                }\r\n\r\n                //***************************\r\n                for (var n = 1; n <= this.Depth; n++) {//����������\r\n                    var tempNodes = this.DepthGroup[n].Nodes;\r\n                    for (var m = 0; m < tempNodes.length; m++) {\r\n                        if (n != this.Depth) {//���õ�����\r\n                            if (tempNodes[m].Nodes.length != 0) {\r\n                                var tempLineLeft = tempNodes[m].Left\r\n                                        + (tempNodes[m].Width / 2);\r\n                                var tempLineHeight = ((this.IntervalHeight - this.LineSize) / 2);\r\n                                tempLineHeight += (this.DepthGroup[n].Height - tempNodes[m].Height);\r\n                                var tempLineTop = tempNodes[m].Top\r\n                                        + tempNodes[m].Height;\r\n                                var tempBottomLine = new CreateLine(2, tempLineTop,\r\n                                        tempLineLeft, tempLineHeight,\r\n                                        this.LineSize, this.LineColor,\r\n                                        \"LineBottom_\" + tempNodes[m].Id);\r\n                                tempNodes[m].BottomLine = tempBottomLine.Line;\r\n                            }\r\n                        }\r\n                        if (n != 1) {//���ö�����\r\n                            var tempLineLeft = tempNodes[m].Left\r\n                                    + (tempNodes[m].Width / 2);\r\n                            var tempLineHeight = ((this.IntervalHeight - this.LineSize) / 2);\r\n                            var tempLineTop = tempNodes[m].Top - tempLineHeight;\r\n                            if (this.DepthGroup[tempNodes[m].Depth].NodeGroups[tempNodes[m].NodeGroupId].length == 1) {//���ֻ��һ���ڵ�\r\n                                var tempBottomLineHeight = parseFloat(tempNodes[m].parentNode.BottomLine.style.height)\r\n                                        + this.LineSize;\r\n                                tempNodes[m].parentNode.BottomLine.style.height = (tempLineHeight + tempBottomLineHeight)\r\n                                        + \"px\";\r\n                            } else {\r\n                                var temptopLine = new CreateLine(2, tempLineTop,\r\n                                        tempLineLeft, tempLineHeight,\r\n                                        this.LineSize, this.LineColor, \"LineTop_\"\r\n                                                + tempNodes[m].Id);\r\n                                tempNodes[m].TopLine = temptopLine.Line;\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n\r\n                for (var n = 2; n <= this.Depth; n++) {//���ú�����\r\n                    var tempNodeGroups_ = this.DepthGroup[n].NodeGroups;\r\n                    for (var m = 0; m < tempNodeGroups_.length; m++) {\r\n                        if (tempNodeGroups_[m].length != 1) {\r\n                            var tempLineWidth = tempNodeGroups_[m].Width\r\n                                    - (tempNodeGroups_[m][0].Width / 2)\r\n                                    + this.LineSize;\r\n                            tempLineWidth -= (tempNodeGroups_[m][tempNodeGroups_[m].length - 1].Width / 2);\r\n                            var tempLineTop = tempNodeGroups_[m][0].Top\r\n                                    - ((this.IntervalHeight - this.LineSize) / 2)\r\n                                    - this.LineSize;\r\n                            var tempLineLeft = tempNodeGroups_[m][0].Left\r\n                                    + (tempNodeGroups_[m][0].Width / 2);\r\n                            var tempGroupLine = new CreateLine(1, tempLineTop,\r\n                                    tempLineLeft, tempLineWidth, this.LineSize,\r\n                                    this.LineColor, \"LineGroup_\"\r\n                                            + tempNodeGroups_[m][0].Id);\r\n                        }\r\n                    }\r\n                }\r\n                //*************************************************************************************************\r\n            }\r\n            function GetGroupWidthByNode(Node_) {//����Ⱥ������һ�ڵ㣬ȡ��Ⱥ����\r\n                var tempNodesGroup_ = This.DepthGroup[Node_.Depth].NodeGroups[Node_.NodeGroupId];\r\n                var tempGroupWidth_ = tempNodesGroup_[tempNodesGroup_.length - 1].Left\r\n                        - tempNodesGroup_[0].Left;\r\n                tempGroupWidth_ += tempNodesGroup_[tempNodesGroup_.length - 1].Width\r\n                return tempGroupWidth_;\r\n            }\r\n\r\n            function GetParentLeftByNode(Node_) {// ����Ⱥ������һ�ڵ㣬ȡ�ø��ڵ�Left ������Ҫ����\r\n                var tempNodesGroup_ = This.DepthGroup[Node_.Depth].NodeGroups[Node_.NodeGroupId];\r\n                var tempGroupWidth_ = (tempNodesGroup_[tempNodesGroup_.length - 1].Left\r\n                        + tempNodesGroup_[0].Left + tempNodesGroup_[0].Width) / 2;\r\n                return tempGroupWidth_;\r\n            }\r\n\r\n            function adjustBox(Node_, index) {// �ڵ�û���ӽڵ㣬��Ϊ�ýڵ����¿ռ䣬�����·����ڵ�\r\n                var tempNodesGroup_ = This.DepthGroup[Node_.Depth].Nodes;\r\n                for (var i = index - 1; i >= 0; i--) {\r\n                    if (tempNodesGroup_[i].Nodes.length > 0) {\r\n                        // ȡ�øýڵ���ӽڵ㼯��\r\n                        var tempChildNodesGroup_ = tempNodesGroup_[i].Nodes;\r\n                        if (i == index - 1) {\r\n                            if (tempChildNodesGroup_.length >= 3\r\n                                    || ((index + 1) < tempNodesGroup_.length && tempChildNodesGroup_.length == 2 && tempNodesGroup_[index + 1].Nodes.length == 2)) {\r\n                                break;\r\n                            }\r\n                        }\r\n                        // �����ӽڵ�\r\n                        var tempLastChildNode = tempChildNodesGroup_[tempChildNodesGroup_.length - 1];\r\n                        // �ò�������ӽڵ�\r\n                        var tempChildNodesGroupAll_ = This.DepthGroup[Node_.Depth + 1].Nodes;\r\n                        var indexFrom = tempLastChildNode.NodeOrderId;\r\n                        for (var j = 0; j < tempChildNodesGroupAll_.length; j++) {\r\n                            if (tempChildNodesGroupAll_[j] == tempLastChildNode) {\r\n                                indexFrom = j;\r\n                            }\r\n                        }\r\n\r\n                        adjustBoxIn(tempChildNodesGroupAll_, indexFrom + 1,\r\n                                tempChildNodesGroupAll_.length, Node_.Width);\r\n                        break;\r\n                    }\r\n                }\r\n            }\r\n            // �ݹ� ����\r\n            function adjustBoxIn(arr, left, right, boxWidth) {\r\n                for (var i = left; i < right; i++) {\r\n                    arr[i].Left += (boxWidth + This.IntervalWidth);\r\n                    if (arr[i].Nodes.length > 0) {\r\n                        adjustBoxIn(arr[i].Nodes, 0, arr[i].Nodes.length, boxWidth);\r\n                    }\r\n                }\r\n            }\r\n            function SetLeftByDepthNode(DepthNodes_, NodeId, Type) {\r\n                if (Type == \"LTR\" && NodeId == DepthNodes_.length - 1) {\r\n                    SetLeftByDepthNode(DepthNodes_, NodeId, \"RTL\");\r\n                    return;\r\n                }\r\n                if (Type == \"RTL\" && NodeId == 0) {\r\n                    SetLeftByDepthNode(DepthNodes_, NodeId, \"LTR\");\r\n                    return;\r\n                }\r\n                var FindIndex = null;\r\n                if (Type == \"LTR\") {\r\n                    for (var r_ = NodeId + 1; r_ < DepthNodes_.length; r_++) {\r\n                        if (DepthNodes_[r_].Left != null) {\r\n                            FindIndex = r_;\r\n                            break;\r\n                        }\r\n                    }\r\n                    if (FindIndex == null) {\r\n                        SetLeftByDepthNode(DepthNodes_, NodeId, \"RTL\");\r\n                        return;\r\n                    } else {\r\n                        for (var r_ = FindIndex - 1; r_ >= NodeId; r_--) {\r\n                            DepthNodes_[r_].Left = DepthNodes_[r_ + 1].Left\r\n                                    - This.IntervalWidth - DepthNodes_[r_].Width;\r\n                        }\r\n                    }\r\n                }\r\n                if (Type == \"RTL\") {\r\n                    for (var r_ = NodeId - 1; r_ >= 0; r_--) {\r\n                        if (DepthNodes_[r_].Left != null) {\r\n                            FindIndex = r_;\r\n                            break;\r\n                        }\r\n                    }\r\n                    if (FindIndex == null) {\r\n                        SetLeftByDepthNode(DepthNodes_, NodeId, \"LTR\");\r\n                        return;\r\n                    } else {\r\n                        for (var r_ = FindIndex + 1; r_ <= NodeId; r_++) {\r\n                            DepthNodes_[r_].Left = DepthNodes_[r_ - 1].Left\r\n                                    + This.IntervalWidth\r\n                                    + DepthNodes_[r_ - 1].Width;\r\n                        }\r\n                    }\r\n                }\r\n            }\r\n            function GetDepthHeightToRoot(DepthId) {//ȡ�þ��붥��ĸ߶�\r\n                var tempHeight_ = 0;\r\n                for (var x_ = DepthId; x_ >= 1; x_--) {\r\n                    tempHeight_ += This.DepthGroup[x_].Height;\r\n                }\r\n                tempHeight_ += This.IntervalHeight * (DepthId - 1);\r\n                tempHeight_ -= This.DepthGroup[DepthId].Height;\r\n                return tempHeight_;\r\n            }\r\n\r\n            function SetLeftPosByChildNode(Node_, ChildNode_) {//�����¼��ڵ�λ�����ýڵ�λ��\r\n                var tempNodeGroups = This.DepthGroup[ChildNode_.Depth].NodeGroups[ChildNode_.NodeGroupId];\r\n                var tempNodeLeft;\r\n                if (tempNodeGroups.length == 1) {\r\n                    tempNodeLeft = ((ChildNode_.Width / 2) + ChildNode_.Left)\r\n                            - (Node_.Width / 2);\r\n                } else {\r\n                    tempNodeLeft = GetFirstLeftPos(ChildNode_)\r\n                            + (tempNodeGroups.Width / 2) - (Node_.Width / 2);\r\n                }\r\n                Node_.Left = tempNodeLeft;\r\n            }\r\n            function GetFirstLeftPos(Node_) {//���ݽڵ�λ��ȡ��ͬһ�����׸��ڵ�λ��\r\n                if (Node_.NodeOrderId == 0) {//Node_Ϊ�׽ڵ�\r\n                    return Node_.Left;\r\n                }\r\n                var tempWidth = 0;\r\n                for (var k_ = 0; k_ <= Node_.NodeOrderId; k_++) {\r\n                    var tempGroupsNode = This.DepthGroup[Node_.Depth].NodeGroups[Node_.NodeGroupId][k_];\r\n                    tempWidth += tempGroupsNode.Width;\r\n                }\r\n                tempWidth += (Node_.NodeOrderId * This.IntervalWidth);\r\n                return ((Node_.Left - tempWidth) + (Node_.Width / 2));\r\n            }\r\n            function ChildNodesWidth(OrgObj) {//ȡ�ò��\r\n                var ReWidth = 0;\r\n                for (var s_ = 0; s_ < OrgObj.Nodes.length; s_++) {\r\n                    ReWidth += OrgObj.Nodes[s_].Width;\r\n                }\r\n                ReWidth += (OrgObj.Nodes.length - 1) * This.IntervalWidth;\r\n                return ReWidth;\r\n            }\r\n            function SetDepthGroupWidth() {//���ò�����\r\n                for (var n_ = 1; n_ <= This.Depth; n_++) {\r\n                    var tempNodeGroups = This.DepthGroup[n_].NodeGroups;\r\n                    for (var m_ = 0; m_ < tempNodeGroups.length; m_++) {\r\n                        tempNodeGroups[m_].Width = GetGroupWidthByNode(tempNodeGroups[m_][0]);\r\n                    }\r\n                }\r\n            }\r\n\r\n            function SetDepthsHeight() {//���ò�