INSERT INTO `txt` VALUES ('970', '2', '转.txt', '��.net Framework�У�System.Convert�����ṩ�˽�Ϊȫ��ĸ������͡���ֵ֮���ת�����ܡ����е����������������ɵ�ʵ�ָ��ֽ��Ƶ���ֵ���ת����\r\n\r\nConvert.ToInt32(string value, int fromBase)��\r\n\r\n���԰Ѳ�ͬ������ֵ���ַ���ת��Ϊ���֣�����fromBase����Ϊ���Ƶĸ�ʽ��ֻ����2��8��10��16:\r\n\r\n��Convert.ToInt32(��0010��,2)ִ�еĽ��Ϊ2;\r\n\r\nConvert.ToString(int value, int toBase):\r\n\r\n���԰�һ������ת��Ϊ��ͬ������ֵ���ַ�����ʽ,����toBase����Ϊ���Ƶĸ�ʽ��ֻ����2��8��10��16:\r\n\r\n��Convert.ToString(2,2)ִ�еĽ��Ϊ��0010��\r\n\r\n����������һ������ʵ�ָ��ֽ��Ƽ���ַ�������ת����ѡ����ת����ֵ�ͣ�Ȼ����ת����Ӧ�Ľ��Ƶ��ַ���:\r\n\r\npublic string ConvertString(string value, int fromBase, int toBase)\r\n\r\n{\r\n\r\n  int intValue = Convert.ToInt32(value, fromBase);\r\n\r\n  return Convert.ToString(intValue, toBase);\r\n}\r\n$ad$\r\n����fromBaseΪԭ���ĸ�ʽ\r\n\r\ntoBaseΪ��Ҫת���ɵĸ�ʽ\r\n=============================\r\n//ʮ����ת������ \r\n\r\nConsole.WriteLine(Convert.ToString(69, 2)); \r\n\r\n//ʮ����ת�˽��� \r\n\r\nConsole.WriteLine(Convert.ToString(69, 8)); \r\n\r\n//ʮ����תʮ������ \r\n\r\nConsole.WriteLine(Convert.ToString(69, 16)); \r\n\r\n//������תʮ���� \r\n\r\nConsole.WriteLine(Convert.ToInt32(��100111101��, 2)); \r\n\r\n//�˽���תʮ���� \r\n\r\nConsole.WriteLine(Convert.ToInt32(��76��, 8)); \r\n\r\n//C# 16����ת��10���� \r\n\r\nConsole.WriteLine(Convert.ToInt32(��FF��, 16)); \r\n\r\n��C#�п��Զ������������λ�����߼����㡣��λ�����߼�����������ǣ�����ȡ����������ÿ��λ�������߼����㣬ÿ��λ���߼��������ǽ��ֵ��ÿ��λ�� \r\n\r\nC#֧�ֵ�λ�߼���������2��ʾ�� \r\n\r\n������� ���� ����������� ���������� ������ ʵ�� \r\n\r\n~ λ�߼������� ���ͣ��ַ��� ���� 1 ~a \r\n& λ�߼������� 2 a & b \r\n| λ�߼������� 2 a | b \r\n^ λ�߼�������� 2 a ^ b \r\n<< λ�������� 2 a<<4 \r\n>> λ�������� 2 a>>2 \r\n1��λ�߼������� \r\nλ�߼��������ǵ�Ŀ�ģ�ֻ��һ���������λ�߼������㰴λ����������ֵ���з����㣬�������ĳһλ����0���ͽ���ת��Ϊ1�����ĳһλ����1���ͽ���ת��Ϊ0�� \r\n\r\n���磬�Զ����Ƶ�10010001����λ�߼������㣬�������01101110����ʮ���Ʊ�ʾ���ǣ�~145����110���Զ����Ƶ�01010101����λ�߼������㣬�������10101010����ʮ���Ʊ�ʾ����~85����176�� \r\n2��λ�߼������� \r\nλ�߼������㽫�����������λ���������㡣������Ĺ���1��1����1��1��0����0�� \r\n���磺10010001�������ƣ�&11110000����10010000�������ƣ��� \r\n3��λ�߼������� \r\nλ�߼������㽫�����������λ���л����㡣������Ĺ����ǣ�1��1��1��1��0����1�� \r\n0��0����0������10010001�������ƣ�| 11110000�������ƣ�����11110001�������ƣ��� \r\n4��λ�߼�������� \r\nλ�߼�������㽫�����������λ����������㡣�������Ĺ����ǣ�1���1����0�� \r\n1���0����1��0���0����0��������ͬ��0�������1�� \r\n���磺10010001�������ƣ�^11110000�������ƣ�����01100001�������ƣ��� \r\n5��λ�������� \r\nλ�������㽫��������λ�������λ�����ƺ�ճ��Ĳ���0�����磺8λ��byte�ͱ��� \r\nbyte a=0x65(�������Ƶ�01100101),��������3λ��a<<3�Ľ����0x27(�������Ƶ�00101000)�� \r\n6��λ�������� \r\nλ�������㽫��������λ�������λ�����ƺ�ճ��Ĳ�����0�����磺8λ��byte�ͱ��� \r\nByte a=0x65(�ȣ������Ƶ�01100101��)��������3λ��a>>3�Ľ����0x0c(������00001100)�� \r\n�ڽ���λ�롢���������ʱ���������������������һ�£��������������;��������������͡����������int����a��b�������㣬�����������ͻ���int�͡�������������������Ͳ�һ��Դ����գ���C#Ҫ�Բ�һ�µ����ͽ�������ת�������һ�µ����ͣ�Ȼ��������㡣 \r\n\r\nC# 16����ת��10��������ת���Ĺ���ͬ������������������ת����һ�¡� \r\n��λ������������������ɵı��ʽ����λ������ʽ�� \r\n\r\nC# 16����ת��10���ƾͽ��ܵ����\r\n====================================\r\n/// <summary>\r\n  /// 16����ת�ַ�\r\n  /// </summary>\r\n  /// <param name=\"str1\"></param>\r\n  /// <returns></returns>\r\n  public string HexToStr(string str1)\r\n  {\r\n   string returnValue = \"\";\r\n\r\n   if(str1==\"\")\r\n    return \"\";\r\n\r\n   byte[] w = new byte[str1.Length/2];\r\n\r\n   for(int i = 0;i<str1.Length;i++)\r\n   {\r\n    w[i] = HexToStr(str1,i+1);\r\n   }\r\n\r\n   returnValue = System.Text.Encoding.Default.GetString(w);\r\n   return returnValue;\r\n  }\r\n  /// <summary>\r\n  /// 16����ת�ַ�\r\n  /// </summary>\r\n  /// <param name=\"str1\"></param>\r\n  /// <param name=\"spage\"></param>\r\n  /// <returns></returns>\r\n  byte HexToStr(string str1,int spage)\r\n  {\r\n   string returnValue;\r\n   int intTemp;\r\n   int ipage = Convert.ToInt32(spage);\r\n   int StartInt = 0;\r\n   int iLengthInt = 2;\r\n\r\n   intTemp = str1.Length;//ȡ���ַ�������\r\n   \r\n   if(ipage==1)\r\n   {\r\n    StartInt = 0;\r\n    iLengthInt = 2;\r\n   }\r\n   else\r\n   {\r\n    StartInt = (ipage-1) * iLengthInt;\r\n\r\n    if((intTemp-ipage*2)<2)\r\n    {\r\n     iLengthInt = intTemp-(ipage-1)*2;\r\n    }\r\n    else\r\n    {\r\n     iLengthInt = 2;\r\n    }\r\n   }\r\n\r\n   returnValue = str1.Substring(StartInt,iLengthInt);\r\n\r\n   returnValue = ConvertString(returnValue,16,10);\r\n\r\n   return Convert.ToByte(returnValue);\r\n  }\r\n\r\n  /// <summary>\r\n  /// ConvertString(ֵ,��Դ����,Ŀ�Ľ���);\r\n  /// </summary>\r\n  /// <param name=\"value\"></param>\r\n  /// <param name=\"fromBase\"></param>\r\n  /// <param name=\"toBase\"></param>\r\n  /// <returns></returns>\r\n  string ConvertString(string value, int fromBase, int toBase)\r\n  {\r\n   int intValue = Convert.ToInt32(value, fromBase);\r\n   return Convert.ToString(intValue, toBase);\r\n  }\r\n', '0', '2017-09-08 09:49:18', null, '转.txt');
